// Start of modified PBR code from https://www.shadertoy.com/view/XsfXWX

float somestep(float t) {
    return pow(t,4.0);
}

vec3 textureAVG(samplerCube tex, vec3 tc) {
    const float diff0 = 0.35;
    const float diff1 = 0.12;
 	vec3 s0 = texture(tex,tc).xyz;
    vec3 s1 = texture(tex,tc+vec3(diff0)).xyz;
    vec3 s2 = texture(tex,tc+vec3(-diff0)).xyz;
    vec3 s3 = texture(tex,tc+vec3(-diff0,diff0,-diff0)).xyz;
    vec3 s4 = texture(tex,tc+vec3(diff0,-diff0,diff0)).xyz;

    vec3 s5 = texture(tex,tc+vec3(diff1)).xyz;
    vec3 s6 = texture(tex,tc+vec3(-diff1)).xyz;
    vec3 s7 = texture(tex,tc+vec3(-diff1,diff1,-diff1)).xyz;
    vec3 s8 = texture(tex,tc+vec3(diff1,-diff1,diff1)).xyz;

    return (s0 + s1 + s2 + s3 + s4 + s5 + s6 + s7 + s8) * 0.111111111;
}

vec3 textureBlured(samplerCube tex, vec3 tc) {
   	vec3 r = textureAVG(tex,vec3(1.0,0.0,0.0));
    vec3 t = textureAVG(tex,vec3(0.0,1.0,0.0));
    vec3 f = textureAVG(tex,vec3(0.0,0.0,1.0));
    vec3 l = textureAVG(tex,vec3(-1.0,0.0,0.0));
    vec3 b = textureAVG(tex,vec3(0.0,-1.0,0.0));
    vec3 a = textureAVG(tex,vec3(0.0,0.0,-1.0));

    float kr = dot(tc,vec3(1.0,0.0,0.0)) * 0.5 + 0.5; 
    float kt = dot(tc,vec3(0.0,1.0,0.0)) * 0.5 + 0.5;
    float kf = dot(tc,vec3(0.0,0.0,1.0)) * 0.5 + 0.5;
    float kl = 1.0 - kr;
    float kb = 1.0 - kt;
    float ka = 1.0 - kf;

    kr = somestep(kr);
    kt = somestep(kt);
    kf = somestep(kf);
    kl = somestep(kl);
    kb = somestep(kb);
    ka = somestep(ka);    

    float d;
    vec3 ret;
    ret  = f * kf; d  = kf;
    ret += a * ka; d += ka;
    ret += l * kl; d += kl;
    ret += r * kr; d += kr;
    ret += t * kt; d += kt;
    ret += b * kb; d += kb;

    return ret / d;
}

// GGX code from https://www.shadertoy.com/view/MlB3DV
float G1V ( float dotNV, float k ) {
	return 1.0 / (dotNV*(1.0 - k) + k);
}
float GGX(vec3 N, vec3 V, vec3 L, float roughness, float F0) {
    	float alpha = roughness*roughness;
	vec3 H = normalize (V + L);

	float dotNL = clamp (dot (N, L), 0.0, 1.0);
	float dotNV = clamp (dot (N, V), 0.0, 1.0);
	float dotNH = clamp (dot (N, H), 0.0, 1.0);
	float dotLH = clamp (dot (L, H), 0.0, 1.0);

	float D, vis;
	float F;

	// NDF : GGX
	float alphaSqr = alpha*alpha;
	float pi = 3.1415926535;
	float denom = dotNH * dotNH *(alphaSqr - 1.0) + 1.0;
	D = alphaSqr / (pi * denom * denom);

	// Fresnel (Schlick)
	float dotLH5 = pow (1.0 - dotLH, 5.0);
	F = F0 + (1.0 - F0)*(dotLH5);

	// Visibility term (G) : Smith with Schlick's approximation
	float k = alpha / 2.0;
	vis = G1V (dotNL, k) * G1V (dotNV, k);

	return /*dotNL */ D * F * vis;
}

vec3 shade(vec3 ray, vec3 normal, vec3 materialColor) {
    // material
    float metallic = 0.07;
    float roughness = 0.1;
    float fresnel_pow = mix(5.0, 3.5,metallic);
    vec3 color_mod = vec3(1.0);
    vec3 light_color = pow(texture(iChannel1,vec3(1.0,0.0,0.0)).xyz * 1.2, vec3(2.2));


    // IBL
    vec3 ibl_diffuse = pow(textureBlured(iChannel1,normal), vec3(2.2));
    vec3 ibl_reflection = pow(textureBlured(iChannel1,reflect(ray,normal)), vec3(2.2));

    // fresnel
    float fresnel = max(1.0 - dot(normal,-ray), 0.0);
    fresnel = pow(fresnel,fresnel_pow);    

    // reflection        
    vec3 refl = pow(texture(iChannel1,reflect(ray,normal)).xyz, vec3(2.2));
    refl = mix(refl,ibl_reflection,(1.0-fresnel)*roughness);
    refl = mix(refl,ibl_reflection,roughness);

    // specular
    vec3 light = normalize(vec3(-0.5,1.0,0.0));
    float power = 1.0 / max(roughness * 0.4,0.01);
    vec3 spec = light_color * GGX(normal,-ray,light,roughness*0.7, 0.2);
    refl -= spec;

    // diffuse
    vec3 diff = ibl_diffuse * pow(materialColor, vec3(2.2));
    diff = mix(diff * color_mod,refl,fresnel);        

    vec3 color = mix(diff,refl * color_mod,metallic) + spec;
    return pow(color, vec3(1.0/2.2));
}

// End of modified PBR code from https://www.shadertoy.com/view/XsfXWX

//============================================================================//



vec3 saturate(vec3 color, float amount) {
    vec3 gray = vec3(dot(vec3(0.2126,0.7152,0.0722), color));
    return clamp(mix(gray, color, 1.0 + amount), vec3(0.0), vec3(1.0));
}

vec3 calculateColor(Ray r) {
    float t, u, v;

    if (intersect(r, t, u, v)) {
        vec3 n = normal(r, u, v);

#if RENDERING_MODE == REGULAR
        vec3 lv = normalize(vec3(-1.0, 2.0, -3.0));
        float ld = dot(lv, n);
        float l = mix(max(ld, 0.0), mix(0.02, 1.0, ld * 0.5 + 0.5), 0.1);
        vec3 c = vec3(1.0);
        return pow(c * l, vec3(1.0 / 2.2));
#elif RENDERING_MODE == NORMAL_MAP
        return n * 0.5 + 0.5;
#elif RENDERING_MODE == PBR
        return shade(r.d, n, saturate(n * 0.5 + 0.5, 0.7));
#endif
    } else {
#if RENDERING_MODE == REGULAR
        vec3 lv = normalize(vec3(-1.0, 2.0, -3.0));
        float ld = dot(lv, r.d);
        float l = pow(mix(0.4, 1.0, ld * 0.5 + 0.5), 5.0);
        vec3 c = vec3(0.2);
        return pow(c * l, vec3(1.0 / 2.0));
#elif RENDERING_MODE == NORMAL_MAP
        return vec3(0.2);
#elif RENDERING_MODE == PBR
    #if USE_CUBEMAP_BACKGROUND_FOR_PBR
        return texture(iChannel1, r.d).rgb;
    #else
        return vec3(0.2);
    #endif
#endif
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Calculate camera configuration from keyboard & mouse input buffer
    vec2 camRot = texelFetch(iChannel0, ivec2(0, 0), 0).xy;
    vec2 cr = cos(camRot);
    vec2 sr = sin(camRot);
    mat3 v2wRotMat = mat3(
            cr.y,         0.0,  -sr.y,
            sr.x * sr.y,  cr.x,  cr.y * sr.x,
            cr.x * sr.y, -sr.x,  cr.x * cr.y);
    vec4 camPosD = texelFetch(iChannel0, ivec2(1, 0), 0);
    vec3 camPos = camPosD.xyz + v2wRotMat * vec3(0.0, 0.0, 1.0) * camPosD.w; 
    vec2 tanHalfFov = vec2(tan(radians(fovY) * 0.5)); 
    tanHalfFov.x *= iResolution.x / iResolution.y;
    vec2 cCoord = fragCoord / iResolution.xy * 2.0 - 1.0;
    vec3 vDir = normalize(vec3(cCoord * tanHalfFov, -1.0));
    vec3 wDir = v2wRotMat * vDir;
    Ray r = Ray(camPos, wDir);

    // Calculate color
    fragColor = vec4(calculateColor(r), 1.0);

#if VIGNETTE
    fragColor.rgb *= 1.0 - 0.8 * pow(distance(vec2(0.5, 0.5), fragCoord / iResolution.xy), 3.0);
#endif
}

