// Overloads:
#define MAKE_OVERLOADSB(fn) iBool fn(bool x,iBool y){return fn(ibool(x),y);}iBool fn(iBool x,bool y){return fn(x,ibool(y));}
#define MAKE_OVERLOADSBF(fn) iBool fn(float x,iFloat y){return fn(ifloat(x),y);}iBool fn(iFloat x,float y){return fn(x,ifloat(y));}
#define MAKE_OVERLOADS2(fn) iFloat fn(float x,iFloat y){return fn(ifloat(x),y);}iFloat fn(iFloat x,float y){return fn(x,ifloat(y));}
#define MAKE_OVERLOADS3(fn) iFloat fn(float x,iFloat y,iFloat z){return fn(ifloat(x),y,z);}iFloat fn(iFloat x,float y,iFloat z){return fn(x,ifloat(y),z);}iFloat fn(iFloat x,iFloat y,float z){return fn(x,y,ifloat(z));}iFloat fn(float x,float y,iFloat z){return fn(ifloat(x),ifloat(y),z);}iFloat fn(float x,iFloat y,float z){return fn(ifloat(x),y,ifloat(z));}iFloat fn(iFloat x,float y,float z){return fn(x,ifloat(y),ifloat(z));}

