// Empty Sets:
const iFloat EMPTY_SET = iFloat(-INFINITY, -INFINITY);
const iBool EMPTY_BSET = iBool(true, false);

#if PROPAGATE_EMPTY_SETS
    #define CHECK_IF_EMPTY_SET_F(x) if ((x) == EMPTY_SET) return EMPTY_SET
    #define CHECK_IF_EMPTY_SET_B(x) if ((x) == EMPTY_SET) return EMPTY_BSET
    #define CHECK_IF_EMPTY_BSET_F(x) if ((x) == EMPTY_BSET) return EMPTY_SET
    #define CHECK_IF_EMPTY_BSET_B(x) if ((x) == EMPTY_BSET) return EMPTY_BSET
#else
    #define CHECK_IF_EMPTY_SET_F(x)
    #define CHECK_IF_EMPTY_SET_B(x)
    #define CHECK_IF_EMPTY_BSET_F(x)
    #define CHECK_IF_EMPTY_BSET_B(x)
#endif

// Constructors:
iFloat ifloat(float x) {
    return iFloat(x, x);
}


iBool ibool(bool x) {
    return iBool(x, x);
}

// Helper Functions:
iFloat iMinMax(float x, float y, float z, float w) {
    return iFloat(
        min(x, min(y, min(z, w))),
        max(x, max(y, max(z, w)))
    );
}

iBool iMinMax(bool x, bool y, bool z, bool w) {
    return iBool(
        x || y || z || w,
        x && y && z && w
    );
}


// Function Implementations:

iFloat iAdd(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_F(x); 
    CHECK_IF_EMPTY_SET_F(y);
    return iFloat(x.l + y.l, x.u + y.u);
}

iFloat iSub(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_F(x); 
    CHECK_IF_EMPTY_SET_F(y);
    return iFloat(x.l - y.u, x.u - y.l);
}

iFloat iMul(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_F(x); 
    CHECK_IF_EMPTY_SET_F(y);
    return iMinMax(x.l * y.l, x.l * y.u, x.u * y.l, x.u * y.u);
}

iFloat iDiv(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_F(x);
    CHECK_IF_EMPTY_SET_F(y);
    return iMul(x, iInv(y));
}


iFloat iNeg(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(-x.u, -x.l);
}

iFloat iInv(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x); 

    if (x.l > 0.0 || x.u < 0.0) {
        return iFloat(1.0 / x.u, 1.0 / x.l);
    } else if (x.l < 0.0 && x.u > 0.0) {
        return iFloat(-INFINITY, INFINITY);
    } else if (x.u == 0.0) {
        return iFloat(-INFINITY, 1.0 / x.l);
    } else {
        return iFloat(1.0 / x.u, INFINITY);
    }
}


iFloat iSin(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    float u = PI * round(x.l / PI) + PI * 1.5;

    if (u < x.u) {
        return iFloat(-1.0, 1.0);
    }

    u -= PI;
    vec2 s = vec2(sin(x.l), sin(x.u));
    vec2 r = vec2(min(s.x, s.y), max(s.x, s.y));

    if (u < x.u) {
        float s = sin(u);
        return iFloat(min(r.x, s), max(r.y, s));
    }

    return iFloat(r.x, r.y);
}

iFloat iCos(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iSin(iAdd(x, ifloat(PI / 2.0)));
}

iFloat iTan(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iDiv(iSin(x), iCos(x));
}


iFloat iAsin(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);

    if (x.u < -1.0) {
        return EMPTY_SET;
    }

    if (x.l <= -1.0) {
        if (x.u < 1.0) {
            return iFloat(-PI / 2.0, asin(x.u));
        }

        return iFloat(-PI / 2.0, PI / 2.0);
    }

    if (x.u < 1.0) {
        return iFloat(asin(x.l), asin(x.u));
    }

    if (x.l < 1.0) {
        return iFloat(asin(x.l), PI / 2.0);
    }

    if (x.l == 1.0) {
        return ifloat(PI / 2.0);
    }

    return EMPTY_SET;
}

iFloat iAcos(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);

    if (x.u < -1.0) {
        return EMPTY_SET;
    }

    if (x.l <= -1.0) {
        if (x.u < 1.0) {
            return iFloat(acos(x.u), PI);
        }

        return iFloat(0.0, PI);
    }

    if (x.u < 1.0) {
        return iFloat(acos(x.u), acos(x.l));
    }

    if (x.l < 1.0) {
        return iFloat(0.0, acos(x.l));
    }

    if (x.l == 1.0) {
        return ifloat(0.0);
    }

    return EMPTY_SET;
}

iFloat iAtan(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(atan(x.l), atan(x.u));
}

iFloat iAtan(iFloat y, iFloat x) {
    CHECK_IF_EMPTY_SET_F(y);
    CHECK_IF_EMPTY_SET_F(x);

    if (x.u < 0.0) {
        if (y.u < 0.0) {
            return iFloat(atan(y.u, x.l), atan(y.l, x.u));
        }

        if (y.l < 0.0) {
            return iFloat(-PI, PI);
        }

        return iFloat(atan(y.u, x.u), atan(y.l, x.l));
    }

    if (x.u == 0.0) {
        if (x.l < 0.0) {
            if (y.u < 0.0) {
                return iFloat(atan(y.u, x.l), -PI / 2.0);
            }

            if (y.l < 0.0) {
                return iFloat(-PI, PI);
            }

            if (y.l == 0.0) {
                return iFloat(0.0, PI);
            }

            return (iFloat(PI / 2.0, atan(y.l, x.l)));
        }

        if (y.u < 0.0) {
            return ifloat(-PI / 2.0);
        }

        if (y.u == 0.0) {
            if (y.l < 0.0) {
                return iFloat(-PI / 2.0, 0.0);
            }

            return ifloat(0.0);
        }

        if (y.l < 0.0) {
            return iFloat(-PI / 2.0, PI / 2.0);
        }

        if (y.l == 0.0) {
            return iFloat(0.0, PI / 2.0);
        }

        return ifloat(PI / 2.0);
    }

    if (x.l < 0.0) {
        if (y.u < 0.0) {
            return iFloat(atan(y.u, x.l), atan(y.u, x.u));
        }

        if (y.u == 0.0) {
            if (y.l == 0.0) {
                return iFloat(0.0, PI);
            }

            return iFloat(-PI, PI);
        }

        if (y.l < 0.0) {
            return iFloat(-PI, PI);
        }

        if (y.l == 0.0) {
            return iFloat(0.0, PI);
        }

        return iFloat(atan(y.l, x.u), atan(y.l, x.l));
    }

    if (x.l == 0.0) {
        if (y.u <= 0.0) {
            return iFloat(atan(y.u, x.u), -PI / 2.0);
        }

        if (y.l < 0.0) {
            return iFloat(-PI / 2.0, PI / 2.0);
        }

        if (y.l == 0.0) {
            return iFloat(0.0, PI / 2.0);
        }

        return iFloat(atan(y.l, x.u), PI / 2.0);
    }

    return iAtan(iDiv(y, x));
}


iFloat iSinh(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(sinh(x.l), sinh(x.u));
}

iFloat iCosh(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);

    if (x.u <= 0.0) {
        return iFloat(cosh(x.u), cosh(x.l));
    }

    if (x.l <= 0.0 && 0.0 < x.u) {
        return iFloat(1.0, cosh(max(-x.l, x.u)));
    }

    return iFloat(cosh(x.l), cosh(x.u));
}

iFloat iTanh(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(tanh(x.l), tanh(x.u));
}


iFloat iAsinh(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(asinh(x.l), asinh(x.u));
}

iFloat iAcosh(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);

    if (x.u < 1.0) {
        return EMPTY_SET;
    }

    if (x.u == 1.0) {
        return ifloat(0.0);
    }

    if (x.l <= 1.0) {
        return iFloat(0.0, acosh(x.u));
    }

    return iFloat(acosh(x.l), acosh(x.u));
}

iFloat iAtanh(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);

    if (x.u <= -1.0) {
        return EMPTY_SET;
    }

    if (x.l <= -1.0) {
        if (x.u < 1.0) {
            return iFloat(-INFINITY, atanh(x.u));
        }

        return iFloat(-INFINITY, INFINITY);
    }

    if (x.l < 1.0) {
        if (x.u < 1.0) {
            return iFloat(atanh(x.l), atanh(x.u));
        }

        return iFloat(atanh(x.l), INFINITY);
    }

    return EMPTY_SET;
}


iFloat iExp(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(exp(x.l), exp(x.u));
}

iFloat iExp2(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(exp2(x.l), exp2(x.u));
}


iFloat iLog(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);

    if (x.l > 0.0) {
        return iFloat(log(x.l), log(x.u));
    }

    if (x.u > 0.0) {
        return iFloat(-INFINITY, log(x.u));
    }

    return EMPTY_SET;
}

iFloat iLog2(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);

    if (x.l > 0.0) {
        return iFloat(log2(x.l), log2(x.u));
    }

    if (x.u > 0.0) {
        return iFloat(-INFINITY, log2(x.u));
    }

    return EMPTY_SET;
}


iFloat iPow(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_F(x);
    CHECK_IF_EMPTY_SET_F(y);

    if (x.l < 0.0 && y.l == y.u && fract(y.l) == 0.0) {
        float y = y.l;

        if (y == 0.0) {
            return ifloat(1.0);
        }

        if (y > 0.0) {
            if (mod(y, 2.0) == 1.0) {
                return iFloat(-pow(-x.l, y), pow(abs(x.u), y) * sign(x.u));
            }

            if (x.u > 0.0) {
                return iFloat(0.0, pow(max(-x.l, x.u), y));
            }        

            return iFloat(pow(-x.u, y), pow(-x.l, y));;
        }

        if (mod(y, 2.0) == 1.0) {
            if (x.u > 0.0) {
                return iFloat(-INFINITY, INFINITY);
            }

            if (x.u == 0.0) {
                return iFloat(-INFINITY, -pow(-x.l, y));
            }

            return iFloat(-pow(-x.u, y), -pow(-x.l, y));
        }

        if (x.u > 0.0) {
            return iFloat(min(pow(-x.l, y), pow(x.u, y)), INFINITY);
        }

        if (x.u == 0.0) {
            return iFloat(pow(-x.l, y), INFINITY);
        }

        return iFloat(pow(-x.l, y), pow(-x.u, y));
    }

    if (x.u < 0.0) {
        return EMPTY_SET;
    }

    if (x.u == 0.0) {
        if (y.l == 0.0 && y.u == 0.0) {
            return EMPTY_SET;
        }

        return ifloat(0.0);
    }

    x.l = max(x.l, 0.0);

    if (x.l == 0.0) {
        if (y.u <= 0.0) {
            if (y.l == 0.0) {
                return ifloat(1.0);
            }

            if (x.u < 1.0) {
                return iFloat(pow(x.u, y.u), INFINITY);
            }

            return iFloat(pow(x.u, y.l), INFINITY);
        }else

        if (y.l < 0.0) {
            return iFloat(0.0, INFINITY);
        }

        if (y.l == 0.0) {
            return iFloat(0.0, max(1.0, pow(x.u, y.u)));
        }
    }

    return iMinMax(pow(x.l, y.l), pow(x.l, y.u), pow(x.u, y.l), pow(x.u, y.u));
}


iFloat iSqrt(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);

    if (x.l > 0.0) {
        return iFloat(sqrt(x.l), sqrt(x.u));
    }

    if (x.u > 0.0) {
        return iFloat(0.0, sqrt(x.u));
    }

    if (x.u == 0.0) {
        return ifloat(0.0);
    }

    return EMPTY_SET;
}

iFloat iCbrt(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(pow(abs(x.l), 1.0 / 3.0) * sign(x.l), pow(abs(x.u), 1.0 / 3.0) * sign(x.u));
}

iFloat iSquare(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);

    if (x.l <= 0.0 && 0.0 <= x.u) {
        float t = max(-x.l, x.u);
        return iFloat(0.0, t * t);
    }

    if (x.u <= 0.0) {
        return iFloat(x.u * x.u, x.l * x.l);
    }

    return iFloat(x.l * x.l, x.u * x.u);
}


iFloat iSign(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(sign(x.l), sign(x.u));
}

iFloat iAbs(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);

    if (x.l >= 0.0) {
        return x;
    }

    if (x.u <= 0.0) {
        return iNeg(x);
    }

    return iFloat(0.0, max(-x.l, x.u));
}

iFloat iFloor(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(floor(x.l), floor(x.u));
}

iFloat iCeil(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(ceil(x.l), ceil(x.u));
}

iFloat iRound(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(round(x.l), round(x.u));
}

iFloat iTrunc(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iFloat(trunc(x.l), trunc(x.u));
}

iFloat iMod(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_F(x);
    CHECK_IF_EMPTY_SET_F(y);
    return iSub(x, iMul(y, iFloor(iDiv(x, y))));
}

iFloat iFract(iFloat x) {
    CHECK_IF_EMPTY_SET_F(x);
    return iSub(x, iFloor(x));
}


iFloat iMin(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_F(x);
    CHECK_IF_EMPTY_SET_F(y);
    return iFloat(min(x.l, y.l), min(x.u, y.u));
}

iFloat iMax(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_F(x);
    CHECK_IF_EMPTY_SET_F(y);
    return iFloat(max(x.l, y.l), max(x.u, y.u));
}

iFloat iClamp(iFloat x, iFloat minVal, iFloat maxVal) {
    CHECK_IF_EMPTY_SET_F(x);
    CHECK_IF_EMPTY_SET_F(minVal);
    CHECK_IF_EMPTY_SET_F(maxVal);
    return iFloat(clamp(x.l, minVal.l, maxVal.l), clamp(x.u, minVal.u, maxVal.u));
}


iFloat iMix(iFloat x, iFloat y, iFloat a) {
    CHECK_IF_EMPTY_SET_F(x);
    CHECK_IF_EMPTY_SET_F(y);
    CHECK_IF_EMPTY_SET_F(a);
    return iAdd(x, iMul(iSub(y, x), a));
}

iFloat iSmoothstep(iFloat edge0, iFloat edge1, iFloat x) {
    CHECK_IF_EMPTY_SET_F(edge0);
    CHECK_IF_EMPTY_SET_F(edge1);
    CHECK_IF_EMPTY_SET_F(x);
    iFloat t = iClamp(iDiv(iSub(x, edge0), iSub(edge1, edge0)), 0.0, 1.0);
    return iMul(iSquare(t), iSub(3.0, iMul(2.0, t)));
}

iFloat iStep(iFloat edge, iFloat x) {
    CHECK_IF_EMPTY_SET_F(edge);
    CHECK_IF_EMPTY_SET_F(x);

    if (edge.u <= x.l) {
        return ifloat(1.0);
    }

    if (x.u < edge.l) {
        return ifloat(0.0);
    }

    return iFloat(0.0, 1.0);
}


iFloat iIf(iBool P, iFloat x, iFloat y) {
    CHECK_IF_EMPTY_BSET_F(P);

    if (P.l) {
        return x;
    }

    if (!P.u) {
        return y;
    }

    if (x == EMPTY_SET) {
        return y;
    }

    if (y == EMPTY_SET) {
        return x;
    }

    return iFloat(min(x.l, y.l), max(x.u, y.u));
}

iFloat iIf(bool P, iFloat x, iFloat y) {
    return iIf(ibool(P), x, y);
}
iFloat iIf(bool P, float x, iFloat y) {
    return iIf(ibool(P), ifloat(x), y);
}
iFloat iIf(bool P, iFloat x, float y) {
    return iIf(ibool(P), x, ifloat(y));
}
iFloat iIf(iBool P, float x, iFloat y) {
    return iIf(P, ifloat(x), y);
}
iFloat iIf(iBool P, float x, float y) {
    return iIf(P, ifloat(x), ifloat(y));
}
iFloat iIf(iBool P, iFloat x, float y) {
    return iIf(P, x, ifloat(y));
}

iBool iEqual(iFloat x, iFloat y) {
    if (x == y) {
        return ibool(true);
    }

    if (x.l <= y.u && y.l <= x.l) {
        return iBool(false, true);
    }

    return ibool(false);
}

iBool iGreaterThan(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_B(x);
    CHECK_IF_EMPTY_SET_B(y);

    if (x.l > y.u) {
        return ibool(true);
    }

    if (x.u <= y.l) {
        return ibool(false);
    }

    return iBool(false, true);
}

iBool iGreaterThanEqual(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_B(x);
    CHECK_IF_EMPTY_SET_B(y);

    if (x.l >= y.u) {
        return ibool(true);
    }

    if (x.u < y.l) {
        return ibool(false);
    }

    return iBool(false, true);
}

iBool iLessThan(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_B(x);
    CHECK_IF_EMPTY_SET_B(y);

    if (x.u < y.l) {
        return ibool(true);
    }

    if (x.l >= y.u) {
        return ibool(false);
    }

    return iBool(false, true);
}

iBool iLessThanEqual(iFloat x, iFloat y) {
    CHECK_IF_EMPTY_SET_B(x);
    CHECK_IF_EMPTY_SET_B(y);

    if (x.u <= y.l) {
        return ibool(true);
    }

    if (x.l > y.u) {
        return ibool(false);
    }

    return iBool(false, true);
}


iBool iNOT(iBool x) {
    CHECK_IF_EMPTY_BSET_B(x);

    if (x.l && x.u) {
        return ibool(false);
    }

    if (!x.l && !x.u) {
        return ibool(true);
    }

    return iBool(false, true);
}

iBool iAND(iBool x, iBool y) {
    CHECK_IF_EMPTY_BSET_B(x);
    CHECK_IF_EMPTY_BSET_B(y);

    return iMinMax(x.l && y.l, x.l && y.u, x.u && y.l, x.u && y.u);
}

iBool iOR(iBool x, iBool y) {
    CHECK_IF_EMPTY_BSET_B(x);
    CHECK_IF_EMPTY_BSET_B(y);

    return iMinMax(x.l || y.l, x.l || y.u, x.u || y.l, x.u || y.u);
}

iBool iXOR(iBool x, iBool y) {
    CHECK_IF_EMPTY_BSET_B(x);
    CHECK_IF_EMPTY_BSET_B(y);

    return iMinMax(x.l ^^ y.l, x.l ^^ y.u, x.u ^^ y.l, x.u ^^ y.u);
}

