/*
Things to try out (need recompile and reset):
    - Input waveforms (cw-pulse, cw, pulse)
    - Switch visualized field (v variable in mainImage)
    - Wavelengths (f variable in _step)
    - Pipe shapes (initialization in update)

Interesting:
    - Sign-flip of mass density for reflection in narrow pipe
    - Sign-flip of flux density for reflection in wide pipe
*/

void mainImage(out vec4 C, vec2 O)
{
    ivec2 I = ivec2(O);
    int y = int(O.y / iResolution.y * float(npipes));
    float Oy = O.y - (float(y) + .5) * iResolution.y / float(npipes);

    vec4 h0 = texelFetch(iChannel0, ivec2(I.x + -1, y), 0);
    vec4 h1 = texelFetch(iChannel0, ivec2(I.x + -0, y), 0);

    C = vec4(0);
    float mr =           h1.x     ;
    float fr = mix(h0.x, h1.x, .5);
    float vr = mr * iResolution.y/2./float(npipes)*.8 - abs(Oy);

    float mA = mr*mr;
    float fA = fr*fr;

    float v = h1.y / mA; // mass density
    //v = h1.z / c / fA; // flux density
    if (vr > 0.)
    {
        C = mix(C, vec4(0, .1, 1, 1), clamp(-v, 0., 1.));
        C = mix(C, vec4(1, .2, 0, 1), clamp(+v, 0., 1.));
    }

    // pipe
    C = mix(C, vec4(1), clamp((1. + .3 * abs(dFdx(vr))) - abs(vr), 0., 1.));
    C = sqrt(C);

    // tip of the wave
    float t = float(iFrame * nbuffers);
    float w = iResolution.x;
    float x = abs(mod(t * c + w, 2. * w) - w);
    C = mix(C, vec4(1, 0, 0, 1), clamp(1. - abs(O.x - .5 - x), 0., 1.));
}

