const int   npipes  = 5;
const int  nbuffers = 4;
const float c       = 1. / 2.; // speed of sound [px/step]
const float damping = 0e-5;

const float pi = radians(180.);
const float tau = pi * 2.;

vec3 resolution;
int frame;

float constriction(float x, float w, float s);
float narrowing(float x, float w, float s);
float profile(int pipe, float x)
{
    //return x = .9 - .8 * step(0.4, x);
    if (pipe == 4)
        return constriction(x, .1, 10.);
    if (pipe == 3)
        return constriction(x, .1, 100.);
    if (pipe == 1)
        return narrowing(-x, -.4, 10.);
    if (pipe == 0)
        return narrowing(x, .4, 10.);
    return 1.;
}

vec2 boundary(float t)
{
    float f, xt, z;

    //f = c / 10.;
    //f = c / 50.;
    f = c / (resolution.x * .2);

    z = sin(t*tau*f) * .3*c * float(t*f < 1.); // short cw-pulse
    //z = sin(t*tau*f) * .3*c * float(t*f < 2.); // long cw-pulse
    //z = sin(t*tau*f) * .1*c;                   // cw
    //xt=t*tau*f-3., z = exp(-xt*xt)*1.*c;       // pulse

    return vec2(z, 0);
    //return vec2(0, z);
}

float dm(float lf, float rf)
{
    return lf - rf;
}

float df(float lr, float rr, float lm, float rm, float f)
{
    float cr = mix(lr, rr, .5);

    float lA = lr*lr;
    float cA = cr*cr;
    float rA = rr*rr;
    float p = (lm/lA - rm/rA) * cA;

    return p*c*c - f*damping;
}

void update(out vec4 C, vec2 O, sampler2D iChannel0)
{
    ivec2 I = ivec2(O);

    if (I.y >= npipes)
    {
        C = vec4(1,2,3,4);
        return;
    }

    //if (frame == -1) // doesn't work with preview in firefox
    if (texelFetch(iChannel0, ivec2(0, npipes), 0) != vec4(1,2,3,4))
    {
        C = vec4(0);
        C.x = profile(I.y, O.x / resolution.x);
        return;
    }

    vec4 I0 = texelFetch(iChannel0, ivec2(I.x + -1, I.y), 0);
    vec4 I1 = texelFetch(iChannel0, ivec2(I.x + -0, I.y), 0);
    vec4 I2 = texelFetch(iChannel0, ivec2(I.x + +1, I.y), 0);

    // Leap frog integration
    // 1a. Update flux
    I1.z += df(I0.x, I1.x, I0.y, I1.y, I1.z);
    I2.z += df(I1.x, I2.x, I1.y, I2.y, I2.z);

    // 1b. Override flux at boundary
    vec2 b = boundary(float(frame));
    if (I.x == 0)
        I1.z = b.x;
    if (I.x == int(resolution.x) - 1)
        I2.z = b.y;

    // 2. Update mass
    I1.y += dm(I1.z, I2.z);

    C = I1;
}

float constriction(float x, float w, float s)
{
    s /= resolution.x;
    float a = .5-w;
    float b = .5+w;
    return mix(1., .1, smoothstep(a-s, a, x) - smoothstep(b, b+s, x));
}

float narrowing(float x, float w, float s)
{
    s /= resolution.x;
    s /= 2.;
    return mix(1., .4, smoothstep(w-s, w+s, x));
}

#define buffer(i) \
void mainImage(out vec4 C, vec2 O) \
{ \
    resolution = iResolution; \
    frame = iFrame * nbuffers + i; \
    update(C, O, iChannel0); \
} \

