#define PI         3.1415926535
#define TWO_PI     6.2831853071
#define BIG_EPS    1e-3
#define SMOL_EPS   1e-8

#define Saturate(x) clamp(x, 0.0, 1.0)

#define M_LAMBERTIAN 0
#define M_METAL 1
#define M_DIELECTRIC 2

const float cameraNear = 0.001;
const float cameraFar  = 65535.0;

struct Camera
{
    vec3 origin, lowerLeftCorner, horizontal, vertical;
};

struct Ray
{
    vec3 origin, direction;
};

struct Material
{
    int type;
    vec3 albedo;
    float extraParam; // This can be fuzz for metals, or IOR for dielectrics (nothing for lambert)
};

struct HitResult
{
    float t;
    vec3 position, normal;
    Material material;
    bool frontFace;
};

struct Sphere
{
    vec3 center;
    float radius;
    Material material;
};

const int numberOfSpheres = 5;

const Sphere sphereList[numberOfSpheres] = Sphere[](
    Sphere(vec3(0.0, -5000.5, 0.0), 5000.0, Material(M_LAMBERTIAN, vec3(0.8, 0.8, 0.0), 0.0)), // floor
    Sphere(vec3(0.0, 0.0, -1.0), 0.5, Material(M_LAMBERTIAN, vec3(0.1, 0.2, 0.5), 0.0)), // center
    Sphere(vec3(-1.0-BIG_EPS, 0.0+BIG_EPS, -1.0), 0.5, Material(M_DIELECTRIC, vec3(1.0), 1.5)), // outer left
    Sphere(vec3(-1.0, 0.0, -1.0), -0.495, Material(M_DIELECTRIC, vec3(1.0), 1.5)), // inner left
    Sphere(vec3(1.0, 0.0, -1.0), 0.5, Material(M_METAL, vec3(0.8, 0.6, 0.2), 0.15)) // right   
);

// Quality hashes collection by nimitz https://www.shadertoy.com/view/Xt3cDn
float hashSeed = 0.0;

uint BaseHash(uvec2 p) {
    p = 1103515245U * ((p >> 1U) ^ (p.yx));
    uint h32 = 1103515245U * ((p.x) ^ (p.y >> 3U));
    return h32 ^ (h32 >> 16);
}

float Hash1(inout float seed)
{
    uint n = BaseHash(floatBitsToUint(vec2(seed+=.1,seed+=.1)));
    return float(n)*(1.0/float(0xffffffffU));
}

vec2 Hash2(inout float seed) {
    uint n = BaseHash(floatBitsToUint(vec2(seed += 0.1, seed += 0.1)));
    uvec2 rz = uvec2(n, n * 48271U);
    return vec2(rz.xy & uvec2(0x7fffffffU)) / float(0x7fffffff);
}

vec3 Hash3(inout float seed) {
    uint n = BaseHash(floatBitsToUint(vec2(seed += 0.1, seed += 0.1)));
    uvec3 rz = uvec3(n, n * 16807U, n * 48271U);
    return vec3(rz & uvec3(0x7fffffffU)) / float(0x7fffffff);
}

// Source: Karthik Karanth's blog: 
// https://karthikkaranth.me/blog/generating-random-points-in-a-sphere/#better-choice-of-spherical-coordinates
vec3 RandomPointInUnitSphere(inout float seed) {
    vec3 h = Hash3(seed) * vec3(TWO_PI, 2.0, 1.0) - vec3(0.0, 1.0, 0.0);
    float theta = h.x;
    float sinPhi = sqrt(1.0 - h.y * h.y);
    float r = pow(h.z, 1.0 / 3.0);

    return r * vec3(cos(theta) * sinPhi, sin(theta) * sinPhi, h.y);
}

vec3 GetIntersectPoint(in Ray ray, in float t)
{
    return ray.origin + ray.direction * t;
}

vec3 GetBackgroundColor(in float y)
{
    // Get a nice skyblue-ish gradient background
    return mix(vec3(1.0), vec3(0.5, 0.7, 1.0), y);
}

void FlipNormalAndIOR(in Ray incoming, inout HitResult result)
{
    bool isFrontFace = dot(incoming.direction, result.normal) < 0.0;

    if (isFrontFace)
    {
        result.material.extraParam  = 1.0 / result.material.extraParam;
    }
    else
    {
        result.normal = -result.normal;
    }
}

// PBRT Book refract: 
// https://www.pbr-book.org/3ed-2018/Reflection_Models/Specular_Reflection_and_Transmission#Refract
bool RefractPBRT(in vec3 V, in vec3 N, in float etaRatio, inout float cosThetaI, inout vec3 transmitted)
{
    transmitted = vec3(SMOL_EPS); // Avoid NaNs

    cosThetaI = max(0.0, dot(-V, N));
    float sin2ThetaI = 1.0 - cosThetaI * cosThetaI;
    float cos2ThetaT = 1.0 - etaRatio * etaRatio * sin2ThetaI;

    if (cos2ThetaT < 0.0) return false;

    transmitted = etaRatio * V + (etaRatio * cosThetaI - sqrt(cos2ThetaT)) * N;

    return true;
}

float SchlickApprox(float cosine, float etaRatio) 
{
    float r0 = (1.0 - etaRatio) / (1.0 + etaRatio);
    r0 *= r0;
    return r0 + (1.0 - r0) * pow((1.0 - cosine), 5.0);
}

// Scatter the incoming ray based on the bxdf
bool ScatterRay(in Ray incoming, in HitResult hitInfo, inout vec3 attenuation, inout Ray scattered)
{
    if (hitInfo.material.type == M_LAMBERTIAN)
    {
        scattered = Ray(hitInfo.position, normalize(hitInfo.normal + RandomPointInUnitSphere(hashSeed)));
        attenuation = hitInfo.material.albedo;
        return true;
    }
    else if(hitInfo.material.type == M_METAL)
    {
        scattered = Ray(hitInfo.position, normalize(reflect(incoming.direction, hitInfo.normal) + 
            hitInfo.material.extraParam * RandomPointInUnitSphere(hashSeed)));
        attenuation = hitInfo.material.albedo;
        return dot(scattered.direction, hitInfo.normal) > 0.0;
    }
    else if (hitInfo.material.type == M_DIELECTRIC)
    {
        FlipNormalAndIOR(incoming, hitInfo);
        vec3 transmitDirection;

        float cosThetaI;
        bool isRefracted = RefractPBRT(incoming.direction, hitInfo.normal, hitInfo.material.extraParam, 
            cosThetaI, transmitDirection);
        float shouldReflect = isRefracted ? step(Hash1(hashSeed), 
            SchlickApprox(cosThetaI, hitInfo.material.extraParam)) : 1.0;
        transmitDirection = mix(transmitDirection, reflect(incoming.direction, hitInfo.normal), shouldReflect);

        scattered = Ray(hitInfo.position, normalize(transmitDirection));

        attenuation = hitInfo.material.albedo;

        return true;
    }

    return false;
}

bool IntersectSphere(in Ray ray, in Sphere sphere, in float tMin, in float tMax, inout HitResult result)
{
    vec3 oc = ray.origin - sphere.center;
    float a = dot(ray.direction, ray.direction);
    float b = dot(oc, ray.direction);
    float c = dot(oc, oc) - sphere.radius * sphere.radius;
    float d = b * b - a * c;

    if (d < 0.0) return false;

    float temp = (-b - sqrt(d)) / a; // Nearest hit

    if (temp < tMax && temp > tMin)
    {
        result.t = temp;
        result.position = GetIntersectPoint(ray, temp);
        result.normal = (result.position - sphere.center) * 1.0 / sphere.radius;
        result.material = sphere.material;
        return true;
    }

    return false;
}

bool IntersectWorld(in Ray ray, inout HitResult worldResult)
{
    float closestHit = cameraFar;
    HitResult tempResult;
    bool worldHit = false;

    for (int i = 0; i < numberOfSpheres; ++i)
    {
        if (IntersectSphere(ray, sphereList[i], cameraNear, closestHit, tempResult))
        {
            closestHit = tempResult.t;
            worldResult = tempResult;
            worldHit = true;
        }
    }

    return worldHit;
}

#define MAX_BOUNCES 8

vec3 GetSceneColor(in Ray ray)
{
    HitResult worldResult;
    vec3 sceneColor = vec3(1.0);

    for (int i = 0; i < MAX_BOUNCES; ++i)
    {
        if (IntersectWorld(ray, worldResult))
        {
            Ray scattered; 
            vec3 attenuation;
            if (ScatterRay(ray, worldResult, attenuation, scattered))
            {
                sceneColor *= attenuation;
                ray = scattered;
            }
            else
            {
                break;
            }
        }
        else
        {
            return sceneColor * GetBackgroundColor(ray.direction.y * 0.5 + 0.5);
        }
    }

    return vec3(0.0);
}

Ray GetCameraRay(in Camera camera, in vec2 uv)
{
    return Ray(camera.origin, normalize(camera.lowerLeftCorner + uv.x * camera.horizontal \
        + uv.y * camera.vertical - camera.origin));
}

Camera GetCamera(in vec3 eye, in vec3 lookAt, in vec3 up, in vec2 camRot, in float fov, in float aspect)
{
    Camera camera;

    float theta = fov * PI / 180.0;
    float h = tan(theta * 0.5);
    float viewportHeight = 2.0 * h;
    float viewportWidth = aspect * viewportHeight;

    // Handle camera mouse rotation
    float camRadius = length(eye - lookAt);

    // Translate toward anchor point
    eye -= lookAt;

    // Rotate using spherical coords
    camRot.x = camRot.x * PI * 2.0;
    camRot.y = mix(PI * 0.04, PI * 0.54, camRot.y);

    eye.x = camRadius * sin(camRot.x) * sin(camRot.y);
    eye.y = camRadius * cos(camRot.y);
    eye.z = camRadius * cos(camRot.x) * sin(camRot.y);

    // Translate rotated position back
    eye += lookAt;

    // Construct orthonormal basis
    vec3 camForward = normalize(eye - lookAt);
    vec3 camLeft = normalize(cross(up, camForward));
    vec3 camUp = cross(camForward, camLeft);

    camera.origin = eye;
    camera.horizontal = viewportWidth * camLeft;
    camera.vertical = viewportHeight * camUp;
    camera.lowerLeftCorner = camera.origin - camera.horizontal * 0.5 \
        - camera.vertical * 0.5 - camForward;

    return camera;
}

