/**/

//                    = Anaglyph XOR-City =         
//                by Maximilian Knape >| 2022            
// -----------------------------------------------------------
// This work is licensed under a Creative Commons Attribution-
//        NonCommercial-ShareAlike 3.0 Unported License

#define GAMMA vec3(.4545)

#define MAX_STEPS 140
#define STEP_FAC 0.13
#define MAX_DIST 200.
#define MIN_DIST 5.

#define SURF_DIST .0001
#define SURF_MUL 10000.
#define SURF_EXP 1.2

#define GLOW_INT 0.8
#define PP_CONT 1.2
#define PP_VIGN 2.0

#define EYE_DIS 0.4
#define FIL_COR 1.1


#define XOR_SEED 10000


#define iTime iTime*1.
#define pi 3.14159265358979
#define S(x,y,t) smoothstep(x,y,t)

mat2 Rot(float a) 
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float SdBox(vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)),0.) + length(max(d, 0.));
}

float SdBoxFrame( vec3 p, vec3 b, float e )
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;

  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

vec2 Dist(vec3 p) 
{   

    float col;

    //cam movement
    float cmt = pi / 160.; 
    float pos = pow(cos(iTime*cmt), 3.) * 800.;
    float x = (p.x + mix(pos, -pos, step(0., cos(iTime*cmt*.5-pi*.25))));
    float y = (p.z + mix(0., pos, step(0., cos(iTime*cmt))));

    int r = int(x+y)^(int(x-y)); //XOR 
    float d = float(abs((r*r*r)/int(x+y)) % (XOR_SEED)) / float(XOR_SEED)*1.0; //small buildings

    float w = S(-0.1, 1., sin(((x+sin(y/4.)-cos(y/8.))*6.)*1.)*0.03 + //small sand waves
                dot(sin(vec2(x+10., y)/16.), cos(vec2(x, y+10.)/21.)))*0.5; //displacement

    w = sin((x+y)/50.)*dot(sin(vec2(x+100., y)/42.), cos(vec2(x, y+100.)/64.))*8. - w; //sand hills
    float dis = sqrt(x*x + y*y);
    w *= S(50.,100., dis);

    col = d > w ? .6 : -0.2; //material
    d = max(w, d);
    d = (p.y - d); //heightmap

    //ancient temple buildings
    float size = 21.6;
    float th = 0.5 + S(20., 50., dis)*3. - S(400., 1000., dis)*3.4;
    p.xz = vec2(x,y)/1.24;
    p.xz *= Rot(pi*(.5 - S(10., 20., dis) * .25));
    p.xz = mod(abs(-mod(p.xz + vec2(0,.5), 4.*size) + 2.*size), 4.*size);
    p.yx *= Rot(.02 * step(500., dis));
    float c = SdBox(p - vec3(1,0,1)*(1.-pow(S(0., 800., dis), 0.6)) * 42.2,
        vec3(th, 1.2 + (pow(S(700., 50., dis), 5.)+step(850., dis)) * 7. + step(-50., -dis)*100., th));    

    col = d > c ? mix(-.2+S(.9, 1., sin(iTime*3.-sqrt(p.y/3.)))*0.5, .55, step(20., dis)) : col;
    d = min(c, d);

    return vec2(d, col);
}

vec3 Normal(vec3 p) 
{
    vec2 e = vec2(.001, 0.);
    return normalize(Dist(p).x - vec3(Dist(p-e.xyy).x, Dist(p-e.yxy).x,Dist(p-e.yyx).x));
}

vec3 RayMarch(vec3 ro, vec3 rd) 
{
    float col = 0.;
	float dO = MIN_DIST;
    int steps = 0;

    for(int i = 0; i < MAX_STEPS; i++) 
    {
        steps = i;

    	vec3 p = ro + rd*dO;
        vec2 dS = Dist(p);
        col = dS.y;

        float dis = pow(dO/MAX_DIST, SURF_EXP);
        dO += dS.x * mix(STEP_FAC, 0.8, min(1., dis + S(1.2, 3., p.y)));//dS.x * mix(STEP_FAC, 1., dis);//min(dS.x * STEP_FAC, max(1. + p.y - XORMap(p.xz, 5.)*10., SURF_DIST));

        if (dO > MAX_DIST || dS.x < (SURF_DIST * (dis*SURF_MUL+1.))) break;
    }

    return vec3(dO, steps, col);
}

float SoftShadow(vec3 ro, vec3 lp, float k) //Shane
{
    const int maxIterationsShad = 24; 

    vec3 rd = lp - ro;

    float shade = 1.;
    float dist = .002;    
    float end = max(length(rd), .001);
    float stepDist = end/float(maxIterationsShad);

    rd /= end;

    for (int i = 0; i<maxIterationsShad; i++)
    {

        float h = Dist(ro + rd*dist).x;
        shade = min(shade, S(0., 1., k*h/dist));
        dist += clamp(h, .02, .25);

        if (h < .0 || dist > end) break;
    }

    return min(max(shade, 0.) + .1, 1.); 
}


float GetLight(vec3 p, vec3 n, vec3 lP, vec3 rd , float shine) 
{

    vec3 l = normalize(lP - p);
    float dif = S(-0., 1., dot(n, l)*.5+.5) * max(1.-max(-shine, 0.), 0.);
    if (shine > 0.) dif = mix(dif, dif*.1 + pow(max(dot(l, reflect(rd, n)), 0.), shine*20.), shine);
    return dif;
}


vec3 R(vec2 uv, vec3 p, vec3 l, float z) //BigWings
{
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y;
	vec2 m = iMouse.xy / iResolution.xy;
    if (length(m) <= 0.) m = vec2(.3,.7);

    float offset = S(15., 0., iTime) * 20.;
    vec3 ro = vec3(0., 10. + offset, -50. + m.y*30. - offset*2.);
    ro.yz *= Rot((1.0-m.y) * .2*pi);
    ro.xz *= Rot(-m.x * 2.*pi);
    vec3 rd = R(uv, ro, vec3(0., 10., 0.), 0.9);
    ro += cross(rd, vec3(0., -1., 0.)) * EYE_DIS * -.5;

    vec3 n, lPos = vec3(3,1.3,-1)*1000.;
    lPos.xz *= Rot(2.*pi*iTime/300.);
    float bg = pow(S(-1., 1., dot(rd, normalize(lPos))), 2.5)*.5+.4;

    //left eye
    float colL = bg;
    vec3 p1 = vec3(0.);
    vec3 rmd = RayMarch(ro, rd);

    if(rmd.x < MAX_DIST) 
    {
        p1 = ro + rd * rmd.x;
        n = Normal(p1);

        colL = GetLight(p1, n, lPos, rd, rmd.z);
        colL *= SoftShadow(p1 + n*.015, lPos, 1.5);
        colL += rmd.y / float(MAX_STEPS) * GLOW_INT;
        colL = mix(colL, bg, rmd.x / MAX_DIST);
    }

    //eye distance
    ro += cross(rd, vec3(0., -1., 0.)) * EYE_DIS;

    //right eye
    float colR = bg;
    vec3 p2 = vec3(0.);
    rmd = RayMarch(ro, rd);

    if(rmd.x < MAX_DIST) 
    {
        p2 = ro + rd * rmd.x;
        n = Normal(p2);

        colR = GetLight(p2, n, lPos, rd, rmd.z);
        colR *= SoftShadow(p2 + n*.015, lPos, 1.5);
        colR += rmd.y / float(MAX_STEPS) * GLOW_INT;
        colR = mix(colR, bg, rmd.x / MAX_DIST);
    }

    //filter correction
    colL *= FIL_COR; 
    colR /= FIL_COR;

    //contrast
    vec3 colS = mix(vec3(colL, colR, colR), S(vec3(0.), vec3(1.), vec3(colL, colR, colR)), PP_CONT);

    //ACES color
    //colS =  clamp((colS * (2.51f * colS + 0.03f)) / (colS * (2.43f * colS + 0.59f) + 0.14f), 0.0f, 1.0f);

    //gamma correction
    colS = pow(colS, GAMMA);

    //Vignette
    colS *= S(PP_VIGN,-PP_VIGN/5., dot(uv,uv)); 

    fragColor = vec4(colS,1.0);
}

#define P3(x, x0, x1, y0, y1) ((y0) + ((x)-(x0)) / ((x1)-(x0)) * ((y1)-(y0)))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
