  float p;
  float t;

  /* rotate (x0,y0,z0) and (x1,y1,z1) */
  float angle = 2. * M_PI * frame / 100.;

  rotate_x(x0, y0, z0, -M_PI/7. * cos(2. * M_PI * frame / 80.));
  rotate_x(x1, y1, z1, -M_PI/7. * cos(2. * M_PI * frame / 80.));

  rotate_y(x0, y0, z0, angle);
  rotate_y(x1, y1, z1, angle);

