   * (x-x0) / (x1-x0) = (y-y0) / (y1-y0)
   * y = y0 + (x-x0) / (x1-x0) * (y1-y0)
   */
#define P3(x, x0, x1, y0, y1) ((y0) + ((x)-(x0)) / ((x1)-(x0)) * ((y1)-(y0)))
  /* p in [1/2 .. 4] */
  frame = mod(frame, 200.);
  /* 1/2   1   2    4 */
  if (frame < 50.)       p = P3(frame, 0., 50., 1./2., 1.);
  else if (frame < 75.)  p = P3(frame, 50., 75., 1., 2.);
  else if (frame < 100.) p = P3(frame, 75., 100., 2., 4.);
  else if (frame < 125.) p = P3(frame, 100., 125., 4., 2.);
  else if (frame < 150.) p = P3(frame, 125., 150., 2., 1.);
  else                   p = P3(frame, 150., 200., 1., 1./2.);

  vec4 blue = vec4(15, 17, 23, 0)/255.;
  t = dig_t(p, x0, y0, z0, x1, y1, z1);
  if (t == 1000.) return blue;

  /* a palette based on distance from (0,0,0) to the point */
  float d = euclid(t, x0, y0, z0, x1, y1, z1);
  float g = P3(d, 0., 0.6, 125., 10.);
  float rb = P3(d, 0., 0.6, 125., 10.);
  float ra = P3(d, 0., 2., 0., 255.);
  g = clamp(g, 0., 255.);
  rb = clamp(rb, 0., 255.);
  float w = clamp(P3(d, 1., 1.4, 0., 120.), 0., 255.);
  return vec4(
    max(clamp(ra+rb, 0., 255.), w),
    max(g, w),
    max(20., w),
    0) / 255.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  if (fragCoord.x > 160. || fragCoord.y > 90.) { fragColor = vec4(0,0,0,0); return; }

  float x = fragCoord.x-80.;
  float y = fragCoord.y-45.;
  x = floor(x);
  y = floor(y);
  float frame = iTime * 25.;
  float scale = 1./100.;
  float x0 = x * scale;
  float y0 = y * scale;
  fragColor = raytrace_norm(x0, y0, frame);
}

