vec2 parametric_cubic_bezier(float t, vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	vec2 a0 = (-p0 + 3. * p1 - 3. * p2 + p3);
	vec2 a1 = (3. * p0  -6. * p1 + 3. * p2);
	vec2 a2 = (-3. * p0 + 3. * p1);
	vec2 a3 = p0;

	return (((a0 * t) + a1) * t + a2) * t + a3;
}

float cubic_bezier_normal_iteration(float t, vec2 a0, vec2 a1, vec2 a2, vec2 a3){
	//horner's method
	vec2 a_2=a2+t*a3;
	vec2 a_1=a1+t*a_2;
	vec2 b_2=a_2+t*a3;

	vec2 uv_to_p=a0+t*a_1;
	vec2 tang=a_1+t*b_2;

	float l_tang=dot(tang,tang);
	return t-dot(tang,uv_to_p)/l_tang;
}

float cubic_bezier_dis_approx_sq(vec2 uv, vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	vec2 a3 = (-p0 + 3. * p1 - 3. * p2 + p3);
	vec2 a2 = (3. * p0 - 6. * p1 + 3. * p2);
	vec2 a1 = (-3. * p0 + 3. * p1);
	vec2 a0 = p0 - uv;

	float d0 = 1e38;

	vec4 t0=vec4(0.,.25,.75,1.);
	float t;

	for(int i=0;i<3;i++){
		t=t0[i];
		for(int j=0;j<3;j++){
			t=cubic_bezier_normal_iteration(t,a0,a1,a2,a3);
		}
		t=clamp(t,0.,1.);
		vec2 uv_to_p=((a3*t+a2)*t+a1)*t+a0;
		d0=min(d0,dot(uv_to_p,uv_to_p));
	}

	return d0;
}

float cubic_bezier_dis_approx(vec2 uv, vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	return sqrt(cubic_bezier_dis_approx_sq(uv,p0,p1,p2,p3));
}

// Modified from http://tog.acm.org/resources/GraphicsGems/gems/Roots3And4.c
// Credits to Doublefresh for hinting there
int solve_quadric(vec2 coeffs, inout vec2 roots){

    // normal form: x^2 + px + q = 0
    float p = coeffs[1] / 2.;
    float q = coeffs[0];

    float D = p * p - q;

    if (D < 0.){
		return 0;
    }
    else if (D > 0.){
		roots[0] = -sqrt(D) - p;
		roots[1] = sqrt(D) - p;

		return 2;
    }
}

//From Trisomie21
//But instead of his cancellation fix i'm using a newton iteration
int solve_cubic(vec3 coeffs, inout vec3 r){

	float a = coeffs[2];
	float b = coeffs[1];
	float c = coeffs[0];

	float p = b - a*a / 3.0;
	float q = a * (2.0*a*a - 9.0*b) / 27.0 + c;
	float p3 = p*p*p;
	float d = q*q + 4.0*p3 / 27.0;
	float offset = -a / 3.0;
	if(d >= 0.0) { // Single solution
		float z = sqrt(d);
		float u = (-q + z) / 2.0;
		float v = (-q - z) / 2.0;
		u = sign(u)*pow(abs(u),1.0/3.0);
		v = sign(v)*pow(abs(v),1.0/3.0);
		r[0] = offset + u + v;	

		//Single newton iteration to account for cancellation
		float f = ((r[0] + a) * r[0] + b) * r[0] + c;
		float f1 = (3. * r[0] + 2. * a) * r[0] + b;

		r[0] -= f / f1;

		return 1;
	}
	float u = sqrt(-p / 3.0);
	float v = acos(-sqrt( -27.0 / p3) * q / 2.0) / 3.0;
	float m = cos(v), n = sin(v)*1.732050808;

	//Single newton iteration to account for cancellation
	//(once for every root)

	float f,f1;
	r[0] = offset + u * (m + m);

	f = ((r[0] + a) * r[0] + b) * r[0] + c;
	f1 = (3. * r[0] + 2. * a) * r[0] + b;

	r[0] -= f / f1;

	r[1] = offset - u * (n + m);

	f = ((r[1] + a) * r[1] + b) * r[1] + c;
	f1=(3. * r[1] + 2. * a) * r[1] + b;

	r[1] -= f / f1;

	r[2] = offset + u * (n - m);

	f = ((r[2] + a) * r[2] + b) * r[2] + c;
	f1 = (3. * r[2] + 2. * a) * r[2] + b;

	r[2] -= f / f1;

	return 3;
}

float halley_iteration4(vec4 coeffs, float x){

	float f = (((x + coeffs[3]) * x + coeffs[2]) * x + coeffs[1]) * x + coeffs[0];
	float f1 = ((4. * x + 3. * coeffs[3]) * x + 2. * coeffs[2]) * x + coeffs[1];
	float f2 = (12. * x + 6. * coeffs[3]) * x + 2. * coeffs[2];

	return x - (2. * f * f1) / (2. * f1 * f1 - f * f2);
}

// Modified from http://tog.acm.org/resources/GraphicsGems/gems/Roots3And4.c
// Credits to Doublefresh for hinting there
int solve_quartic(vec4 coeffs, inout vec4 s){

	float a = coeffs[3];
	float b = coeffs[2];
	float c = coeffs[1];
	float d = coeffs[0];

    /*  substitute x = y - A/4 to eliminate cubic term:
	x^4 + px^2 + qx + r = 0 */

    float sq_a = a * a;
    float p = - 3./8. * sq_a + b;
    float q = 1./8. * sq_a * a - 1./2. * a * b + c;
    float r = - 3./256.*sq_a*sq_a + 1./16.*sq_a*b - 1./4.*a*c + d;

	int num;

	/* doesn't seem to happen for me */
    //if(abs(r)<eps){
	//	/* no absolute term: y(y^3 + py + q) = 0 */

	//	vec3 cubic_coeffs;

	//	cubic_coeffs[0] = q;
	//	cubic_coeffs[1] = p;
	//	cubic_coeffs[2] = 0.;

	//	num = solve_cubic(cubic_coeffs, s.xyz);

	//	s[num] = 0.;
	//	num++;
    //}
    {
		/* solve the resolvent cubic ... */

		vec3 cubic_coeffs;

		cubic_coeffs[0] = 1.0/2. * r * p - 1.0/8. * q * q;
		cubic_coeffs[1] = - r;
		cubic_coeffs[2] = - 1.0/2. * p;

		solve_cubic(cubic_coeffs, s.xyz);

		/* ... and take the one real solution ... */

		float z = s[0];

		/* ... to build two quadric equations */

		float u = z * z - r;
		float v = 2. * z - p;

		if(u > -eps){
			u = sqrt(abs(u));
		}
		else{
			return 0;
		}

		if(v > -eps){
			v = sqrt(abs(v));
		}
		else{
			return 0;
		}

		vec2 quad_coeffs;

		quad_coeffs[0] = z - u;
		quad_coeffs[1] = q < 0. ? -v : v;

		num = solve_quadric(quad_coeffs, s.xy);

		quad_coeffs[0]= z + u;
		quad_coeffs[1] = q < 0. ? v : -v;

		vec2 tmp=vec2(1e38);
		int old_num=num;

		num += solve_quadric(quad_coeffs, tmp);

		//s[old_num] = tmp[0];
		//s[old_num+1] = tmp[1];

		if(old_num == 0){
            s[0] = tmp[0];
            s[1] = tmp[1];
        }
        else{//old_num == 2
            s[2] = tmp[0];
            s[3] = tmp[1];
        }

    }

    /* resubstitute */

    float sub = 1./4. * a;

	/* single halley iteration to fix cancellation */
	for(int i=0;i<4;i+=2){
		if(i < num){
			s[i] -= sub;
			s[i] = halley_iteration4(coeffs,s[i]);

			s[i+1] -= sub;
			s[i+1] = halley_iteration4(coeffs,s[i+1]);
		}
	}

    return num;
}

float segment_dis( vec2 p, vec2 a, vec2 b ){
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

vec2 incenter(vec2 p0, vec2 p1, vec2 p2){
	vec2 v1=normalize(p1-p0);
	vec2 v2=normalize(p2-p0);

	vec2 v3=normalize(p2-p1);
	vec2 v4=normalize(p0-p1);

	vec3 line1, line2;
	line1.xy=(v1+v2)/2.;
	line1.xy=vec2(-line1.y,line1.x);
	line1.z=-dot(line1.xy,p0);

	line2.xy=(v3+v4)/2.;
	line2.xy=vec2(-line2.y,line2.x);
	line2.z=-dot(line2.xy,p1);

	vec3 l12=cross(line1,line2);

	return l12.xy/l12.z;
}

vec2 circumcenter(vec2 p0, vec2 p1, vec2 p2){
	vec3 line1, line2;
	line1.xy=p1-p0;
	line1.z=-dot(line1.xy,(p0+p1)/2.);

	line2.xy=p2-p1;
	line2.z=-dot(line2.xy,(p1+p2)/2.);

	vec3 m1=cross(line1,line2);
	m1/=m1.z;

	return m1.xy;
}

vec2 compute_best_node(vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	vec3 line1, line2, line3, line4;

	line1.xy=p1-p0;
	line1.xy=vec2(line1.y,-line1.x);
	line1.z=-dot(line1.xy,p0);

	line2.xy=p3-p2;
	line2.xy=vec2(line2.y,-line2.x);
	line2.z=-dot(line2.xy,p2);

	vec3 l12=cross(line1,line2);

	vec2 m0=l12.xy/l12.z;
	vec2 m1=incenter(p0,m0,p3);
	vec2 m2=circumcenter(p0,m1,p3);

	line3.xy=m2-p0;
	line3.z=-dot(line3.xy,p0);

	line4.xy=p3-p0;
	line4.z=-dot(line4.xy,(p0+p3)/2.);

	vec3 l34=cross(line3,line4);
	vec2 m3=l34.xy/l34.z;

	vec2 a3=(-p0+3.* p1-3.*p2+p3);
	vec2 a2=(3.*p0-6.*p1+3.*p2);
	vec2 a1=(-3.*p0+3.*p1);

	vec2 a0_1=p0-m2;
	vec2 a0_2=p0-m3;

	float b5=dot(a3,a3);
	float b4=2.*dot(a3,a2);
	float b3=dot(a2,a2) + 2.*dot(a1,a3);

	float b2_1=2.*(dot(a1,a2) + dot(a0_1,a3));
	float b2_2=2.*(dot(a1,a2) + dot(a0_2,a3));

	float b1_1=dot(a1,a1) + 2.*dot(a0_1,a2);
	float b1_2=dot(a1,a1) + 2.*dot(a0_2,a2);

	float c4=b5;
	float c3=b4+c4;
	float c2=b3+c3;

	float c1_1=b2_1+c2;
	float c1_2=b2_2+c2;

	float c0_1=b1_1+c1_1;
	float c0_2=b1_2+c1_2;

	vec4 roots_1 = vec4(1e38);
	int num_roots_1 = solve_quartic(vec4(c0_1,c1_1,c2,c3)/c4,roots_1);

	for(int i=0;i<num_roots_1;i++){
		if(roots_1[i] > 0. && roots_1[i] < 1.){
			return parametric_cubic_bezier(roots_1[i],p0,p1,p2,p3);
		}
	}

	vec4 roots_2 = vec4(1e38);
	int num_roots_2 = solve_quartic(vec4(c0_2,c1_2,c2,c3)/c4,roots_2);

	for(int i=0;i<num_roots_2;i++){
		if(roots_2[i] > 0. && roots_2[i] < 1.){
			return parametric_cubic_bezier(roots_2[i],p0,p1,p2,p3);
		}
	}

	return m1;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float border=2./iResolution.x;

	vec2 uv=fragCoord.xy/iResolution.xy;
	uv-=.5;
    uv.y*=iResolution.y/iResolution.x;

	vec2 mouse=iMouse.xy/iResolution.xy;
	mouse-=.5;
    mouse.y*=iResolution.y/iResolution.x;

	border*=zoom;
	uv*=zoom;
	mouse*=zoom;

	vec2 p0=vec2(-.3,-.1);
	#ifdef MOVE_NODE
	vec2 p1=vec2(-.4,.2);
	#else
	vec2 p1=mouse;
    // Animate if mouse is in left bottom corner
    if(max(iMouse.x, iMouse.y) < 20.){
        p1=vec2(-.2,.0)+vec2(.1*sin(iTime),.2*cos(iTime));
    }
	#endif
	vec2 p2=vec2(.1,-.2);
	vec2 p3=vec2(.2,.15);

	vec3 line0, line1, line2, line3, line4, line5, line6, line7;

	// Tangent in p0

	line0.xy=p0-p1;
	line0.xy=normalize(vec2(line0.y,-line0.x));
	line0.z=-dot(line0.xy,p0);

	// Tangent in p3

	line1.xy=p2-p3;
	line1.xy=normalize(vec2(line1.y,-line1.x));
	line1.z=-dot(line1.xy,p3);

	// Tangent intersection point
	vec3 l01=cross(line0,line1);
	vec2 p4=l01.xy/l01.z;

	#ifdef MOVE_NODE
	vec2 node=mouse;
	#else
	vec2 node=compute_best_node(p0,p1,p2,p3);
	#endif

	vec3 color=bg_col;

	// Show segments
	float dis=segment_dis(uv,p0,p1)-line_width;
	dis=min(dis,segment_dis(uv,p1,p2)-line_width);
	dis=min(dis,segment_dis(uv,p2,p3)-line_width);
	color=mix(line_col,color,smoothstep(-border/2.,border/2.,dis));

	// Show cubic bezier curve
	dis=cubic_bezier_dis_approx(uv,p0,p1,p2,p3)-line_width;
	color=mix(bezier_col,color,smoothstep(-border/2.,border/2.,dis));

	// Line at p0 perpendicular to the segment between p0 and p1
	line2.xy=p4-p0;
	line2.z=-dot(line2.xy,p0);

	// Perpendicular bisector of segment between p0 and node
	line3.xy=node-p0;
	line3.z=-dot(line3.xy,(p0+node)/2.);

	// Line at p3 perpendicular to the segment between p2 and p3
	line4.xy=p3-p4;
	line4.z=-dot(line4.xy,p3);

	// Perpendicular bisector of segment between p3 and node
	line5.xy=node-p3;
	line5.z=-dot(line5.xy,(p3+node)/2.);

	// The midpoint of the first arc
	vec3 l23=cross(line2,line3);
	vec2 m1=l23.xy/l23.z;
	float r1=distance(m1,p0);

	// The midpoint of the second arc
	vec3 l45=cross(line4,line5);
	vec2 m2=l45.xy/l45.z;
	float r2=distance(m2,p3);

	vec2 m3=incenter(p0,p4,p3);

	// The midpoint of the first green circle
	vec2 m4=circumcenter(p0,m3,p3);
	float r4=distance(p0,m4);

	// Line at p0 perpendicular to the first green circle
	line6.xy=m4-p0;
	line6.z=-dot(line6.xy,p0);

	// Perpendicular bisector of segment between p0 and p3
	line7.xy=p3-p0;
	line7.z=-dot(line7.xy,(p0+p3)/2.);

	// The midpoint of the second green circle
	vec3 l67=cross(line6,line7);
	vec2 m5=l67.xy/l67.z;
	float r5=distance(p0,m5);

	// Show red circles (arcs)
	dis=abs(distance(m1,uv)-r1)-line_width;
	dis=min(dis,abs(distance(m2,uv)-r2)-line_width);
	color=mix(arc_col,color,smoothstep(-border/2.,border/2.,dis));

	// Show green circles
	dis=abs(distance(m4,uv)-r4)-line_width;
	dis=min(dis,abs(distance(m5,uv)-r5)-line_width);
	color=mix(vec3(0,1,0),color,smoothstep(-border/2.,border/2.,dis));

	// Show dots
	dis=distance(p0,uv)-dot_size;
	dis=min(dis,distance(uv,p1)-dot_size);
	dis=min(dis,distance(uv,p2)-dot_size);
	dis=min(dis,distance(uv,p3)-dot_size);
	dis=min(dis,distance(uv,p4)-dot_size);
	dis=min(dis,distance(uv,node)-dot_size);
	dis=min(dis,distance(m1,uv)-dot_size);
	dis=min(dis,distance(m2,uv)-dot_size);
	dis=min(dis,distance(m3,uv)-dot_size);
	dis=min(dis,distance(m4,uv)-dot_size);
	dis=min(dis,distance(m5,uv)-dot_size);
	color=mix(dot_col,color,smoothstep(-border/2.,border/2.,dis));

	fragColor=vec4(color,1);
}

