//#define DISTANCE_FIELD /* Show distance field */

const vec3 dot_col=vec3(0,0,1);
const vec3 line_col=vec3(0,0,1);
const vec3 bezier_col=vec3(0,1,0);
const vec3 arc_col=vec3(1,0,0);
const vec3 bg_col=vec3(1);

const float dot_size=.004;
const float line_width=.001;

const float zoom=1.;

const float pi=3.1415925;

const float eps = .000005;

vec2 parametric_cubic_bezier(float t, vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	vec2 a0 = (-p0 + 3. * p1 - 3. * p2 + p3);
	vec2 a1 = (3. * p0  -6. * p1 + 3. * p2);
	vec2 a2 = (-3. * p0 + 3. * p1);
	vec2 a3 = p0;

	return (((a0 * t) + a1) * t + a2) * t + a3;
}

float cubic_bezier_normal_iteration(float t, vec2 a0, vec2 a1, vec2 a2, vec2 a3){
	//horner's method
	vec2 a_2=a2+t*a3;
	vec2 a_1=a1+t*a_2;
	vec2 b_2=a_2+t*a3;

	vec2 uv_to_p=a0+t*a_1;
	vec2 tang=a_1+t*b_2;

	float l_tang=dot(tang,tang);
	return t-dot(tang,uv_to_p)/l_tang;
}

float cubic_bezier_dis_approx_sq(vec2 uv, vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	vec2 a3 = (-p0 + 3. * p1 - 3. * p2 + p3);
	vec2 a2 = (3. * p0 - 6. * p1 + 3. * p2);
	vec2 a1 = (-3. * p0 + 3. * p1);
	vec2 a0 = p0 - uv;

	float d0 = 1e38;

	vec4 t0=vec4(0.,.25,.75,1.);
	float t;

	for(int i=0;i<4;i++){
		t=t0[i];
		for(int j=0;j<3;j++){
			t=cubic_bezier_normal_iteration(t,a0,a1,a2,a3);
		}
		t=clamp(t,0.,1.);
		vec2 uv_to_p=((a3*t+a2)*t+a1)*t+a0;
		d0=min(d0,dot(uv_to_p,uv_to_p));
	}

	return d0;
}

float cubic_bezier_dis_approx(vec2 uv, vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	return sqrt(cubic_bezier_dis_approx_sq(uv,p0,p1,p2,p3));
}

// Modified from http://tog.acm.org/resources/GraphicsGems/gems/Roots3And4.c
// Credits to Doublefresh for hinting there
int solve_quadric(vec2 coeffs, inout vec2 roots){

    // normal form: x^2 + px + q = 0
    float p = coeffs[1] / 2.;
    float q = coeffs[0];

    float D = p * p - q;

    if (D < 0.){
		return 0;
    }
    else if (D > 0.){
		roots[0] = -sqrt(D) - p;
		roots[1] = sqrt(D) - p;

		return 2;
    }
}

//From Trisomie21
//But instead of his cancellation fix i'm using a newton iteration
int solve_cubic(vec3 coeffs, inout vec3 r){

	float a = coeffs[2];
	float b = coeffs[1];
	float c = coeffs[0];

	float p = b - a*a / 3.0;
	float q = a * (2.0*a*a - 9.0*b) / 27.0 + c;
	float p3 = p*p*p;
	float d = q*q + 4.0*p3 / 27.0;
	float offset = -a / 3.0;
	if(d >= 0.0) { // Single solution
		float z = sqrt(d);
		float u = (-q + z) / 2.0;
		float v = (-q - z) / 2.0;
		u = sign(u)*pow(abs(u),1.0/3.0);
		v = sign(v)*pow(abs(v),1.0/3.0);
		r[0] = offset + u + v;	

		//Single newton iteration to account for cancellation
		float f = ((r[0] + a) * r[0] + b) * r[0] + c;
		float f1 = (3. * r[0] + 2. * a) * r[0] + b;

		r[0] -= f / f1;

		return 1;
	}
	float u = sqrt(-p / 3.0);
	float v = acos(-sqrt( -27.0 / p3) * q / 2.0) / 3.0;
	float m = cos(v), n = sin(v)*1.732050808;

	//Single newton iteration to account for cancellation
	//(once for every root)

	float f,f1;
	r[0] = offset + u * (m + m);

	f = ((r[0] + a) * r[0] + b) * r[0] + c;
	f1 = (3. * r[0] + 2. * a) * r[0] + b;

	r[0] -= f / f1;

	r[1] = offset - u * (n + m);

	f = ((r[1] + a) * r[1] + b) * r[1] + c;
	f1=(3. * r[1] + 2. * a) * r[1] + b;

	r[1] -= f / f1;

	r[2] = offset + u * (n - m);

	f = ((r[2] + a) * r[2] + b) * r[2] + c;
	f1 = (3. * r[2] + 2. * a) * r[2] + b;

	r[2] -= f / f1;

	return 3;
}

float halley_iteration4(vec4 coeffs, float x){

	float f = (((x + coeffs[3]) * x + coeffs[2]) * x + coeffs[1]) * x + coeffs[0];
	float f1 = ((4. * x + 3. * coeffs[3]) * x + 2. * coeffs[2]) * x + coeffs[1];
	float f2 = (12. * x + 6. * coeffs[3]) * x + 2. * coeffs[2];

	return x - (2. * f * f1) / (2. * f1 * f1 - f * f2);
}

// Modified from http://tog.acm.org/resources/GraphicsGems/gems/Roots3And4.c
// Credits to Doublefresh for hinting there
int solve_quartic(vec4 coeffs, inout vec4 s){

	float a = coeffs[3];
	float b = coeffs[2];
	float c = coeffs[1];
	float d = coeffs[0];

    /*  substitute x = y - A/4 to eliminate cubic term:
	x^4 + px^2 + qx + r = 0 */

    float sq_a = a * a;
    float p = - 3./8. * sq_a + b;
    float q = 1./8. * sq_a * a - 1./2. * a * b + c;
    float r = - 3./256.*sq_a*sq_a + 1./16.*sq_a*b - 1./4.*a*c + d;

	int num;

	/* doesn't seem to happen for me */
    //if(abs(r)<eps){
	//	/* no absolute term: y(y^3 + py + q) = 0 */

	//	vec3 cubic_coeffs;

	//	cubic_coeffs[0] = q;
	//	cubic_coeffs[1] = p;
	//	cubic_coeffs[2] = 0.;

	//	num = solve_cubic(cubic_coeffs, s.xyz);

	//	s[num] = 0.;
	//	num++;
    //}
    {
		/* solve the resolvent cubic ... */

		vec3 cubic_coeffs;

		cubic_coeffs[0] = 1.0/2. * r * p - 1.0/8. * q * q;
		cubic_coeffs[1] = - r;
		cubic_coeffs[2] = - 1.0/2. * p;

		solve_cubic(cubic_coeffs, s.xyz);

		/* ... and take the one real solution ... */

		float z = s[0];

		/* ... to build two quadric equations */

		float u = z * z - r;
		float v = 2. * z - p;

		if(u > -eps){
			u = sqrt(abs(u));
		}
		else{
			return 0;
		}

		if(v > -eps){
			v = sqrt(abs(v));
		}
		else{
			return 0;
		}

		vec2 quad_coeffs;

		quad_coeffs[0] = z - u;
		quad_coeffs[1] = q < 0. ? -v : v;

		num = solve_quadric(quad_coeffs, s.xy);

		quad_coeffs[0]= z + u;
		quad_coeffs[1] = q < 0. ? v : -v;

		vec2 tmp=vec2(1e38);
		int old_num=num;

		num += solve_quadric(quad_coeffs, tmp);

		//s[old_num] = tmp[0];
		//s[old_num+1] = tmp[1];

		if(old_num == 0){
            s[0] = tmp[0];
            s[1] = tmp[1];
        }
        else{//old_num == 2
            s[2] = tmp[0];
            s[3] = tmp[1];
        }

    }

    /* resubstitute */

    float sub = 1./4. * a;

	/* single halley iteration to fix cancellation */
	for(int i=0;i<4;i+=2){
		if(i < num){
			s[i] -= sub;
			s[i] = halley_iteration4(coeffs,s[i]);

			s[i+1] -= sub;
			s[i+1] = halley_iteration4(coeffs,s[i+1]);
		}
	}

    return num;
}

float segment_dis( vec2 p, vec2 a, vec2 b ){
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

vec2 incenter(vec2 p0, vec2 p1, vec2 p2){
	vec2 v1=normalize(p1-p0);
	vec2 v2=normalize(p2-p0);

	vec2 v3=normalize(p2-p1);
	vec2 v4=normalize(p0-p1);

	vec3 line1, line2;
	line1.xy=(v1+v2)/2.;
	line1.xy=vec2(-line1.y,line1.x);
	line1.z=-dot(line1.xy,p0);

	line2.xy=(v3+v4)/2.;
	line2.xy=vec2(-line2.y,line2.x);
	line2.z=-dot(line2.xy,p1);

	vec3 l12=cross(line1,line2);

	return l12.xy/l12.z;
}

vec2 circumcenter(vec2 p0, vec2 p1, vec2 p2){
	vec3 line1, line2;
	line1.xy=p1-p0;
	line1.z=-dot(line1.xy,(p0+p1)/2.);

	line2.xy=p2-p1;
	line2.z=-dot(line2.xy,(p1+p2)/2.);

	vec3 m1=cross(line1,line2);
	m1/=m1.z;

	return m1.xy;
}

vec2 compute_best_node(vec2 p0, vec2 p1, vec2 p2, vec2 p3){
	vec3 line1, line2, line3, line4;

	line1.xy=p1-p0;
	line1.xy=vec2(line1.y,-line1.x);
	line1.z=-dot(line1.xy,p0);

	line2.xy=p3-p2;
	line2.xy=vec2(line2.y,-line2.x);
	line2.z=-dot(line2.xy,p2);

	vec3 l12=cross(line1,line2);

	vec2 m0=l12.xy/l12.z;
	vec2 m1=incenter(p0,m0,p3);
	vec2 m2=circumcenter(p0,m1,p3);

	line3.xy=m2-p0;
	line3.z=-dot(line3.xy,p0);

	line4.xy=p3-p0;
	line4.z=-dot(line4.xy,(p0+p3)/2.);

	vec3 l34=cross(line3,line4);
	vec2 m3=l34.xy/l34.z;

	vec2 a3=(-p0+3.* p1-3.*p2+p3);
	vec2 a2=(3.*p0-6.*p1+3.*p2);
	vec2 a1=(-3.*p0+3.*p1);

	vec2 a0_1=p0-m2;
	vec2 a0_2=p0-m3;

	float b5=dot(a3,a3);
	float b4=2.*dot(a3,a2);
	float b3=dot(a2,a2) + 2.*dot(a1,a3);

	float b2_1=2.*(dot(a1,a2) + dot(a0_1,a3));
	float b2_2=2.*(dot(a1,a2) + dot(a0_2,a3));

	float b1_1=dot(a1,a1) + 2.*dot(a0_1,a2);
	float b1_2=dot(a1,a1) + 2.*dot(a0_2,a2);

	float c4=b5;
	float c3=b4+c4;
	float c2=b3+c3;

	float c1_1=b2_1+c2;
	float c1_2=b2_2+c2;

	float c0_1=b1_1+c1_1;
	float c0_2=b1_2+c1_2;

	vec4 roots_1 = vec4(1e38);
	int num_roots_1 = solve_quartic(vec4(c0_1,c1_1,c2,c3)/c4,roots_1);

	for(int i=0;i<num_roots_1;i++){
		if(roots_1[i] > 0. && roots_1[i] < 1.){
			return parametric_cubic_bezier(roots_1[i],p0,p1,p2,p3);
		}
	}

	vec4 roots_2 = vec4(1e38);
	int num_roots_2 = solve_quartic(vec4(c0_2,c1_2,c2,c3)/c4,roots_2);

	for(int i=0;i<num_roots_2;i++){
		if(roots_2[i] > 0. && roots_2[i] < 1.){
			return parametric_cubic_bezier(roots_2[i],p0,p1,p2,p3);
		}
	}

	return m1;
}

vec2 compute_arc_midpoint(vec2 p0, vec2 p1, vec2 p2){
	vec3 line1, line2;

	line1.xy=p1-p0;
	line1.z=-dot(line1.xy,p0);

	line2.xy=p2-p0;
	line2.z=-dot(line2.xy,(p0+p2)/2.);

	vec3 l12=cross(line1,line2);

	return l12.xy/l12.z;
}

float arc_dis(vec2 uv, vec2 p0, vec2 p1, vec2 m0, float r0){
	vec2 m1=normalize((p0+p1)/2.-m0);

	if(dot(m1,normalize(uv-m0))>dot(m1,normalize(p0-m0))){
		return abs(distance(uv,m0)-r0);
	}
	else{
		return min(distance(uv,p0),distance(uv,p1));
	}
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float border=2./iResolution.x;

	vec2 uv=fragCoord.xy/iResolution.xy;
	uv-=.5;
    uv.y*=iResolution.y/iResolution.x;

	vec2 mouse=iMouse.xy/iResolution.xy;
	mouse-=.5;
    mouse.y*=iResolution.y/iResolution.x;

	border*=zoom;
	uv*=zoom;
	mouse*=zoom;

	vec2 p0=vec2(-.3,-.1);
	vec2 p1=mouse;
	vec2 p2=vec2(.1,-.2);
	vec2 p3=vec2(.2,.15);

    // Animate if mouse is in left bottom corner
    if(max(iMouse.x, iMouse.y) < 20.){
        p1=vec2(-.2,.0)+vec2(.1*sin(iTime),.2*cos(iTime));
    }

	vec2 node=compute_best_node(p0,p1,p2,p3);

	vec2 m1=compute_arc_midpoint(p0,p1,node);
	float r1=distance(m1,p0);

	vec2 m2=compute_arc_midpoint(p3,p2,node);
	float r2=distance(m2,p3);

	vec3 color=bg_col;
	float dis=1e38;

	#ifdef DISTANCE_FIELD
	float sgn=1.;

	dis=arc_dis(uv,p0,node,m1,r1);
	dis=min(dis,arc_dis(uv,p3,node,m2,r2));

	//iq's sd color scheme
	color = vec3(1.0) - sgn*vec3(0.1,0.4,0.7);
	color *= 1.0 - exp(-8.0 * abs(dis));
	color *= 0.8 + 0.2*cos(480.0*abs(dis));
	color = mix(color, vec3(1.0), 1.0-smoothstep(0.0,0.005,abs(dis)));
	#endif

	dis=segment_dis(uv,p0,p1)-line_width;
	dis=min(dis,segment_dis(uv,p1,p2)-line_width);
	dis=min(dis,segment_dis(uv,p2,p3)-line_width);
	color=mix(line_col,color,smoothstep(-border/2.,border/2.,dis));

	dis=cubic_bezier_dis_approx(uv,p0,p1,p2,p3)-line_width;
	color=mix(bezier_col,color,smoothstep(-border/2.,border/2.,dis));

	#ifndef DISTANCE_FIELD
	dis=arc_dis(uv,p0,node,m1,r1)-line_width;
	dis=min(dis,arc_dis(uv,p3,node,m2,r2)-line_width);
	color=mix(arc_col,color,smoothstep(-border/2.,border/2.,dis));
	#endif

	dis=distance(p0,uv)-dot_size;
	dis=min(dis,distance(uv,p1)-dot_size);
	dis=min(dis,distance(uv,p2)-dot_size);
	dis=min(dis,distance(uv,p3)-dot_size);
	color=mix(dot_col,color,smoothstep(-border/2.,border/2.,dis));

	fragColor=vec4(color,1);
}

float smin( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*k*(1.0/4.0);
}

vec3 sdgSMin( in vec3 a, in vec3 b, in float k )
{
    float h = max(k-abs(a.x-b.x),0.0);
    float m = 0.25*h*h/k; // [0 - k/4] for [|a-b|=k - |a-b|=0]
    float n = 0.50*  h/k; // [0 - 1/2] for [|a-b|=k - |a-b|=0]
    return vec3( min(a.x,  b.x) - m, 
                 mix(a.yz, b.yz, (a.x<b.x)?n:1.0-n) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ouv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    float d = 1000.0;

    vec3 dg = vec3(0.0);

    for (int i=0; i<15; i++) {
        float v = float(i);
        vec2 p = 0.2*vec2(sin(45.2*v+1.1*iTime), cos(61.1*v+iTime));
            float ds = distance(p, uv) - 0.1;
        vec3 sg = vec3(ds, normalize(p-uv));
        d = smin(d, ds, 0.3);
        dg = sdgSMin(sg, dg, 0.3);
    }

    // Time varying pixel color
    vec3 col = vec3(0.5*sin(74.0*length(dg))+0.5);
    //vec3(smoothstep(0.04, 0.0, d), dg);
    //col = vec3(dg.yz, 0.0);
    float mag = 1.0;//smoothstep(-0.1,0.1, d);
    vec4 tex = texture(iChannel0, ouv+mag*0.35*dg.yz);
    // Output to screen
    fragColor = tex;
    //fragColor = vec4(col,1.0);
}

#define pi 3.14159
#define thc(a,b) tanh(a*cos(b))/tanh(a)

vec3 cell(in ivec2 p) {
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;
    return texelFetch(iChannel0, p, 0 ).rgb;
}

vec3 cell2(in ivec2 p) {
    ivec2 r = ivec2(textureSize(iChannel1, 0));
    p = (p+r) % r;
    return texelFetch(iChannel1, p, 0 ).rgb;
}

float h21 (float a, float b, float r) {
    a = mod(a, r); b = mod(b, r);
    return fract(sin(dot(vec2(a, b), vec2(12.9898, 78.233)))*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ouv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragCoord += 10. * iTime;
    vec2 uv = fragCoord/iResolution.xy;

    float sc = 8.;
    vec2 ipos = floor(sc * uv);
    vec2 fpos = fract(sc * uv);

    float h = h21(ipos.x, ipos.y, 301.);
    vec3 col = cell(ivec2(fpos * 64. + vec2(13, 17) * floor(iTime + 64. * h) * 64.));
   // float v = length(col.gb);   
    vec2 uv2 = col.bg - 0.5;
    float d = length(uv2);
    float k = sc * 2. / iResolution.y;
    float s = step(0.9 + 0.1 * cos(2. * pi * h + iTime), abs(col.r-col.b));
    s += smoothstep(-k, k, -d + 0.45);
    col *= vec3(s);
    col += 0.01;
    fragCoord *= 2. * col.r * 3.;
    vec3 col3 = 4. * cell2(ivec2(fragCoord));
    // forgot uv=col.bg-0.5 when I wrote this (but looks cool)
    col += exp(-2. * length(uv2 - 0.5));
    col /= col3;

    col = clamp(col, 0., 1.);
    float mx = (0.75 + 0.25 * thc(2., iResolution.y * 0.5 * ouv.y+ 0.5 * iTime)) / cosh(2. * length(ouv));
    col = mix(col, 0.7 * col3 * col.r , 1.-mx);
    fragColor = vec4(col,1.0);
}

#define tiles 10.0

float hash21(in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

float sdfTile(in vec2 p, float r, float t, bool rot)
{
    p.x = rot? 1.0 - p.x : p.x;
    return abs(min(length(p), length(p - vec2(1.0))) - r) - t;
}

vec2 stepPoint(vec2 p, float c)
{
    return floor(p * c) / c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    uv += 0.5;
    uv.x += iTime*0.3;
    uv.y += sin(iTime)*0.2;
    float px = tiles/iResolution.y;

    vec2 tileUv = fract(uv * tiles);
    float tile = hash21(stepPoint(uv, tiles));


    float col = smoothstep(0.0, px, sdfTile(tileUv, 0.5, 0.05, tile < (iMouse.z > 0.0? iMouse.y/iResolution.y : 0.5)));

    fragColor = vec4(vec3(col),1.0);
}

/*
A circle can be defined by three points.
Alternatively, one can use two points and one tangent.
(This is the limit case when one point tends to another point).

You often want to have a curve (spline) connecting
two points with a certain tangent at both points.

For this, you can use a biarc, where two arcs
connect two points with a predetermined tangent
at both points. You have some freedom to choose
the point at which you connect both arcs.
(I'm calling that the node)

You want the arcs to connect smoothly,
which restricts the possible nodes to
the points lying on two circles
(Shown in green)

The incenter of the black triangle is always
on one of the two green circles.
(This is used to construct it)

The second green circle also goes through the endpoints,
and intersects the first circle perpendicularly in the endpoints.

One heuristic is to just use the incenter as node.

Another is to compute the intersection point of the quadratic bezier
curve and the green circle going through the incenter.

This follows the quadratic bezier curve more closely,
but is a bit more difficult to compute.
*/

//#define INCENTER /* Use the incenter heuristic instead */
//#define MOVE_NODE /* Move the node with the mouse instead */

const vec3 dot_col=vec3(0);
const vec3 line_col=vec3(0);
const vec3 bezier_col=vec3(0,0,1);
const vec3 arc_col=vec3(1,0,0);
const vec3 bg_col=vec3(1);

const float dot_size=.004;
const float line_width=.001;

const float zoom=1.;

const float pi=3.1415925;

vec2 parametric_quadratic_bezier(float t, vec2 p0, vec2 p1, vec2 p2){
	return mix(mix(p0,p1,t),mix(p1,p2,t),t);
}

float quadratic_bezier_normal_iteration(float t, vec2 a0, vec2 a1, vec2 a2){
	vec2 a_1=a1+t*a2;

	vec2 uv_to_p=a0+t*a_1;
	vec2 tang=a_1+t*a2;

	float l_tang=dot(tang,tang);
	return t-dot(tang,uv_to_p)/l_tang;
}

float quadratic_bezier_dis_approx_sq(vec2 uv, vec2 p0, vec2 p1, vec2 p2){
	vec2 a2 = p0 - 2. * p1 + p2;
	vec2 a1 = -2. * p0 + 2. * p1;
	vec2 a0 = p0 - uv;

	float d0 = 1e38;

	vec3 t0=vec3(0.,.5,1.);
	float t;

	for(int i=0;i<3;i++){
		t=t0[i];
		for(int j=0;j<3;j++){
			t=quadratic_bezier_normal_iteration(t,a0,a1,a2);
		}
		t=clamp(t,0.,1.);
		vec2 uv_to_p=(a2*t+a1)*t+a0;
		d0=min(d0,dot(uv_to_p,uv_to_p));
	}

	return d0;
}

float quadratic_bezier_dis_approx(vec2 uv, vec2 p0, vec2 p1, vec2 p2){
	return sqrt(quadratic_bezier_dis_approx_sq(uv,p0,p1,p2));
}

float segment_dis(vec2 p, vec2 a, vec2 b){
	vec2 pa=p-a, ba=b-a;
	float h=clamp(dot(pa,ba)/dot(ba,ba), 0., 1.);
	return length(pa-ba*h);
}

// Compute the incenter of the triangle given by p0, p1, p2
vec2 incenter(vec2 p0, vec2 p1, vec2 p2){
	vec2 v1=normalize(p1-p0);
	vec2 v2=normalize(p2-p0);

	vec2 v3=normalize(p2-p1);
	vec2 v4=normalize(p0-p1);

	vec3 line1, line2;
	line1.xy=(v1+v2)/2.;
	line1.xy=vec2(-line1.y,line1.x);
	line1.z=-dot(line1.xy,p0);

	line2.xy=(v3+v4)/2.;
	line2.xy=vec2(-line2.y,line2.x);
	line2.z=-dot(line2.xy,p1);

	vec3 l12=cross(line1,line2);

	return l12.xy/l12.z;
}

// Compute the circumcenter of the triangle given by p0, p1, p2
vec2 circumcenter(vec2 p0, vec2 p1, vec2 p2){
	vec3 line1, line2;
	line1.xy=p1-p0;
	line1.z=-dot(line1.xy,(p0+p1)/2.);

	line2.xy=p2-p1;
	line2.z=-dot(line2.xy,(p1+p2)/2.);


	vec3 m1=cross(line1,line2);
	m1/=m1.z;

	return m1.xy;
}

// Compute the point at which the two arcs should be connected
// in order to best approximate a quadratic bezier curve.
vec2 compute_best_node(vec2 p0, vec2 p1, vec2 p2){
	vec2 m1=incenter(p0,p1,p2);
	vec2 m2=circumcenter(p0,m1,p2);

	vec2 a2=p0-2.*p1+p2;
	vec2 a1=2.*(p1-p0);
	vec2 a0=p0-m2;

	float b3=dot(a2,a2);
	float b2=2.*dot(a2,a1);
	float b1=2.*dot(a2,a0)+dot(a1,a1);

	float c2=b3;
	float c1=b2+b3;
	float c0=b1+b2+b3;

	float discriminant=c1*c1-4.*c2*c0;

	if(discriminant < 0.){
		return m1;
	}

	float disc_sq=sqrt(discriminant);

	float root1=(-c1-disc_sq)/(2.*c2);
	float root2=(-c1+disc_sq)/(2.*c2);

	if(root1 > 0. && root1 < 1.){
		return parametric_quadratic_bezier(root1,p0,p1,p2);
	}
	else if(root2 > 0. && root2 < 1.){
		return parametric_quadratic_bezier(root2,p0,p1,p2);
	}
	else{
		return m1;
	}
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float border=2./iResolution.x;

	vec2 uv=fragCoord.xy/iResolution.xy;
	uv-=.5;
    uv.y*=iResolution.y/iResolution.x;

	vec2 mouse=iMouse.xy/iResolution.xy;
	mouse-=.5;
    mouse.y*=iResolution.y/iResolution.x;

	border*=zoom;
	uv*=zoom;
	mouse*=zoom;

    // Quadratic bezier control points

	vec2 p0=vec2(-.2,-.2);
    #ifndef MOVE_NODE
	vec2 p1=mouse;
    // Animate if mouse is in left bottom corner
    if(max(iMouse.x, iMouse.y) < 20.){
        p1=vec2(-.26,.1)+vec2(.2*sin(iTime),.08*cos(iTime));
    }
    #else
    vec2 p1=vec2(-.26,.1);
    #endif
	vec2 p2=vec2(.2,-.2);

    // Node (The point where the two arcs connect)

    #ifdef MOVE_NODE
	vec2 p3=mouse;
    #else
    #ifdef INCENTER
    vec2 p3=incenter(p0,p1,p2);
    #else
	vec2 p3=compute_best_node(p0,p1,p2);
    #endif
    #endif

	vec3 color=bg_col;

	// Show segments
	float dis=segment_dis(uv,p0,p1)-line_width;
	dis=min(dis,segment_dis(uv,p1,p2)-line_width);
	color=mix(line_col,color,smoothstep(-border/2.,border/2.,dis));

	// Show quadratic bezier curve
	dis=quadratic_bezier_dis_approx(uv,p0,p1,p2)-line_width;
	color=mix(bezier_col,color,smoothstep(-border/2.,border/2.,dis));

	vec3 line1, line2, line3, line4, line5, line6;

	// Line at p0 perpendicular to the segment between p0 and p1
	line1.xy=p1-p0;
	line1.z=-dot(line1.xy,p0);

	// Perpendicular bisector of segment between p0 and p3
	line2.xy=p3-p0;
	line2.z=-dot(line2.xy,(p0+p3)/2.);

	vec3 l12=cross(line1,line2);

	// Intersection of line1 and line2, the midpoint of the first arc
	vec2 m1=l12.xy/l12.z;
	float r1=distance(m1,p0);

	// Line at p0 perpendicular to the segment between p2 and p1
	line3.xy=p2-p1;
	line3.z=-dot(line3.xy,p2);

	// Perpendicular bisector of segment between p2 and p3
	line4.xy=p3-p2;
	line4.z=-dot(line4.xy,(p2+p3)/2.);

	vec3 l34=cross(line3,line4);

	// Intersection of line2 and line3, the midpoint of the second arc
	vec2 m2=l34.xy/l34.z;
	float r2=distance(m2,p2);

	vec2 m3=incenter(p0,p1,p2);

	// The midpoint of the first green circle
	vec2 m4=circumcenter(p0,m3,p2);
	float r4=distance(p0,m4);

	// Line at p0 perpendicular to the first green circle
	line5.xy=m4-p0;
	line5.z=-dot(line5.xy,p0);

	// Perpendicular bisector of segment between p0 and p2
	line6.xy=p2-p0;
	line6.z=-dot(line6.xy,(p0+p2)/2.);

	vec3 l56=cross(line5,line6);

	// The midpoint of the second green circle
	vec2 m5=l56.xy/l56.z;
	float r5=distance(p0,m5);

	// Show dots
	dis=distance(m1,uv)-dot_size;
	dis=min(dis,distance(m2,uv)-dot_size);
	dis=min(dis,distance(m3,uv)-dot_size);
	dis=min(dis,distance(m4,uv)-dot_size);
	dis=min(dis,distance(m5,uv)-dot_size);
	color=mix(dot_col,color,smoothstep(-border/2.,border/2.,dis));

	// Show red circles (arcs)
	dis=abs(distance(m1,uv)-r1)-line_width;
	dis=min(dis,abs(distance(m2,uv)-r2)-line_width);
	color=mix(arc_col,color,smoothstep(-border/2.,border/2.,dis));

	// Show green circles
	dis=abs(distance(m4,uv)-r4)-line_width;
	dis=min(dis,abs(distance(m5,uv)-r5)-line_width);
	color=mix(vec3(0,1,0),color,smoothstep(-border/2.,border/2.,dis));

	dis=distance(uv,p0)-dot_size;
	dis=min(dis,distance(uv,p1)-dot_size);
	dis=min(dis,distance(uv,p2)-dot_size);
	dis=min(dis,distance(uv,p3)-dot_size);
	color=mix(dot_col,color,smoothstep(-border/2.,border/2.,dis));


	fragColor=vec4(color,1);
}

/*
See https://www.shadertoy.com/view/sl3BWs for some explanation.
*/

//#define INCENTER /* Use the incenter heuristic instead */
//#define DISTANCE_FIELD /* Show distance field */

const vec3 dot_col=vec3(0,0,1);
const vec3 line_col=vec3(0,0,1);
const vec3 bezier_col=vec3(0,1,0);
const vec3 arc_col=vec3(1,0,0);
const vec3 bg_col=vec3(1);

const float dot_size=.004;
const float line_width=.001;

const float zoom=1.;

const float pi=3.1415925;

vec2 parametric_quadratic_bezier(float t, vec2 p0, vec2 p1, vec2 p2){
	return mix(mix(p0,p1,t),mix(p1,p2,t),t);
}

float quadratic_bezier_normal_iteration(float t, vec2 a0, vec2 a1, vec2 a2){
	vec2 a_1=a1+t*a2;

	vec2 uv_to_p=a0+t*a_1;
	vec2 tang=a_1+t*a2;

	float l_tang=dot(tang,tang);
	return t-dot(tang,uv_to_p)/l_tang;
}

float quadratic_bezier_dis_approx_sq(vec2 uv, vec2 p0, vec2 p1, vec2 p2){
	vec2 a2=p0-2.*p1+p2;
	vec2 a1=-2.*p0 +2.*p1;
	vec2 a0=p0-uv;

	float d0=1e38;

	vec3 t0=vec3(0.,.5,1.);
	float t;

	for(int i=0;i<3;i++){
		t=t0[i];
		for(int j=0;j<3;j++){
			t=quadratic_bezier_normal_iteration(t,a0,a1,a2);
		}
		t=clamp(t,0.,1.);
		vec2 uv_to_p=(a2*t+a1)*t+a0;
		d0=min(d0,dot(uv_to_p,uv_to_p));
	}

	return d0;
}

float quadratic_bezier_dis_approx(vec2 uv, vec2 p0, vec2 p1, vec2 p2){
	return sqrt(quadratic_bezier_dis_approx_sq(uv,p0,p1,p2));
}

float segment_dis(vec2 p, vec2 a, vec2 b){
	vec2 pa=p-a, ba=b-a;
	float h=clamp(dot(pa,ba)/dot(ba,ba), 0., 1.);
	return length(pa-ba*h);
}

vec2 incenter(vec2 p0, vec2 p1, vec2 p2){
	vec2 v1=normalize(p1-p0);
	vec2 v2=normalize(p2-p0);

	vec2 v3=normalize(p2-p1);
	vec2 v4=normalize(p0-p1);

	vec3 line1, line2;
	line1.xy=(v1+v2)/2.;
	line1.xy=vec2(-line1.y,line1.x);
	line1.z=-dot(line1.xy,p0);

	line2.xy=(v3+v4)/2.;
	line2.xy=vec2(-line2.y,line2.x);
	line2.z=-dot(line2.xy,p1);

	vec3 l12=cross(line1,line2);

	return l12.xy/l12.z;
}

vec2 circumcenter(vec2 p0, vec2 p1, vec2 p2){
	vec3 line1, line2;
	line1.xy=p1-p0;
	line1.z=-dot(line1.xy,(p0+p1)/2.);

	line2.xy=p2-p1;
	line2.z=-dot(line2.xy,(p1+p2)/2.);

	vec3 m1=cross(line1,line2);
	m1/=m1.z;

	return m1.xy;
}

vec2 compute_best_node(vec2 p0, vec2 p1, vec2 p2){
	vec2 m1=incenter(p0,p1,p2);
	vec2 m2=circumcenter(p0,m1,p2);

	vec2 a2=p0-2.*p1+p2;
	vec2 a1=2.*(p1-p0);
	vec2 a0=p0-m2;

	float b3=dot(a2,a2);
	float b2=2.*dot(a2,a1);
	float b1=2.*dot(a2,a0)+dot(a1,a1);

	float c2=b3;
	float c1=b2+b3;
	float c0=b1+b2+b3;

	float discriminant=c1*c1-4.*c2*c0;

	if(discriminant < 0.){
		return m1;
	}

	float disc_sq=sqrt(discriminant);

	float root1=(-c1-disc_sq)/(2.*c2);
	float root2=(-c1+disc_sq)/(2.*c2);

	if(root1 > 0. && root1 < 1.){
		return parametric_quadratic_bezier(root1,p0,p1,p2);
	}
	else if(root2 > 0. && root2 < 1.){
		return parametric_quadratic_bezier(root2,p0,p1,p2);
	}
	else{
		return m1;
	}
}

vec2 compute_arc_midpoint(vec2 p0, vec2 p1, vec2 p2){
	vec3 line1, line2;

	line1.xy=p1-p0;
	line1.z=-dot(line1.xy,p0);

	line2.xy=p2-p0;
	line2.z=-dot(line2.xy,(p0+p2)/2.);

	vec3 l12=cross(line1,line2);

	return l12.xy/l12.z;
}

float arc_dis(vec2 uv, vec2 p0, vec2 p1, vec2 m0, float r0){
	vec2 m1=normalize((p0+p1)/2.-m0);

	if(dot(m1,normalize(uv-m0))>dot(m1,normalize(p0-m0))){
		return abs(distance(uv,m0)-r0);
	}
	else{
		return min(distance(uv,p0),distance(uv,p1));
	}
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float border=2./iResolution.x;

	vec2 uv=fragCoord.xy/iResolution.xy;
	uv-=.5;
    uv.y*=iResolution.y/iResolution.x;

	vec2 mouse=iMouse.xy/iResolution.xy;
	mouse-=.5;
    mouse.y*=iResolution.y/iResolution.x;

	border*=zoom;
	uv*=zoom;
	mouse*=zoom;

	vec2 p0=vec2(-.2,-.2);
	vec2 p1=mouse;
	vec2 p2=vec2(.2,-.2);

    // Animate if mouse is in left bottom corner
    if(max(iMouse.x, iMouse.y) < 20.){
        p1=vec2(-.26,.1)+vec2(.2*sin(iTime),.08*cos(iTime));
    }

	#ifdef INCENTER
	vec2 p3=incenter(p0,p1,p2);
	#else
	vec2 p3=compute_best_node(p0,p1,p2);
	#endif

	vec2 m1=compute_arc_midpoint(p0,p1,p3);
	float r1=distance(m1,p0);

	vec2 m2=compute_arc_midpoint(p2,p1,p3);
	float r2=distance(m2,p2);

	vec3 color=bg_col;

	float dis=1e38;

	#ifdef DISTANCE_FIELD
	float sgn=1.;

	dis=min(dis,arc_dis(uv,p0,p3,m1,r1));
	dis=min(dis,arc_dis(uv,p2,p3,m2,r2));

	//iq's sd color scheme
	color = vec3(1.0) - sgn*vec3(0.1,0.4,0.7);
	color *= 1.0 - exp(-8.0 * abs(dis));
	color *= 0.8 + 0.2*cos(480.0*abs(dis));
	color = mix(color, vec3(1.0), 1.0-smoothstep(0.0,0.005,abs(dis)));
	#endif

	dis=segment_dis(uv,p0,p1)-line_width;
	dis=min(dis,segment_dis(uv,p1,p2)-line_width);
	color=mix(line_col,color,smoothstep(-border/2.,border/2.,dis));

	dis=quadratic_bezier_dis_approx(uv,p0,p1,p2)-line_width;
	color=mix(bezier_col,color,smoothstep(-border/2.,border/2.,dis));

	#ifndef DISTANCE_FIELD
	dis=arc_dis(uv,p0,p3,m1,r1)-line_width;
	dis=min(dis,arc_dis(uv,p2,p3,m2,r2)-line_width);
	color=mix(arc_col,color,smoothstep(-border/2.,border/2.,dis));
	#endif

    dis=distance(uv,p0)-dot_size;
	dis=min(dis,distance(uv,p1)-dot_size);
	dis=min(dis,distance(uv,p2)-dot_size);
	color=mix(dot_col,color,smoothstep(-border/2.,border/2.,dis));

	fragColor=vec4(color,1);
}

/* Sed - P-Norm infinity - 2022 */

/* GLSL adaptation of http://sedcore.eu.org/small/norm_infinity.c */
/* comments were kept as is - useless c code removed */
/* released in the public domain */
/* not super efficient I guess */

/* 16/9 aspect ratio expected - me too lazy to handle generic case */

/* set this for full resolution (personally I like big pixels)
 * note that the computation time is the same in any case
 */
//#define FULL_RESOLUTION

/* 2022-09-07 */

/* inspired by https://twitter.com/Marina_Costant/status/1560219380482539522 */

/* gcc -Wall -o norm_infinity norm_infinity.c -lm -O3 -ffast-math -fomit-frame-pointer -march=native -fopenmp
 * ./norm_infinity > XX
 * ffmpeg -f rawvideo -s 640x360 -r 25 -pix_fmt bgra -i XX -vf palettegen palette.png
 * ffmpeg -f rawvideo -s 640x360 -r 25 -pix_fmt bgra -i XX -i palette.png -filter_complex paletteuse movie.gif
 *
 * or:
 * ffmpeg -f rawvideo -s 640x360 -r 25 -pix_fmt bgra -i XX -plays 0 movie.apng
 */

/* not realtime at all, set delta = 0.05 or higher for faster but uglier */
/* or improve dig_t(): we look for the smallest t>1 for which pnorm() = 1,
 * if it exists of course
 * also classic lookup tables, caching, and so on, can be done
 */

/* linear interpolation, x is in [x0..x1] we want y in [y0..y1] such that
 * when x is x0 then y is y0 and when x is x1 then y is y1
 */
/* x0   y0
 * x    y
 * x1   y1
 * (x-x0) / (x1-x0) = (y-y0) / (y1-y0)
 * y = y0 + (x-x0) / (x1-x0) * (y1-y0)
 */
#define P3(x, x0, x1, y0, y1) ((y0) + ((x)-(x0)) / ((x1)-(x0)) * ((y1)-(y0)))

#define rotate_x(x, y, z, angle)    {          \
  float y2 = y * cos(angle) - z * sin(angle); \
  float z2 = z * cos(angle) + y * sin(angle); \
  y = y2;                                      \
  z = z2;                                      \
}

#define rotate_y(x, y, z, angle)    {          \
  float z2 = z * cos(angle) - x * sin(angle); \
  float x2 = x * cos(angle) + z * sin(angle); \
  x = x2;                                      \
  z = z2;                                      \
}

#define rotate_z(x, y, z, angle)    {          \
  float x2 = x * cos(angle) - y * sin(angle); \
  float y2 = y * cos(angle) + x * sin(angle); \
  x = x2;                                      \
  y = y2;                                      \
}

const float M_PI = radians(180.);

/****************************************************************************/
/* bezier curve begin                                                       */
/****************************************************************************/

/* bezier curves are super nice for trajectories */

#if 0

typedef struct {
  float x;
  float y;
  float z;
} point_t;

typedef struct {
  point_t p0;
  point_t p1;
  point_t p2;
