/* cubic bezier (4 control points) */
vec3 bezier(in vec3 b[4], in float t)
{
  float _1_t = 1. - t;
  float _1_t_2 = _1_t * _1_t;
  float _1_t_3 = _1_t_2 * _1_t;
  float t_2 = t * t;
  float t_3 = t * t_2;
  return vec3(
    _1_t_3 * b[0].x + 3. * _1_t_2 * t * b[1].x + 3. * _1_t * t_2 * b[2].x + t_3 * b[3].x,
    _1_t_3 * b[0].y + 3. * _1_t_2 * t * b[1].y + 3. * _1_t * t_2 * b[2].y + t_3 * b[3].y,
    _1_t_3 * b[0].z + 3. * _1_t_2 * t * b[1].z + 3. * _1_t * t_2 * b[2].z + t_3 * b[3].z
  );
}

/****************************************************************************/
/* bezier curve end                                                         */
/****************************************************************************/

/****************************************************************************/
/* camera management begin                                                  */
/****************************************************************************/

#define eye    camera[0]
#define center camera[1]
#define vx     camera[2]
#define vy     camera[3]

#if 0

/* the 4 component of the camera array are defined as below */
typedef struct {
  point_t eye;           /* position of the eye */
  point_t center;        /* center of screen of camera */
  point_t vx, vy;        /* unit vectors on screen */
} camera_t;

#endif

void compute_camera(out vec3 camera[4], in float frame)
{
  float d;
  float t;
  float angle;

  frame = mod(frame, 400.);

  /* basic idea is to start the camera in front of cube (0,0,0) then move
   * forward with some rotations for nice visual effect and end in front
   * of cube (0,0,-24) so that we can loop the animation (the cube (0,0,-24)
   * has the same neighbors as the cube (0,0,0), so we can loop)
   */

  /* position the eye */
  /* eye movement is snakish, we start in front of cube (0,0,0) and end
   * in front of cube (0,0,-24) turning around center of each cube on the path
   */

  if (frame < 100.) {
    /* turn on the left */
    angle = P3(frame, 0., 100., -M_PI/2., -3.*M_PI/2.);
  } else if (frame < 200.) {
    /* right */
    angle = P3(frame, 100., 200., -M_PI/2., M_PI/2.);
  } else if (frame < 300.) {
    /* left */
    angle = P3(frame, 200., 300., -M_PI/2., -3.*M_PI/2.);
  } else {
    /* right */
    angle = P3(frame, 300., 400., -M_PI/2., M_PI/2.);
  }
  /* we start with a point pointing to the right: (3,0,0) */
  vec3 p0 = vec3(3,0,0);
  rotate_y(p0.x, p0.y, p0.z, angle);
  /* we force z to move forward, the rotation-based stuff is ugly for z */
  p0.z = P3(frame, 0., 100., 3., -3.);
  /* move a bit closer to the shape */
  p0.z += - .5;
  /* tilt trajectory */
  rotate_z(p0.x, p0.y, p0.z, M_PI/6.);

  eye = p0;

  /* position p1 (direction of camera from the eye) */
  /* more or less going forward but some backward for not too much boring */
  vec3 p1b[4];
  p1b[0] = vec3(0, 0, 0);     /* start point */
  p1b[1] = vec3(5, 3, 2);
  p1b[2] = vec3(-5,-3, -46);
  p1b[3] = vec3(0, 0, -24);   /* end point */
  vec3 p1 = bezier(p1b, P3(frame, 0., 400., 0., 1.));

  /* center is on vector (p1-p0) at right distance of the eye */
  d = length(p1-p0); //sqrt((p1.x-p0.x)*(p1.x-p0.x) + (p1.y-p0.y)*(p1.y-p0.y) + (p1.z-p0.z)*(p1.z-p0.z));
  /* right distance is 0.5 in front of eye (quite close, wide camera angle) */
  t = 0.5 / d;
  center = p0 + t * (p1 - p0);

  p1 = center;

  /* position p2 (anchor for angle of camera) (not sure if that means
   * something, but it's clear in my head: 'eye' is where is the eye, 'center',
   * is where the eye looks at, but then the camera can rotate around this
   * center, so we need a third point to define this rotation, this is p2)
   */
  /* rotate a bit, not too much, more or less randomly chosen */
  vec3 p2b[4];
  p2b[0] = vec3(10, 0, 0);      /* start: no rotation, horizontal cam */
  p2b[1] = vec3(-10, -10, 0);
  p2b[2] = vec3(10, 10, -20);
  p2b[3] = vec3(10, 0, -24);    /* end: no rotation, horizontal cam */
  vec3 p2 = bezier(p2b, P3(frame, 0., 400., 0., 1.));

  /* project p2 on plane perpendicular to vector (p1-p0) containing p1 */
  /* vector (p2p-p2) and (p1-p0) are parallel: (p2p-p2) = t * (p1-p0)
   * and scalar product (p2p-p1) x (p1-p0) = 0
   * (p2 + t * (p1-p0) - p1) x (p1-p0) = 0
   * (p2.x + t * (p1.x-p0.x) - p1.x) * (p1.x - p0.x)
   * + [same y]
   * + [same z] = 0
   * ((p2.x - p1.x) + t * (p1.x-p0.x)) * (p1.x - p0.x)
   * + [same y]
   * + [same z] = 0
   * (p2.x - p1.x) * (p1.x - p0.x) + t * (p1.x-p0.x) * (p1.x - p0.x)
   * + [same y]
   * + [same z] = 0
   * t * (p1.x-p0.x) * (p1.x - p0.x)
   * + [same y]
   * + [same z] = -(p2.x - p1.x) * (p1.x - p0.x)
   * t = (-(p2.x - p1.x) * (p1.x - p0.x) -(p2.y - p1.y) * (p1.y - p0.y) -(p2.z - p1.z) * (p1.z - p0.z)) /
   *     ((p1.x-p0.x) * (p1.x - p0.x) + (p1.y-p0.y) * (p1.y - p0.y) + (p1.z-p0.z) * (p1.z - p0.z))
   */
  vec3 p2p;
  t = (-(p2.x - p1.x) * (p1.x - p0.x) -(p2.y - p1.y) * (p1.y - p0.y) -(p2.z - p1.z) * (p1.z - p0.z)) /
      ((p1.x-p0.x) * (p1.x - p0.x) + (p1.y-p0.y) * (p1.y - p0.y) + (p1.z-p0.z) * (p1.z - p0.z));
  p2p = p2 + t * (p1 - p0);

  /* vx is vector (p2p-p1) at right distance */
  d = length(p2p - p1); //sqrt((p2p.x-p1.x)*(p2p.x-p1.x) + (p2p.y-p1.y)*(p2p.y-p1.y) + (p2p.z-p1.z)*(p2p.z-p1.z));
  /* length (right distance) is 1/150 */
  t = 1./150. / d;
  vx = t * (p2p - p1);

  /* vy is vectorial product of vx and vector (center-p0) */
  vy = cross(vx, center - p0);
  /* at the right length (also 1/150, as for vx) */
  d = length(vy); //sqrt(ret.vy.x*ret.vy.x + ret.vy.y*ret.vy.y + ret.vy.z*ret.vy.z);
  t = 1./150. / d;
  vy *= t;
}

/****************************************************************************/
/* camera management end                                                    */
/****************************************************************************/

float pnorm(float p, float t, float x0, float y0, float z0, float x1, float y1, float z1,
             float frame,
             float p2, float p3, float p4)
{
  /* the space is covered by cubes of size 6x6x6, repeating in all directions,
   * each one containing a shape.
   * We want 4 different shapes on screen for less boring, so each cube is
   * numbered in [0, 1, 2, 3] and the numbering repeats like this in all
   * directions, each numbering receives a shape (different shapes have
   * different p for a given frame)
   */

  /* (x,y,z) is the point to test */
  float x = x0 + t * (x1 - x0);
  float y = y0 + t * (y1 - y0);
  float z = z0 + t * (z1 - z0);

  /* find in which cube we are */
  int px = int(round(x / 6.)); px = (px % 4 + 4) % 4;
  int py = int(round(y / 6.)); py = (py % 4 + 4) % 4;
  int pz = int(round(z / 6.)); pz = (pz % 4 + 4) % 4;

  /* put (x,y,z) in the cube (0,0,0) for processing */
  x = mod(mod(x-3.,6.) + 6., 6.)-3.;
  y = mod(mod(y-3.,6.) + 6., 6.)-3.;
  z = mod(mod(z-3.,6.) + 6., 6.)-3.;

  /* rotate the shape */
  float angle = 2. * M_PI * frame / 100.;
  rotate_y(x, y, z, angle);

  /* select p depending on the cube we're in */
  if ((px+py+pz) % 4 == 1) p = p2;
  else if ((px+py+pz) % 4 == 2) p = p3;
  else if ((px+py+pz) % 4 == 3) p = p4;

  /* no need for final pow(1/p), we care about distance = 1 */
  return   pow(abs(x), p)
         + pow(abs(y), p)
         + pow(abs(z), p);
}

float get_p(float frame)
{
  float p;
  frame = mod(frame, 200.);
  /* we get p from [1/2 .. 4], linear choice is ugly, so we use several
   * linear ranges, looks more or less nice, there may be better choices
   */
  /* 1/2   1   2    4 */
  if (frame < 50.)       p = P3(frame, 0., 50., 1./2., 1.);
  else if (frame < 75.)  p = P3(frame, 50., 75., 1., 2.);
  else if (frame < 100.) p = P3(frame, 75., 100., 2., 4.);
  else if (frame < 125.) p = P3(frame, 100., 125., 4., 2.);
  else if (frame < 150.) p = P3(frame, 125., 150., 2., 1.);
  else                   p = P3(frame, 150., 200., 1., 1./2.);
  return p;
}

float dig_t(float p, float x0, float y0, float z0, float x1, float y1, float z1, float frame, float p2, float p3, float p4)
{
  float t0, t1;
  float n0, n1;
  /* delta = .01 for nice result but very very slow, bigger value for faster
   * computation but uglier on screen
   */
  float delta = .01;

  /* norm is abs(x)^p + abs(y)^p + abs(z)^p (we omit final ^1/p)
   * we want norm = 1 (we want to be on the "unit sphere" of the norm)
   * current ray is from (x0, y0, z0) to (x1,y1,z1)
   * a point (x,y,z) is on the ray if there is t such that:
   * (x,y,z) = t * ray
   * for t=0 we decide to be at (x0,y0,z0)
   * for t=1 we decide to be at (x1,y1,z1)
   * so for generic point (x,y,z), we have:
   * 0 x0  y0  z0
   * 1 x1  y1  z1
   * t x   y   z
   * so:
   * t = (x - x0) / (x1 - x0)
   * same for y and z
   * and so:
   * x = x0 + t * (x1 - x0)
   * y = y0 + t * (y1 - y0)
   * z = z0 + t * (z1 - z0)
   */

  /* start from t = 1 to t = 70, step is delta (this is very slow) */
  t0 = 1.;
  n0 = pnorm(p, t0, x0, y0, z0, x1, y1, z1, frame, p2, p3, p4);
  //if (n0 <= 1) { fprintf(stderr, "bad start\n"); exit(1); }
  t1 = t0 + delta;
  n1 = pnorm(p, t1, x0, y0, z0, x1, y1, z1, frame, p2, p3, p4);
  /* we stop if we find a point with n = 1 or if we go too far */
  while ((   (n0 > 1. && n1 > 1.)
          || (n0 < 1. && n1 < 1.))
         && t1 < 70.) {
    n0 = n1;
    t0 = t1;
    t1 += delta;
    n1 = pnorm(p, t1, x0, y0, z0, x1, y1, z1, frame, p2, p3, p4);
  }
  if (t1 >= 70.) return 1000.;
  /* this is not correct, t for which distance = 1 is not necessarily t1
   * but it looks ok on screen, so no big deal at this point
   */
  return t1;
}

vec4 raytrace_pnorm(float x, float y, float frame)
{
  vec3 camera[4];
  compute_camera(camera, frame);
  float x0, y0, z0, x1, y1, z1;
  float p;
  float t;

  /* ray is from eye to (x,y) on screen (center of screen is (0,0)) */
  /* start of ray: eye */
  x0 = eye.x;
  y0 = eye.y;
  z0 = eye.z;

  /* end of ray */
  /* (x,y) of screen is vector (x*vx + y*vx) starting at point center in
   * our 3d space
   */
  x1 = center.x + x * vx.x + y * vy.x;
  y1 = center.y + x * vx.y + y * vy.y;
  z1 = center.z + x * vx.z + y * vy.z;

  /* get the p in [1/2 .. 4], four of them for more diversity on screen */
  p = get_p(frame);
  float p2 = get_p(frame + 100.);
  float p3 = get_p(frame + 50.);
  float p4 = get_p(frame + 150.);

  /* blue is actually black, looks better ('blue' name kept, lazy me) */
  vec4 blue = vec4(0, 0, 0, 0);
  t = dig_t(p, x0, y0, z0, x1, y1, z1, frame, p2, p3, p4);
  if (t == 1000.) return blue;

  /* a palette based on euclidian distance from (0,0,0) to the point */
  /* close is yellow, farther is red, far is white */
  float d = pnorm(2., t, x0, y0, z0, x1, y1, z1, frame, 2., 2., 2.);
  /* g+rb define yellow */
  float g = P3(d, 0., 0.6, 125., 10.);
  float rb = P3(d, 0., 0.6, 125., 10.);
  /* ra defines red */
  float ra = P3(d, 0., 2., 0., 255.);
  g = clamp(g, 0., 255.);
  rb = clamp(rb, 0., 255.);
  /* w defines white */
  float w = clamp(P3(d, 1., 1.4, 0., 120.), 0., 255.);
  /* alpha is used to fade to 'blue' when very far away, looks nice */
  float alpha = clamp(P3(t, 10., 70., 1., 0.), 0., 1.);
  return vec4(
    alpha * max(clamp(ra+rb, 0., 255.), w) + (1.-alpha) * blue.r,
    alpha * max(g, w) + (1.-alpha) * blue.g,
    alpha * max(20., w) + (1.-alpha) * blue.b,
    0
  ) / 255.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float x = P3(fragCoord.x, 0., iResolution.x, -80., 80.);
  float y = P3(fragCoord.y, 0., iResolution.y, -45., 45.);
#ifndef FULL_RESOLUTION
  x = floor(x);
  y = floor(y);
#endif
  float frame = iTime * 25.;
  fragColor = raytrace_pnorm(x, y, frame);
}

/* Sed - P-Norm - 2022 */

/* GLSL adaptation of http://sedcore.eu.org/small/norm.c */
/* comments were kept as is - useless c code removed */
/* released in the public domain */
/* not super efficient I guess */

/* 16/9 aspect ratio expected - me too lazy to handle generic case */

/* set this for full resolution (personally I like big pixels)
