 */
//#define FULL_RESOLUTION

/* 2022-09-07 */

/* inspired by https://twitter.com/Marina_Costant/status/1560219380482539522 */

/* gcc -Wall -o norm norm.c -lm -O3 -ffast-math -fomit-frame-pointer -march=native
 * ./norm > XX
 * ffmpeg -f rawvideo -s 640x360 -r 25 -pix_fmt bgra -i XX -vf palettegen palette.png
 * ffmpeg -f rawvideo -s 640x360 -r 25 -pix_fmt bgra -i XX -i palette.png -filter_complex paletteuse movie.gif
 */

/* not realtime at all, set delta = 0.05 or higher for faster but uglier */
/* or improve dig_t(): we look for the smallest t>1 for which norm() = 1,
 * if it exists of course
 */

/* not super good comments, see norm_infinity.c for better */

const float M_PI = radians(180.);

float norm(float p, float t, float x0, float y0, float z0, float x1, float y1, float z1)
{
  float x = x0 + t * (x1 - x0);
  float y = y0 + t * (y1 - y0);
  float z = z0 + t * (z1 - z0);
  return   pow(abs(x), p)
         + pow(abs(y), p)
         + pow(abs(z), p);
}

float euclid(float t, float x0, float y0, float z0, float x1, float y1, float z1)
{
  float x = x0 + t * (x1 - x0);
  float y = y0 + t * (y1 - y0);
  float z = z0 + t * (z1 - z0);
  return sqrt(x*x + y*y + z*z);
}

float dig_t(float p, float x0, float y0, float z0, float x1, float y1, float z1)
{
  float t0, t1;
  float n0, n1;
  float delta = .01;

  /* norm is abs(x)^p + abs(y)^p + abs(z)^p
   * we want norm = 1 (we want to be on the "unit sphere" of the norm)
   * current ray is from (x0, y0, z0) to (x1,y1,z1)
   * a point (x,y,z) is on the ray if there is t such that:
   * (x,y,z) = t * ray
   * for t=0 we decide to be at (x0,y0,z0)
   * for t=1 we decide to be at (x1,y1,z1)
   * so for generic point (x,y,z), we have:
   * 0 x0  y0  z0
   * 1 x1  y1  z1
   * t x   y   z
   * so:
   * t = (x - x0) / (x1 - x0)
   * same for y and z
   * and so:
   * x = x0 + t * (x1 - x0)
   * y = y0 + t * (y1 - y0)
   * z = z0 + t * (z1 - z0)
   */

  /* start at t = 1 and end at t = 1 + delta */
  t0 = 1.;
  n0 = norm(p, t0, x0, y0, z0, x1, y1, z1);
  //if (n0 <= 1) { fprintf(stderr, "bad start\n"); exit(1); }
  t1 = t0 + delta;
  n1 = norm(p, t1, x0, y0, z0, x1, y1, z1);
  while ((   (n0 > 1. && n1 > 1.)
          || (n0 < 1. && n1 < 1.))
         && t1 < 5.) {
    n0 = n1;
    t0 = t1;
    t1 += delta;
    n1 = norm(p, t1, x0, y0, z0, x1, y1, z1);
  }
  if (t1 >= 5.) return 1000.;
  return t1;
}

#define rotate_x(x, y, z, angle)    {          \
  float y2 = y * cos(angle) - z * sin(angle); \
  float z2 = z * cos(angle) + y * sin(angle); \
  y = y2;                                      \
  z = z2;                                      \
}

#define rotate_y(x, y, z, angle)    {          \
  float x2 = x * cos(angle) - z * sin(angle); \
  float z2 = z * cos(angle) + x * sin(angle); \
  x = x2;                                      \
  z = z2;                                      \
}

vec4 raytrace_norm(float x, float y, float frame)
{
  float x0 = 0., y0 = 0., z0 = 3., x1 = x, y1 = y, z1 = 2.;
  float p;
  float t;

  /* rotate (x0,y0,z0) and (x1,y1,z1) */
  float angle = 2. * M_PI * frame / 100.;

  rotate_x(x0, y0, z0, -M_PI/7. * cos(2. * M_PI * frame / 80.));
  rotate_x(x1, y1, z1, -M_PI/7. * cos(2. * M_PI * frame / 80.));

  rotate_y(x0, y0, z0, angle);
  rotate_y(x1, y1, z1, angle);

  /* x0   y0
   * x    y
   * x1   y1
   * (x-x0) / (x1-x0) = (y-y0) / (y1-y0)
   * y = y0 + (x-x0) / (x1-x0) * (y1-y0)
   */
#define P3(x, x0, x1, y0, y1) ((y0) + ((x)-(x0)) / ((x1)-(x0)) * ((y1)-(y0)))
  /* p in [1/2 .. 4] */
  frame = mod(frame, 200.);
  /* 1/2   1   2    4 */
  if (frame < 50.)       p = P3(frame, 0., 50., 1./2., 1.);
  else if (frame < 75.)  p = P3(frame, 50., 75., 1., 2.);
  else if (frame < 100.) p = P3(frame, 75., 100., 2., 4.);
  else if (frame < 125.) p = P3(frame, 100., 125., 4., 2.);
  else if (frame < 150.) p = P3(frame, 125., 150., 2., 1.);
  else                   p = P3(frame, 150., 200., 1., 1./2.);

  vec4 blue = vec4(15, 17, 23, 0)/255.;
  t = dig_t(p, x0, y0, z0, x1, y1, z1);
  if (t == 1000.) return blue;

  /* a palette based on distance from (0,0,0) to the point */
  float d = euclid(t, x0, y0, z0, x1, y1, z1);
  float g = P3(d, 0., 0.6, 125., 10.);
  float rb = P3(d, 0., 0.6, 125., 10.);
  float ra = P3(d, 0., 2., 0., 255.);
  g = clamp(g, 0., 255.);
  rb = clamp(rb, 0., 255.);
  float w = clamp(P3(d, 1., 1.4, 0., 120.), 0., 255.);
  return vec4(
    max(clamp(ra+rb, 0., 255.), w),
    max(g, w),
    max(20., w),
    0) / 255.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#ifdef FULL_RESOLUTION
  float x = fragCoord.x;
  float y = fragCoord.y;
  x -= iResolution.x / 2.;
  y = iResolution.y / 2. - y;
  x /= (iResolution.x / 160.);
  y /= (iResolution.y / 90.);
  float frame = iTime * 25.;
  float scale = 1./100.;
  float x0 = x * scale;
  float y0 = y * scale;
  fragColor = raytrace_norm(x0, y0, frame);
#else
  float x = P3(fragCoord.x, 0., iResolution.x, -80., 80.);
  float y = P3(fragCoord.y, 0., iResolution.y, -45., 45.);
  x = floor(x);
  y = floor(y);
  float frame = iTime * 25.;
  float scale = 1./100.;
  float x0 = x * scale;
  float y0 = y * scale;
  fragColor = raytrace_norm(x0, y0, frame);
#endif
}

// ---------------------------------------------------------------------------------------
