//
//  I've no idea what these four fluids would have to be to act like this in the real
//  world, but I wanted to bring more color and visual interest to the Two-Phase Salad
//  Dressing Mixer. https://www.shadertoy.com/view/fldBRs
//
//  The rendering effect is achieved by rendering the fluids into separate channels,
//  and blurring them separately as well. To skip the effect of the blurring, you could
//  switch iChannel0 on the Image tab to Buffer C.
//
//  There's an attract mode if you don't click, that turns the mixer on and
//  off once per 1000 frames. If you start clicking, you gain control over
//  the mixer motor: click to enable or disable. You can also drag the mixer
//  wheel and impart a velocity by letting go while it spins.
//
//  Buffer A computes the particle positions and neighbors
//  Buffer B does a voronoi search to help out building neighborhoods
//  Buffer C renders the particles
//  Buffer D blurs buffer C
//
// ---------------------------------------------------------------------------------------


void gradient4(vec2 fragCoord, out vec4 xGrad, out vec4 yGrad)
{
    vec2 delta = vec2(0.1, 0);
    xGrad = texture(iChannel0, (fragCoord + delta.xy) / iResolution.xy) - 
        texture(iChannel0, (fragCoord - delta.xy) / iResolution.xy);

    yGrad = texture(iChannel0, (fragCoord + delta.yx) / iResolution.xy) - 
        texture(iChannel0, (fragCoord - delta.yx) / iResolution.xy);

    xGrad = normalize(xGrad);
    yGrad = normalize(yGrad);
}

float computeSpecular(
    in float specularCoefficient,
    in float specularExponent,
    in vec3 nvNormal,
    in vec3 nvFragToLight,
    in vec3 nvFragToCam)
{
    vec3 blinnH = normalize(nvFragToLight + nvFragToCam);
    float valSpecular = pow(max(0.0, dot(nvNormal, blinnH)), specularExponent);
    valSpecular *= specularCoefficient;

    return valSpecular;
}

const vec4[4] FLUID_COLORS = vec4[](vec4(0.0, 0.0, 1.0, 1.0),
                                    vec4(1.0, 0.7, 0.0, 1.0),
                                    vec4(1.0, 0.0, 0.0, 1.0),
                                    vec4(0.7, 0.0, 1.0, 1.0));

void mainImage( out vec4 fragColor, vec2 fragCoord )
{   
    //vec3 norm = normalize(vec3(grad(fragCoord), 1));
    vec4 xGrad, yGrad;
    gradient4(fragCoord, xGrad, yGrad);
    vec4 fluid = texture(iChannel0, fragCoord/iResolution.xy);
    //vec2 grad = mix(bothGrads.xz, bothGrads.yw,smoothstep(0.0, 1., fluid.y));
    vec2 grad = vec2(xGrad[0], yGrad[0]);
    for (int i = 1; i < 4; ++i)
    {
        grad = mix(grad, vec2(xGrad[i], yGrad[i]), fluid[i]);
    }

    vec2 p = fragCoord/iResolution.xy;

    vec4 background = textureLod(iChannel3, p, 5.0);
    float density = max(max(max(fluid.x, fluid.y), fluid.z), fluid.w) *4.;
    vec4 color = vec4(0);
    for (int i = 0; i < 4; ++i)
    {
        color = mix(color, FLUID_COLORS[i], smoothstep(0.0, 0.5, fluid[i]));
    }
    vec4 water = texture(iChannel3, p + grad * 0.2) * color; // Refract :)
    fragColor = mix(background, water, smoothstep(0.5, 1.0, density)); // Blur water edges

    fxState state = fxGetState();
    vec2 steelCoord = vec2(fragCoord.x * 2.0, (fragCoord.y) * 0.004);
    vec2 adjusted = (p - 0.5) * vec2(iResolution.x / iResolution.y, 1.);
    if (length(adjusted) < 0.5/1.2 + 0.02) // Radial pattern in the center circle
    {
        float a = atan(adjusted.y, adjusted.x) + state.rotation;
        steelCoord = vec2(a*400., length(adjusted)*20.);
    }
    float wallDist = distanceFromWalls(p * vec2(iResolution.x / iResolution.y, 1.), iResolution, state.rotation);

    vec3 steelNorm = texture(iChannel2, steelCoord/iResolution.xy).xyz;
    steelNorm.xz += getNormalFromWalls(p * vec2(iResolution.x / iResolution.y, 1.), iResolution, state.rotation) * (1.0 - smoothstep(0.007, 0.01, -wallDist));

    steelNorm = normalize(steelNorm);
    vec3 lightDir = normalize(vec3(1,-1,1));
    float steelSpec = computeSpecular(0.8, 15.0, steelNorm, lightDir, vec3(0.0, 1.75-p.x, 0.5 - p.y));
    vec4 steel = vec4(vec3(steelSpec), 1.0) * 0.5 + 0.3;

    fragColor = mix(fragColor, steel, vec4(clamp(-wallDist*iResolution.y, 0.0, 1.0)));
    //fragColor = vec4(texelFetch(iChannel0, ivec2(fragCoord), 0).bgra);
    fragColor.a = 1.;
}

