// gaussian blur from FabriceNeyret2's smart gaussian blur: https://www.shadertoy.com/view/WtKfD3

int           N =  15;                              // target sampling rate
float         w,                                   // filter width
              z;                                        // LOD MIPmap level to use for integration 
#define init  w = .0125; \
              z = ceil(max(0.,log2(w*R.y/float(N))));   // N/w = res/2^z
#define R     iResolution.xy


vec4 convol2D(vec2 U) {                                                     
    vec4  O = vec4(0);  
    float r = float(N-1)/2., g, t=0.;                                       
    for( int k=0; k<N*N; k++ ) {                                            
        vec2 P = vec2(k%N,k/N) / r - 1.;                                    
        t += g = exp(-2.*dot(P,P) );                                        
        O += g * textureLod(iChannel0, (U+w*P) *R.y/R, z );  
    }                                                                       
    return O/t;                                                             
}      

void mainImage( out vec4 O, vec2 u )
{
    init
    vec2 p = (u - iResolution.xy * 0.5) / iResolution.y;
    if (length(p) < 0.5) // restrict blur to circle to improve speed
    {
        vec2 U = u / R.y;  
        O = convol2D(U);
    }
}

