const int MAX_ITER = 550;
const float MIN_DIST = 0.01;
const float MAX_DIST = 40.0;
const float PI = radians(180.);

float brightness = 0.;

mat2 rot2d(float r)
{
    return mat2(cos(r), -sin(r), sin(r), cos(r));
}
float random(vec2 seed)
{
    return fract(sin(dot(seed.xy, vec2(22.9,48.6)))*2356.);
}

float rect(vec3 p, vec3 dimensions)
{
    return length(max(abs(p) - dimensions/2., vec3(0.)));
}
float sea(vec3 p)
{
    float r = random(floor(p.xz)*2.);
    if (p.y < 1.5)
        brightness = (r*.5+.5);
    return (p.y + random(floor(p.xz)) + r*sin(r*50.+iTime))*0.03;
}

float scene_dist(vec3 p)
{
    return sea(p);
}
float raymarch(vec3 p, vec3 dir)
{
    float dist = 0.;
    for (int i = 0; i < MAX_ITER; i++)
    {
        float d = scene_dist(p + dist*dir);
        dist += d;

        if (dist >= MAX_DIST || d <= MIN_DIST) break;
    }

    return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord.xy-iResolution.xy) / iResolution.y;

    vec3 ray_dir = normalize(vec3(uv.xy, 1.));
    ray_dir.xz *= rot2d(radians(-35.));
    float d = raymarch(normalize(vec3(1,0,1))*iTime + vec3(0, 2, 0), ray_dir);

    vec3 color = vec3(0.45, 0.2,0.9);
    fragColor = vec4(color.rgb*brightness, 1.);
}

void render(inout float col,vec2 uv){
	vec3 ro = vec3(0.0,0.0,3.0);
	vec3 rd = normalize(vec3(uv,-2.0));

	float d = 0.0;
	for(float i=0.0;i<256.0;i++){
		vec3 p = ro + rd*d, cp = p;
		cp.xy *= mat2(cos(time),-sin(time),sin(time),cos(time));
		cp.yz *= mat2(cos(time),-sin(time),sin(time),cos(time));
		cp.zx *= mat2(cos(time),-sin(time),sin(time),cos(time));
		float h = smin(p.y + 0.5,length(max(abs(cp)-0.4,0.0)) - 0.4,0.25);
		if(h < 0.001 || d > 20.0) break;
		d += h;
	}

  if(d < 20.0){
  	vec3 p = ro + rd*d;
  	col += p08*dot(vec3(0.8),p);
  }
}

