struct material
{
    vec3 baseColor;
    float roughness;
};

vec3 aces_tonemap(vec3 color) // https://www.shadertoy.com/view/XsGfWV
{	
	mat3 m1 = mat3(
        0.59719, 0.07600, 0.02840,
        0.35458, 0.90834, 0.13383,
        0.04823, 0.01566, 0.83777
	);
	mat3 m2 = mat3(
        1.60475, -0.10208, -0.00327,
        -0.53108,  1.10813, -0.07276,
        -0.07367, -0.00605,  1.07602
	);
	vec3 v = m1 * color;    
	vec3 a = v * (v + 0.0245786) - 0.000090537;
	vec3 b = v * (0.983729 * v + 0.4329510) + 0.238081;
	return pow(clamp(m2 * (a / b), 0.0, 1.0), vec3(1.0 / 2.2));	
}

float calcSpecular(vec3 e, vec3 n, vec3 l, float p)
{
    return pow(clamp(dot(l, reflect(e, n)), 0.0, 1.0), p);
}

float join(inout float a, float b, material bm, inout material m)
{
    if(a < b)
    {
        return a;
    }
    else
    {
        m = bm;
        return b;
    }

}

float sdPlane(vec3 p, float y)
{
    return p.y - y;
}

float sdSphere(vec3 p, vec3 pos, float r)
{
    return length(p - pos) - r;
}

float sdBox( vec3 p, vec3 b )
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdBox( vec3 p, vec3 b, vec3 bPos )
{
    return sdBox(p - bPos, b);
}

float sceneDist(vec3 p, out material m)
{
    m = material(vec3(0.0, 0.0, 1.0), 1.0);
    float dist = sdSphere(p, vec3(0.3, 0.0, 2.0), 0.2);
    dist = join(dist, sdSphere(p, vec3(-0.3, 0.0, 2.0), 0.2), material(vec3(0.0, 0.5, 1.0), 8.0), m);
    dist = join(dist, sdBox(p, vec3(0.5, 0.1, 0.5), vec3(0.0,-0.3, 2.0)), material(vec3(1.0, 0.0, 0.0), 8.0), m);
    dist = join(dist, sdPlane(p, -0.3), material(vec3(0.0, 1.0, 0.0), 8.0), m);
    return dist;
}

vec3 calcNormal( in vec3 p ) // for function f(p)
{
    const float h = 0.0001; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    material m;
    return normalize( k.xyy*sceneDist( p + k.xyy*h, m) + 
                      k.yyx*sceneDist( p + k.yyx*h, m) + 
                      k.yxy*sceneDist( p + k.yxy*h, m) + 
                      k.xxx*sceneDist( p + k.xxx*h, m) );
}

void march(vec3 dir, inout vec3 p, int maxSteps, float minDist, out bool hit, out float depth, out vec3 normal, out material m)
{
    vec3 oldP = p;
    depth = 0.0;
    for(int i = 0; i < maxSteps; i++)
    {
        depth = sceneDist(p, m);
        p += depth*dir;
        hit = depth < minDist;
        if(hit)
        {
            normal = calcNormal(p);
            depth = distance(oldP, p);
            return;
        }
    }
    normal = calcNormal(p);
}

void marchShadow(vec3 dir, vec3 p, int maxSteps, float minDist, out float inShadow, float k)
{
    vec3 oldP = p;
    float dist = 0.002;
    float totalDist = dist;

    p += dist*dir;
    inShadow = 1.0;
    material m = material(vec3(0.0, 0.0, 0.0), 0.0);
    for(int i = 0; i < maxSteps; i++)
    {
        dist = sceneDist(p, m);
        totalDist += dist;

        if(dist < minDist)
        {
            inShadow = 1.0;
            return;
        }

        p += dist*dir;
        inShadow = min(inShadow, k*dist/totalDist);
    }
    inShadow = 1.0-inShadow;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.y *= iResolution.y/iResolution.x;

    vec3 eyeDir = normalize(vec3(uv * vec2(1.0, 1.0), 1.0));
    vec3 eyePos = vec3(0.0, 0.0, 0.0);
    vec2 mouse = iMouse.z > 0.0?iMouse.xy/iResolution.xy - 0.5 : vec2(sin(iTime), cos(iTime) + 2.0);
    vec3 lightingDir = normalize(vec3((mouse) * vec2(1.0, iResolution.y/iResolution.x) * 10.0, -1.0));

    bool hit = false;
    float depth = 0.0;
    vec3 normal = vec3(0.0);
    material hitMat;
    march(eyeDir, eyePos, 512, 0.0001, hit, depth, normal, hitMat);

    float inShadow = 0.0;
    marchShadow(lightingDir, eyePos, 64, 0.00001, inShadow, 4.0);

    //lightingDir = vec3(0.0, 1.0, 0.0);
    float lighting = max(0.0, dot(normal, lightingDir));
    float specular = calcSpecular(eyeDir, normal, lightingDir, 8.0 * hitMat.roughness);
    specular = clamp(specular, 0.0, 1.0);
    inShadow *= float(hit);
    //lighting *= (1.0-inShadow) + 0.5;
    //lighting /= 1.5;

    vec3 color = (vec3(lighting + specular) * hitMat.baseColor) * (1.0-inShadow);
    color = aces_tonemap(color);

    //fragColor = vec4(vec3(specular),1.0);
    fragColor = vec4(hit? color : vec3(0.0),1.0);
    //fragColor = vec4(eyeDir.xy,0.0, 1.0);
}

// ---------------------------------------------------------------------------------------
//	Created by fenix in 2022
//	License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
//  After that sickly sweet Candy Mixer https://www.shadertoy.com/view/NtcBzX
//  I think we need some healthy salad to balance our diet. So I ran with
//  elenzil's idea of making a heterogenous fluid, and decided to try to make
//  an oil/water mixture, using the rendering style from my still-most popular
//  shader, Flowing Water https://www.shadertoy.com/view/7s3Bz2
//
//  To differentiate them physically, the oil phase is more viscous, and
//  there is a pretty fake lifting force that sorts the oil to the top.
//
//  The rendering effect is achieved by rendering the water and oil into
//  separate buffers, and blurring them separately as well. Then when
//  rendering, the oil's gradient is preferred based on the oil density.
//  The oil droplets are also rendered larger. I was attempting to approximate
//  the bubble-like appearance of oil droplets in water.
//
//  To skip the effect of the blurring, you could switch iChannel0 on the
//  Image tab to Buffer C.
//
//  There's an attract mode if you don't click, that turns the mixer on and
//  off once per 1000 frames. If you start clicking, you gain control over
//  the mixer motor: click to enable or disable. You can also drag the mixer
//  wheel and impart a velocity by letting go while it spins.
//
//  Buffer A computes the particle positions and neighbors
//  Buffer B does a voronoi search to help out building neighborhoods
//  Buffer C renders the particles
//  Buffer D blurs buffer C
//
// ---------------------------------------------------------------------------------------


vec4 gradient(vec2 fragCoord)
{
    vec2 delta = vec2(0.1, 0);
    vec4 result = vec4(texture(iChannel0, (fragCoord + delta.xy) / iResolution.xy).xy - 
        texture(iChannel0, (fragCoord - delta.xy) / iResolution.xy).xy,
        texture(iChannel0, (fragCoord + delta.yx) / iResolution.xy).xy - 
        texture(iChannel0, (fragCoord - delta.yx) / iResolution.xy).xy);

    result.xz = normalize(result.xz);
    result.yw = normalize(result.yw);

    return result;
}

float computeSpecular(
    in float specularCoefficient,
    in float specularExponent,
    in vec3 nvNormal,
    in vec3 nvFragToLight,
    in vec3 nvFragToCam)
{
    vec3 blinnH = normalize(nvFragToLight + nvFragToCam);
    float valSpecular = pow(max(0.0, dot(nvNormal, blinnH)), specularExponent);
    valSpecular *= specularCoefficient;

    return valSpecular;
}

const vec4 OIL_COLOR = vec4(0.85, 0.8, 0.0, 1.0);

void mainImage( out vec4 fragColor, vec2 fragCoord )
{   
    //vec3 norm = normalize(vec3(grad(fragCoord), 1));
    vec4 bothGrads = gradient(fragCoord);
    vec2 fluid = texture(iChannel0, fragCoord/iResolution.xy).xy;
    vec2 grad = mix(bothGrads.xz, bothGrads.yw,smoothstep(0.0, 1., fluid.y));
    vec2 p = fragCoord/iResolution.xy;

    vec4 background = textureLod(iChannel3, p, 5.0);
    float density = max(fluid.x, fluid.y) *4.;
    vec4 color = mix(vec4(1), OIL_COLOR, smoothstep(0.0, 1., fluid.y));
    vec4 water = texture(iChannel3, p + grad * 0.2) * color; // Refract :)
    fragColor = mix(background, water, smoothstep(0.5, 1.0, density)); // Blur water edges

    fxState state = fxGetState();
    vec2 steelCoord = vec2(fragCoord.x * 2.0, (fragCoord.y) * 0.004);
    vec2 adjusted = (p - 0.5) * vec2(iResolution.x / iResolution.y, 1.);
    if (length(adjusted) < 0.5/1.2 + 0.02) // Radial pattern in the center circle
    {
        float a = atan(adjusted.y, adjusted.x) + state.rotation;
        steelCoord = vec2(a*400., length(adjusted)*20.);
    }
    float wallDist = distanceFromWalls(p * vec2(iResolution.x / iResolution.y, 1.), iResolution, state.rotation);

    vec3 steelNorm = texture(iChannel2, steelCoord/iResolution.xy).xyz;
    steelNorm.xz += getNormalFromWalls(p * vec2(iResolution.x / iResolution.y, 1.), iResolution, state.rotation) * (1.0 - smoothstep(0.007, 0.01, -wallDist));

    steelNorm = normalize(steelNorm);
    vec3 lightDir = normalize(vec3(1,-1,1));
    float steelSpec = computeSpecular(0.8, 15.0, steelNorm, lightDir, vec3(0.0, 1.75-p.x, 0.5 - p.y));
    vec4 steel = vec4(vec3(steelSpec), 1.0) * 0.5 + 0.3;

    fragColor = mix(fragColor, steel, vec4(clamp(-wallDist*iResolution.y, 0.0, 1.0)));
    //fragColor = vec4(texelFetch(iChannel0, ivec2(fragCoord), 0).xy,0,0);
    fragColor.a = 1.;
}

