const vec3 lightDir = normalize(vec3(1,-1,1));

void renderParticle(in fxParticle p, in vec2 fragCoord, inout vec4 fragColor)
{   
    vec2 closest;
    p.pos.x *= iResolution.y / iResolution.x;
    float dist = sqrt(linePointDist2(p.pos, p.pos - p.vel, fragCoord, iResolution, closest));

    if (p.color == vec3(1))
    {
        // water
        float drawSize = min(p.size,PARTICLE_REPEL_SIZE)*1.5;
        fragColor.x = max(fragColor.x, mix(fragColor.x, 1., 1.-smoothstep(drawSize*0.2, drawSize * 2.5, dist)));
    }
    else
    {
        // oil
        float drawSize = PARTICLE_REPEL_SIZE * 2.;//min(p.size,PARTICLE_REPEL_SIZE)*2.;
        fragColor.y = max(fragColor.y, mix(fragColor.y, 1., 1.-smoothstep(drawSize*0.25, drawSize * 1.5, dist)));
    }
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{    
