const int MAX_ITER = 100;
const float MIN_DIST = 0.01;
const float MAX_DIST = 100.0;
const float PI = radians(180.);

mat2 rot2d(float r)
{
    return mat2(cos(r), -sin(r), sin(r), cos(r));
}

float rect(vec3 p, vec3 dimensions)
{
    return length(max(abs(p) - dimensions/2., vec3(0.)));
    //return length(p.xz) - 0.15;
}

float scene_dist(vec3 p)
{
    p.xy *= rot2d(length(p));
    p.xy = abs(p.xy);
    p.z = fract(p.z);
    p -= vec3(0.5,.5,0.5);
    p.yz *= rot2d(PI/2.);
    p.xz *= rot2d(iTime);
    return rect(p, vec3(.25,1.,.25));
}
float raymarch(vec3 p, vec3 dir)
{
    float dist = 0.;
    for (int i = 0; i < MAX_ITER; i++)
    {
        float d = scene_dist(p + dist*dir);
        dist += d;

        if (dist >= MAX_DIST || d <= MIN_DIST) break;
    }

    return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord.xy-iResolution.xy) / iResolution.y;

    vec3 ray_dir = normalize(vec3(uv.xy, 1.));
    float d = raymarch(vec3(0, 0, iTime), ray_dir);

    vec3 color = vec3(0.9, 0.,0.8);
    fragColor = vec4(color.rgb/max(d*.4, 1.), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 factor = fragCoord / iResolution.xy * 2. - vec2(1,1);
    float fov = 70. + texture(iChannel0, vec2(2.5/iResolution.x,0)).x;
    vec3 pos = texture(iChannel0, vec2(0,0)).xyz;
    vec2 rot = texture(iChannel0, vec2(1.5/iResolution.x,0)).zw;
    Ray r = Ray(pos, normalize(vec3(1., factor.y * fov / 100., factor.x * fov / 100. * iResolution.x / iResolution.y)) );
    r.dir = vec3(
    r.dir.x * cos(radians(rot.y)) - r.dir.y * sin(radians(rot.y)),
    r.dir.y * cos(radians(rot.y)) + r.dir.x * sin(radians(rot.y)),
    r.dir.z);
    r.dir = normalize(vec3(
    r.dir.x * cos(radians(rot.x)) - r.dir.z * sin(radians(rot.x)),
    r.dir.y,
    r.dir.z * cos(radians(rot.x)) + r.dir.x * sin(radians(rot.x))));
    HitRecord nearest = HitRecord(false, vec2(0,0), r.dir, vec3(0,0,0), vec3(1,1,1), 1e24);
    lowp int type = MIRROR;
    int iter = 0;
    int sphereIndex = -1;
    spheres[0].pos = pos;
    lights[0].pos = vec3(cos(iTime*.2)*-100.,150.,sin(iTime*.2)*20.);
    bool reversed = false;
    while (type != 0 && iter < MAX_REF)
    {
        iter++;
        int minIndex = -1;
        vec3 tmpcol = nearest.color;
        for (int i = 0; i < spheres.length(); i++)
        {
            HitRecord rec = HitSphere(r, spheres[i], .0, nearest.dist, tmpcol, reversed);
            if (rec.hit && (!nearest.hit || rec.dist < nearest.dist))
            {
                sphereIndex = i;
                nearest = rec;
                type = spheres[i].type;
            }
        }
        for (int i = 0; i < tris.length(); i++)
        {
            HitRecord rec = HitTriangle(r, tris[i], .0, nearest.dist, tmpcol, reversed);
            if (rec.hit && (!nearest.hit || rec.dist < nearest.dist))
            {
                sphereIndex = -1;
                nearest = rec;
                type = tris[i].type;
            }
        }
        if (!nearest.hit) break;
        for (int i = 0; i < lights.length(); i++)
        {
            Ray lr = Ray(nearest.point, normalize(lights[i].pos-nearest.point));
            vec3 norm = nearest.normal;
            if (type == 0) norm = normalize(nearest.normal+texture(iChannel2, nearest.normal).xyz*.1-.05);
            float factor = dot(lr.dir,norm);
            if (factor < 0.) continue;
            bool hit = false;
            for (int n = 0; n < spheres.length(); n++)
            {
                if (sphereIndex == n) continue;
                HitRecord rec = HitSphere(lr, spheres[n], .0001, 1e24, vec3(0,0,0), true);
                if (rec.hit)
                {
                    hit = true;
                    break;
                }

            }
            if (!hit) for (int n = 0; n < tris.length(); n++)
            {
                HitRecord rec = HitTriangle(lr, tris[n], .0001, 1e24, vec3(0,0,0), true);
                if (rec.hit)
                {
                    hit = true;
                    break;
                }
            }
            if (!hit)
            {
                vec3 halfV = normalize(lr.dir - r.dir);
                float deltaB = pow(max(dot(norm, halfV), 0.0), 512.);
                nearest.color += vec3(lights[i].strength * factor*.45 + deltaB);
            }
        }
        if (type != 0)
        {
            r.pos = nearest.point;
            if (type == MIRROR)
            {
                r.dir = reflect(r.dir, nearest.normal);
            }
            else
            {
                vec3 newDir = refract(r.dir, nearest.normal, reversed ? 1.45 : 0.689655);
                if (dot(newDir, nearest.normal) < 0.) reversed = !reversed;
                else
                {
                    newDir = reflect(r.dir, nearest.normal);
                }
                r.dir = newDir;
            }
            nearest.normal = r.dir;
            nearest.dist = 1e24;
            nearest.hit = false;
        }
    }
    if (!nearest.hit)
    {
        float s = pow(max(dot(normalize(lights[0].pos-pos), r.dir), 0.0), 512.);
        fragColor = texture(iChannel1, nearest.normal)*vec4(nearest.color*.7,1.)+s;
    }
    else
    {
        fragColor = vec4(nearest.color,1.);
    }
}

