#define QWERTY

#define OPAQUE 0
#define MIRROR 1
#define TRANSP 2

struct HitRecord
{
    bool hit;
    vec2 UV;
    vec3 normal;
    vec3 point;
    vec3 color;
    float dist;
};

struct Ray
{
    vec3 pos;
    vec3 dir;
};

struct Sphere
{
    vec3 pos;
    float radius2;
    vec3 color;
    lowp int type;
};

struct Triangle
{
    vec3 A, AB, AC;
    vec3 normal;
    vec3 color;
    lowp int type;
};

struct Light
{
    vec3 pos;
    float strength;
};

HitRecord HitSphere(Ray r, Sphere sp, float minimum, float maximum, vec3 colorIn, bool reverse)
{
    HitRecord result = HitRecord(false, vec2(0,0), vec3(0,1,0), vec3(0,0,0), vec3(1,1,1), 0.0);
    float t0, t1;
    vec3 L = r.pos - sp.pos;
    float a = dot(r.dir, r.dir);
    float b = 2.0 * dot(r.dir, L);
    float c = dot(L,L) - sp.radius2;
    float discr = b * b - 4.0 * a * c;
    if (discr < 0.0)
    {
        return result;
    }
    else if (discr == 0.0)
    {
        t0 = - 0.5 * b / a;
        t1 = t0;
       }
    else
    {
        float q = (b > 0.0) ? -0.5 * (b + sqrt(discr)) : -0.5 * (b - sqrt(discr));
        t0 = q / a;
        t1 = c / q;
    }
    if (reverse ? (t1 > t0) : (t0 > t1))
    {
        float tmp = t0;
        t0 = t1;
        t1 = tmp;
    }
    if (t0 < minimum || t0 > maximum) {
        return result;
    }
    result.hit = true;
    result.dist = t0;
    result.point = r.pos + r.dir * t0;
    result.normal = normalize(result.point-sp.pos);
    if (reverse) result.normal = -result.normal;
    result.color = colorIn * sp.color;
    return result;
}

HitRecord HitTriangle(Ray r, Triangle tr, float minimum, float maximum, vec3 colorIn, bool reverse)
{
	HitRecord result = HitRecord(false, vec2(0,0), vec3(0,1,0), vec3(0,0,0), vec3(1,1,1), 0.0);
    if (reverse)
    {
        vec3 pvec = cross(r.dir, tr.AB);
        float det = dot(tr.AC, pvec);
        if (det < 0.00001) return result;
        vec3 tvec = r.pos - tr.A;
        result.UV.x = dot(tvec, pvec);
        if (result.UV.x < .0 || result.UV.x > det) return result;
        vec3 qvec = cross(tvec, tr.AC);
        result.UV.y = dot(r.dir, qvec);
        if (result.UV.y < .0 || result.UV.y + result.UV.x > det) return result;
        det = 1./det;
        result.dist = dot(tr.AB, qvec) * det;
        if (result.dist < minimum || result.dist > maximum) return result;
        result.UV *= det;
        result.normal = -tr.normal;
    }
    else
    {
        vec3 pvec = cross(r.dir, tr.AC);
        float det = dot(tr.AB, pvec);
        if (det < 0.00001) return result;
        vec3 tvec = r.pos - tr.A;
        result.UV.x = dot(tvec, pvec);
        if (result.UV.x < .0 || result.UV.x > det) return result;
        vec3 qvec = cross(tvec, tr.AB);
        result.UV.y = dot(r.dir, qvec);
        if (result.UV.y < .0 || result.UV.y + result.UV.x > det) return result;
        det = 1./det;
        result.dist = dot(tr.AC, qvec) * det;
        if (result.dist < minimum || result.dist > maximum) return result;
        result.UV *= det;
        result.normal = tr.normal;
    }
	result.point = r.pos + r.dir * result.dist;
    result.color = colorIn * tr.color;
	result.hit = true;
	return result;
}

