#define MAX_STEPS 400
#define MAX_DIST 10.
#define SURF_DIST .1

// RayMarching from TheArtOfCode

// From BlackleMori
#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdBox(in vec2 p, in vec2 b){
    vec2 d = abs(p)-b;
    return length(max(d,0.))+min(max(d.x,d.y),0.);
}

vec3 GetRayOrigin() {
    vec2 m = iMouse.xy/iResolution.xy;
    float r = 5.;
    float a = 0.25 * iTime;
    vec3 ro = vec3(r * cos(a), 7, r * sin(a));
    //ro.yz *= rot(-m.y*3.14+1.);
    //ro.xz *= rot(-m.x*6.2831);
    return ro;
}

float test(vec3 p0) {
   // p0.y += 4.;
    float sd = length(p0.xz) - 3.;
    //p0 *= 0.5;
    //sd = p0.y - 0.5;
    vec4 p = vec4(p0, 1.);
    p.y += 0.05 * iTime;
    for(int i = 0; i < 6; i++){
      p.xyz = mod(p.xyz-1., 2.)-1.;
      p*=(1./dot(p.xyz, p.xyz));
    }
    p/=p.w;
    return max(sd, abs(p.y));
}

float GetDist(vec3 p) {
    float d2 = p.y + 0.02 *  dot(p.xz,p.xz);//0.005 * length(p.xz);//length(p) - 0.5;

    vec3 dim = vec3(0.5,100.,0.5);
    float d = length(p + vec3(0,2,0)) - 1.;
    //d = sdBox(p + vec3(0,2. + dim.y,0), dim) - 0.25;
    d = test(p + vec3(0,2,0));
    d2 = -smin(-d2, d, 0.1);
    return d2;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = GetRayOrigin();

    // starts becoming unenjoyable before ~1000 seconds
    vec3 rd = GetRayDir(uv, ro, vec3(0,0.025,0), 1. + iTime * tanh(0.1 * iTime));
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    vec3 p = ro + rd * d;
    float IOR = 2.;
    if(d<MAX_DIST) {        
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        float dif = dot(nExit, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);

        float spec = pow(dif, 15.);

        if (dIn < MAX_DIST) {
            float fres = pow(1. + dot(rd, n), 5.);
            float fres2 = pow(1. + dot(rd, nExit), 4.);

            vec3 c1 = texture(iChannel0, iTime * 0.01 + 0.5 * pExit.xy).rgb;
            vec3 c2 = texture(iChannel0, iTime * 0.01 + 0.5 * pExit.yz).rgb;
            vec3 c3 = texture(iChannel0, iTime * 0.01 + 0.5 * pExit.zx).rgb;
            vec3 c = nExit.z * c1 + nExit.x * c2 + nExit.y * c3;
            col = clamp(col + spec, 0., 1.); //???
            col = mix(col, vec3(0), 1. - fres2);

            float k = 0.25;
            col += smoothstep(-k,k, c.r-0.5);
        }
        float fog = 1.-exp(-0.5 * length(p));
        col = mix(col, vec3(0.5 * col), fog);
        col *= pal(fog + 1.75, 0.5 * vec3(0,1,1)/3.);
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

