const int   npipes = 5     ;
const int   sub    = 80    ; // (step/bufferframe)
const float c      = 1./20.; // speed of sound (px/step)

const float pi = radians(180.);
const float tau = pi * 2.;

#define buffer(i) \
void mainImage(out vec4 C, vec2 O) \
{ \
    update(C, O, iChannel0, iResolution, iFrame * 1 + i); \
} \

const int N = 1 + 2 * sub;
int base;
vec4 frame[N];
vec4 dframe[N];
vec3 resolution;

vec4 fetch(int i)
{
    return frame[i];
}

vec4 _step(float t, int i)
{
    vec4 L = fetch(i - 1);
    vec4 C = fetch(i);
    vec4 R = fetch(i + 1);

    // wavelength [px]
    float f = 10.;
    //f = 50.;
    f = resolution.x * .2;

    // period [step]
    f /= c;

    // waveforms
    float z = 0.;
    z = sin(t*tau/f) * .03 * float(t/f < 2.); // cw-pulse
    //z = sin(t*tau/f) * .01; // cw
    //float xt=t*tau/f-3.; z = exp(-xt*xt)*.03; // pulse

    // boundary flux (left/right)
    float lz = 0., rz = 0.;
    lz = z, rz = 0.;
    //lz = 0., rz = z;

    if (base+i == 0)
        L.y = C.y, C.z = lz;
    if (base+i == int(resolution.x)-1)
        R.z = rz;

    // change of mass
    float dm = 0.;
    dm += C.z - R.z; // through boundary

    // effective pressure
    float ep = (L.y-C.y) + 2.*mix(L.y,C.y,.5)*(L.x-C.x)/mix(L.x,C.x,.5);

    // change of flux
    float df = 0.;
    df += ep * c * c; // through pressure
    //if (false)
        df -= C.z * 3e-5; // through damping

    return vec4(0, dm, df, 0);
}

float constriction(float x, float w, float s)
{
    s /= resolution.x;
    float a = .5-w;
    float b = .5+w;
    return mix(1., .1, smoothstep(a-s, a, x) - smoothstep(b, b+s, x));
}

float narrowing(float x, float w, float s)
{
    s /= resolution.x;
    s /= 2.;
    return mix(1., .4, smoothstep(w-s, w+s, x));
}

void update(out vec4 C, vec2 O, sampler2D iChannel0, vec3 iResolution, int iFrame)
{
    resolution = iResolution;
    ivec2 I = ivec2(O);

    if (I.y >= npipes)
    {
        C = vec4(1,2,3,4);
        return;
    }

    const int startFrame = 1;
    //if (iFrame < startFrame) // preview doesn't work in firefox
    if (texelFetch(iChannel0, ivec2(0, npipes), 0) != vec4(1,2,3,4))
    {
        float x = O.x / resolution.x;
        float h = resolution.y/2./float(npipes)*.8;
        if (I.y == 4)
            h *= constriction(x, .1, 10.);
        if (I.y == 3)
            h *= constriction(x, .1, 100.);
        if (I.y == 1)
            h *= narrowing(-x, -.4, 10.);
        if (I.y == 0)
            h *= narrowing(x, .4, 10.);
        C = vec4(h, 0, 0, 0);
        return;
    }

    base = I.x - sub;
    for (int j=0 ; j<N ; ++j)
        frame[j] = texelFetch(iChannel0, ivec2(base + j, I.y), 0);
    for (int i=0 ; i<sub ; ++i)
    {
        float t = float((iFrame - startFrame) * sub + i);
        // Leapfrog integration
        // 1. Update flow
        for (int j=1+i ; j<N-(1+i) ; ++j)
            dframe[j] = _step(t, j);
        for (int j=1+i ; j<N-(1+i) ; ++j)
            frame[j] += dframe[j] * vec4(0, 0, 1, 0);
        t += .5;
        // 2. Update mass
        for (int j=1+i ; j<N-(1+i) ; ++j)
            dframe[j] = _step(t, j);
        for (int j=1+i ; j<N-(1+i) ; ++j)
            frame[j] += dframe[j] * vec4(0, 1, 0, 0);
    }
    C = fetch(I.x - base);
}

