/**
* Buffer A initializes stuff for ray tracing (camera, rays, input handling etc) and initiates
* the ray tracing process. At the end, it accumulates the ray traced result with the previously
* accumulated history buffer. 
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    hashSeed = float(BaseHash(floatBitsToUint(fragCoord))) / float(0xffffffffU) + iTime;

    vec2 uv = (2.0 * (floor(fragCoord) + Hash2(hashSeed)) - iResolution.xy) / iResolution.y;

    vec4 currentMouse = iMouse / iResolution.xyxy;
    vec4 previousMouse = texelFetch(iChannel1, ivec2(fragCoord), 0); // last frame mouse coords
    vec2 cameraPositionUVSpace = texelFetch(iChannel0, ivec2(0), 0).xy;

    vec4 historyColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
    float sampleCount = historyColor.w;

    // Add mouse drag velocity to camera so it only moves from its current position
    if (currentMouse.z > 0.0 && previousMouse.z > 0.0)
    {
        cameraPositionUVSpace += currentMouse.xy - previousMouse.xy;
        cameraPositionUVSpace.y = Saturate(cameraPositionUVSpace.y);

        // Reset history on mouse click
        sampleCount = 1.0;
        historyColor.xyz = vec3(0.0);
    }

    // Reset history on resolution change
    if (texelFetch(iChannel0, ivec2(0), 0).z != iResolution.x)
    {
        sampleCount = 1.0;
        historyColor.xyz = vec3(0.0);
    }

    vec3 outColor = vec3(0.);

    if (iFrame == 0) // Initial camera position
    {
        cameraPositionUVSpace = vec2(0.0, 0.8);
        sampleCount = 1.0;
    } 

    outColor = GetSceneColor(GetCameraRay(uv, cameraPositionUVSpace));
    outColor = mix(historyColor.xyz, outColor, 1.0 / sampleCount++);

    // Save the cameraPosition at [0, 0] coordinate of this buffer
    if (all(lessThan(fragCoord, vec2(1.0))))
    {
        outColor = vec3(cameraPositionUVSpace, iResolution.x);
    }

    fragColor = vec4(outColor, sampleCount);
}

