// The demo superposes several waves of wavelength uniformly
// distributed in a window around a central wavelength Lambda0.
//
// The waves are assumed to be gravity waves at the surface
// of very deep incompressible fluid (= ocean).
// The dispersion relation is then  = (gk)
//
// The demo shows that the envelop of the superposed waves
// (the 'bulge') is traveling at *half* the speed of the physical
// (aka 'phase') speed of the main wave ( (lambda0 * g)).
//
// y-mouse controls the number of sampling points (=superposed sources)
// x-mouse divides the display in 3 regions resp.: 
//   drawing of the amplitude curve, circular wave, planar wave
//
// skal/ (Pascal Massimino)
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0
//
//  https://en.wikipedia.org/wiki/Group_velocity
//  http://www.cbeaume.com/download/MATH3620_chap6.pdf
//

const float Lambda0 = 0.20;  // center of the window
const float dLambda = 0.02;  // window half-size
const vec2 uv0 = vec2(0., .5);   // origin of waves

const float eps = .05;  // drawn curve size

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord / iResolution.xy;
  int draw_type = 0, N = 2 /* number of sources */;

  if (iTime > 0.) {
    draw_type = int(3. * iMouse.x / iResolution.x);
    N = 2 + int(iMouse.y / iResolution.y * 10.);
  }
  float norm = 1. / float(2 * N - 1);
  float t = iTime * 10.;
  float acc0 = 0., acc = 0., dacc = 0.;
  for (int i = -N + 1; i <= N - 1; ++i) {
    float lambda = Lambda0 + float(i) * dLambda / float(N);
    float omega = sqrt(1. / lambda);    // 2..g = unity
    vec2 r = uv - uv0;
    float d = length(draw_type == 1 ? r : r.xx) * 50.;
    float phi = d / lambda - omega * t;
    dacc += -sin(phi) / lambda;  // derivative
    acc += cos(phi);
    if (i == 0) acc0 = cos(phi);  // stored for the background
  }
  dacc *= norm * 1.5;
  acc = 0.5 + 0.5 * acc * norm;
  if (draw_type > 0) {
    fragColor = vec4(acc, acc, acc, 1.0);
  } else {
    float pt = abs(uv.y - acc) / sqrt(1. + dacc * dacc);
    float d = 1. - clamp(pt, 0., eps) / eps;
    vec3 background =
        clamp(acc0 * uv.x * uv.x, 0., 1.) * vec3(.9, .1, .9);
    vec3 col = mix(background, vec3(1., 1., 1.), d);
    fragColor = vec4(col,1.0);
  }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    float frequency = 0.5;       // How often the ripple appears per second
    float scrollingSpeed = 2.5;  // How fast the ripple propagates
    float thickness = 0.3;       // How thick the ripple is in (1 == entire screen height)


    // Take the height (here uv.y, but could be position.y)
    float ripple = uv.y;

    // Add the time (according to some parameters) to the height, then take the fraction of that to get value from 0 to 1
    ripple = fract((ripple + iTime * scrollingSpeed) * frequency / abs(scrollingSpeed));

    // Turn sawtooth wave ( /|/|/| ) into triangle wave ( /\/\/\ )
    ripple = abs(ripple * 2. - 1.);


    // Cut off the wave below a certain threshold and smoothly blend
    ripple = smoothstep(1.0 - thickness * frequency / abs(scrollingSpeed), 1.0, ripple);

    // Alternatively cut off the wave without smoothly blending
    //ripple = step(1.0 - thickness * frequency / scrollingSpeed, ripple);


    // Get the color and put it on the screen
    vec3 col = vec3(ripple, ripple, ripple);
    fragColor = vec4(col,1.0);
}

