const vec3 KERNEL = vec3(-HALF_SQRT_3, 0.5, THIRD_SQRT_3);

// Transformation Utilities
vec2 translate(vec2 point, vec2 by);
vec2 scale(vec2 point, vec2 scale);


// Hex Utilities
bool is_inside_grid(vec3 hex, float half_width);
vec3 cube_round(vec3 cube);
vec3 uv_to_hex(vec2 uv, float size);
vec2 hex_to_uv(vec3 hex, float size);


// SDF Utilities
float sd_hexagon(vec2 point, float half_size);
float sd_segment(vec2 point, vec2 a, vec2 b);
float sd_lmr_hexagon(vec2 point, float radius, float grid_radius);

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Resolution dependent antialiasing, used with smoothstep
	float aa = 1.0 / min(iResolution.y, iResolution.x) * 10.0 * CAMERA_ZOOM;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Compute zoom taking into account the current aspect ratio
	vec2 zoom = iResolution.xy * CAMERA_ZOOM / min(iResolution.y, iResolution.x);

    // Our camera offset
    vec2 camera_offset = vec2(0.0, 0.0);
    if (iMouse.z > EPSILON) {
        camera_offset = iMouse.xy / iResolution.xy;
        camera_offset = scale(translate(camera_offset, vec2(0.5, 0.5)), zoom);
    }

    // Center and scale the UV
	vec2 point = translate(scale(translate(uv, vec2(0.5, 0.5)), zoom), camera_offset);

    // Compute the current Hexagon in Cube Coordinates
	vec3 hex = uv_to_hex(point, HEX_RADIUS);

    // Get the center of the Hexagon
	vec2 center = hex_to_uv(hex, HEX_RADIUS);

    // Transform our UV into the Local Coordinate System of the Hexagon
	vec2 relative_point = (point - center) / HEX_RADIUS;

    // Compute the SDF
    float roundness = 0.0;
#if ANIMATED == 1
    roundness = cos(iTime * 0.75) * 1.1 + 1.0;
#endif

    vec3 color;
    if (is_inside_grid(hex, GRID_RADIUS)) {
        // Hexagon SDF
        float signed_distance =
            sd_hexagon(relative_point * (HEX_MARGIN + HEX_RADIUS + roundness), HEX_RADIUS) - roundness;

        // Inigo Quilez's Color Scheme
        color = vec3(1.0) - sign(signed_distance) * vec3(0.1,0.4,0.7);
        color *= 1.0 - exp(-2.0 * abs(signed_distance));
        color *= 0.8 + 0.2 * cos(signed_distance);
        color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.01 + aa, abs(signed_distance)));
    } else {
        // LMR Hexagonal Grid SDF
        float signed_distance = sd_lmr_hexagon(point, HEX_RADIUS, GRID_RADIUS);

        color = mix(
            color,
            vec3(1.0),
            smoothstep(
                0.08 + aa * 0.25,
                0.06,
                abs(signed_distance - 0.25) + 0.05
            )
        );

        color = mix(
            color,
            vec3(1.0),
            smoothstep(
                0.08 + aa * 0.25,
                0.06,
                abs(signed_distance - 0.02) + 0.06
            )
        );

		color *= 1.0 - exp(-60.0 * abs(signed_distance));
		color *= 0.9 + 0.6 * cos(30.0 * signed_distance);
		color += (vec3(1.0, 0.58, 0.3) * cos(25.0 * signed_distance)) * smoothstep(0.01, 0.1, signed_distance);
    }

    // Output to screen
    fragColor = vec4(color, 1.0);
}


// -- Transformation Utilities Implementations --
vec2 translate(vec2 point, vec2 by) {
    return point - by;
}

vec2 scale(vec2 point, vec2 scale) {
    return point * scale;
}

// -- Hex Utilities Implementations --
// Please check out https://www.redblobgames.com/grids/hexagons/
// The source of most these functions.
bool is_inside_grid(vec3 hex, float grid_radius) {
	return (
		abs(hex.x) <= grid_radius && 
		abs(hex.y) <= grid_radius && 
		abs(hex.z) <= grid_radius
	);
}

// Justin Pombrio's
// https://justinpombrio.net/programming/2020/04/28/pixel-to-hex.html
vec3 cube_round(vec3 cube) {
	float a = ceil(cube.x - cube.y);
    float b = ceil(cube.y - cube.z);
    float c = ceil(cube.z - cube.x);

	return vec3(
		round((a - c) / 3.0),
		round((b - a) / 3.0),
		round((c - b) / 3.0)
	);
}

vec3 uv_to_hex(vec2 uv, float size) {
	float q = uv.x * TWO_THIRDS / size;
	float r = (-THIRD * uv.x + THIRD_SQRT_3 * uv.y) / size;
	float s = -q - r;

	return cube_round(vec3(q, r, s));
}

vec2 hex_to_uv(vec3 hex, float size) {
	float x = THREE_HALFS * hex.x * size;
	float y = ((HALF_SQRT_3 * hex.x) + (SQRT_3 * hex.y)) * size;

	return vec2(x, y);
}

// -- SDF Utilities Implementations --
// Please check out https://iquilezles.org/articles/distfunctions2d/
// For other SDF functions
float sd_hexagon(vec2 point, float radius) {
    point = abs(point);
    point -= 2.0 * min(dot(KERNEL.xy, point), 0.0) * KERNEL.xy;
    point -= vec2(clamp(point.x, -KERNEL.z * radius, KERNEL.z * radius), radius);
    return length(point) * sign(point.y);
}

float sd_segment(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sd_lmr_hexagon(vec2 point, float radius, float grid_radius) {
    float H = 2.0 * radius;     // height
    float W = SQRT_3 * radius;  // width
    float XS = W;               // horizontal spacing
    float YS = H * 0.75;        // vertical spacing

    // Here you can swap point.y and point.x for pointy-hex grids
    float a = PI + atan(point.y, point.x);
    a = mod(round(a / TAU * 6.0) / 6.0 * TAU, TAU); // pick one of six wedges
    // And also here, you can swap "-sin(a), -cos(a)" and "cos(a), -sin(a)" for pointy-hex grids
    point = mat2(-sin(a), -cos(a), cos(a), -sin(a)) * point;

    // q is the hexagonal column, should be an integer or half integer
    float left_q = -grid_radius / 2.0;
    float right_q = -left_q;
    float q = round(point.x / W - left_q) + left_q;
    q = clamp(q, left_q, right_q);

    // Distance field is to the nearest hexagonal column
    vec2 column = vec2(q * W, grid_radius * YS);
    vec2 left = column + 0.5 * vec2(-W, radius);
    vec2 right = column + 0.5 * vec2(W, radius);
    vec2 center = column + vec2(0, radius);
    return min(sd_segment(point, left, center),
                  sd_segment(point, right, center));
}

#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

// RayMarching from TheArtOfCode

// From BlackleMori
#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdBox(in vec2 p, in vec2 b){
    vec2 d = abs(p)-b;
    return length(max(d,0.))+min(max(d.x,d.y),0.);
}

vec3 GetRayOrigin() {
    vec2 m = iMouse.xy/iResolution.xy;
    float r = 3.;
    float a = pi/2.;// + cos(iTime) * pi/4.;
    vec3 ro = vec3(r * cos(a), 0, r * sin(a));
    //ro.yz *= rot(-m.y*3.14+1.);
    //ro.xz *= rot(-m.x*6.2831);
    return ro;
}

float GetDist(vec3 p) {
    p.y -= 0.17 + 0.05 * cos(length(p) * 1. - 0.5 * iTime);
    p.y += 0.05 * cos(4. * atan(p.x,p.z) + 4. * length(p.xz) + iTime);
    p.y += 0.01 * thc(3., 32. * log(length(p.xz)));
    float r1 = 1.25 + 0.025 * thc(3., p.y * 2. - 0.5 * iTime);
    float r2 = 0.5 + 0.5 * thc(2., 2. * p.y);
    float d1 = length(p.xz) - r1;
    float d2 = sdBox(vec2(p.y, d1), 
    vec2(2. + 0.5 * cos(16. * log(length(p.xz)) - 2. * iTime),1) * r2) 
    - 0.1;
    return d2;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = GetRayOrigin();

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    vec3 p = ro + rd * d;
    float IOR = 1.05 +0.01 * thc(4., 200. * p.y + iTime);

    if(d<MAX_DIST) {        
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        vec3 lightDir = normalize(vec3(1,2,3));
        float dif = dot(n, lightDir)*.5+.5;
        col = vec3(dif);
       // col = vec3(1);
        float fres = pow(1. + dot(rd, n), 5.);
        float fres2 = pow(1. + dot(rd, nExit), 4.);
        float fog = 1.-exp(-length(p));

        float spec = pow(dif, 15.);
        vec3 col2 = pal(spec, vec3(0,1,2)/3.);
        col = vec3(spec);
        //col = mix(col, vec3(0), 1. - fres);
        col = mix(col, vec3(0), 1. - fres2);
        //col = mix(col, vec3(spec) * col2, 0.5);
        //col = mix(col, 0.5 * col, fog);
        col *= 0.8 + 0.2 * h21(p.xz);
        col += exp(-4. * length(p.xz));
        col *= exp(-1.5 * thc(4., 32. * log(length(p.xz)))-12. * (GetDist(p - 0.2 * lightDir)));
        float v = length(col);
        col *= pal(v * 0.08 + 1., vec3(0,1,2)/3.);
        col *= 0.5 + 0.5 * min(1., exp(-10. * p.y));
        col = pow(col, vec3(.4545));
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

