// "Martian Farming" by dr2 - 2022
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

#define AA  0   // (= 0/1) optional antialiasing

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Hashfv3 (vec3 p);
vec2 Hashv2v2 (vec2 p);
float Fbm1 (float p);
float Fbm2 (vec2 p);
float Fbm2e (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 sunDir, qHit;
vec2 gId, obOff, obRand, obCs;
float tCur, dstFar, rEx, trSzFac, treeHt, grndScl, dSep;
int idObj;
const float pi = 3.1415927, sqrt3 = 1.7320508;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float GrndDf (vec3 p)
{
  vec3 q;
  float d, h, a, r, s, f;
  q = p / grndScl;
  r = length (q.xz);
  d = p.y;
  if (r > 0.) {
    a = atan (q.z, - q.x) / (2. * pi) + 0.5;
    s = sqrt (r) / (2. * pi);
    f = 32.;
    h = mix (Fbm2e (f * vec2 (s, a + 1.)), Fbm2e (f * vec2 (s, a)), a);
    d = max (r - 20., q.y - s * mix (1., 3. * h, smoothstep (1.4, 2.2, r)) * smoothstep (1.4, 2.6, r));
  }
  return grndScl * d;
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = VAR_ZERO; j < 220; j ++) {
    p = ro + s * rd;
    h = GrndDf (p);
    if (h < 0.) break;
    sLo = s;
    s += max (0.001, h);
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = VAR_ZERO; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      if (GrndDf (p) > 0.) sLo = s;
      else sHi = s;
    }
    dHit = 0.5 * (sLo + sHi);
  }
  return dHit;
}

vec3 GrndNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = GrndDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjGdf (vec3 p)
{
  vec3 q;
  float dMin, d, r, y;
  dMin = dstFar;
  if (trSzFac > 0.) {
    dMin /= trSzFac;
    p.xz -= HexToPix (gId) + obOff;
    p /= trSzFac;
    q = p;
    q.xz = Rot2Cs (q.xz, obCs);
    q.y -= treeHt;
    y = 0.5 * (1. + q.y / treeHt);
    r = 0.35 * (1. - 0.5 * obRand.y) * (1. - 0.9 * y * y);
    r *= 1. + 0.1 * sin (24. * (1. + obRand.y) * pi * y);
    r += 0.2 * (1. - smoothstep (0., 0.1, y));
    q.xz += Rot2D (vec2 (0.2 + 0.2 * obRand.x, 0.),
       12. * pi * sign (obRand.x - 0.5) * (0.3 + 0.7 * obRand.y) * y + pi * obRand.x);
    q.x += 0.5 * (0.4 + 0.6 * obRand.y) * y;
    q.xz = abs (q.xz) - 0.3 * r;
    d = PrCylDf (q.xzy, r, treeHt);
    DMINQ (1);
    q = p;
    q.y -= 0.05;
    q.xz += Rot2D (vec2 (0.2 + 0.2 * obRand.x, 0.), pi * obRand.x);
    q.xz = abs (q.xz) - 0.3 * r - 0.4;
    d = max (PrSphDf (q, 0.12), - q.y);
    DMINQ (2);
    dMin *= 0.6 * trSzFac;
  }
  return dMin;
}

void SetObjConf ()
{
  vec2 q;
  float r;
  obRand = Hashv2v2 (13.3 * gId + 1.1);
  q = HexToPix (gId);
  q = Rot2D (q, 2. * pi * floor (5. * (atan (q.y, - q.x) / (2. * pi) + 0.5) + 0.5) / 5.);
  q.x -= dSep * rEx;
  r = length (q) / rEx;
  if (r > 1.) trSzFac = 0.;
  else {
    trSzFac = sqrt (1. - r * r) * 0.5 * sqrt3 *  0.6 * (0.85 -
       0.15 * clamp (3. * (obRand.x - 0.5), -1., 1.));
    if (trSzFac < 0.1) trSzFac = 0.;
    obOff = max (0., 0.4 * sqrt3 - trSzFac) * obRand.x *
       sin (2. * pi * obRand.y + vec2 (0.5 * pi, 0.));
    obCs = sin ((0.4 * obRand.x + 0.2) * (obRand.y - 0.5) * pi * tCur + vec2 (0.5 * pi, 0.));
  }
}

float ObjGRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  eps = 0.0005;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = 1. / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2]));
  pM = HexToPix (PixToHex (ro.xz));
  gIdP = vec2 (-999.);
  dHit = 0.;
  for (int j = VAR_ZERO; j < 220; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = Minv3 (hv);
    p = ro + dHit * rd;
    gId = PixToHex (p.xz);
    if (gId != gIdP) {
      gIdP = gId;
      SetObjConf ();
    }
    d = ObjGdf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFar || p.y < 0.) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjGNf (vec3 p)
{
