}

vec3 DodecSym (vec3 p)
{
  vec2 csD;
  csD = sin (0.5 * atan (2.) + vec2 (0.5 * pi, 0.));
  p.xz = Rot2Cs (vec2 (p.x, abs (p.z)), csD);
  p.xy = Rot2D (p.xy, -0.1 * pi);
  p.x = - abs (p.x);
  for (int k = VAR_ZERO; k <= 3; k ++) {
    p.zy = Rot2Cs (p.zy, vec2 (csD.x, - csD.y));
    p.zy = Rot2Cs (vec2 (p.z, - abs (p.y)), csD);
    if (k < 3) p.xy = Rot2Cs (p.xy, sin (-2. * pi / 5. + vec2 (0.5 * pi, 0.)));
  }
  p.xy = sin (mod (atan (p.x, p.y) + pi / 5., 2. * pi / 5.) - pi / 5. +
     vec2 (0., 0.5 * pi)) * length (p.xy);
  p.xz = - vec2 (abs (p.x), p.z);
  return p;
}

float ObjDf (vec3 p)
{ // based on "Pentakis Dodecahedron"
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  p.xz = Rot2D (p.xz, 2. * pi * floor (5. * (atan (p.z, - p.x) / (2. * pi) + 0.5) + 0.5) / 5.);
  p.x -= dSep * rEx;
  q = DodecSym (p);
  d = SmoothMax (abs (length (p) - rEx) - 0.1, min (dot (q.yz,
     sin (0.5 * acos (-1. / sqrt (5.)) - pi + vec2 (0., 0.5 * pi))),
     dot (q.xy, sin (pi / 5. + vec2 (0.5 * pi, 0.)))) - 0.1, 0.05);
  d = max (d, - p.y);
  DMIN (3);
  q = p;
  d = length (max (abs (vec2 (length (q.xz) - rEx, q.y)) - vec2 (0.05, 0.1), 0.)) - 0.05;
  DMIN (3);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.5 + 0.5 * sh;
}

float TrObjDf (vec3 p)
{
  p.xz = Rot2D (p.xz, 2. * pi * floor (5. * (atan (p.z, - p.x) / (2. * pi) + 0.5) + 0.5) / 5.);
  p.x -= dSep * rEx;
  return max (abs (PrSphDf (p, rEx)) - 0.01, - p.y);
}

float TrObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 60; j ++) {
    d = TrObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 TrObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = TrObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 GrndCol (vec3 ro, vec3 vn)
{
  vec4 col4;
  col4 = vec4 (0.65, 0.5, 0.3, 0.) * (1. - 0.4 * Fbm2e (0.5 * ro.xz));
  if (ro.y > -0.1) {
    col4 = mix (col4, 0.8 * col4, smoothstep (0.2, 0.7, 1. - vn.y));
    col4 = mix (col4, 1.2 * col4, smoothstep (0.6, 0.8, ro.y / grndScl +
       0.2 * sin (32. * atan (ro.z, - ro.x))));
  }
  return col4;
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float f, fd, ff;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (8. * atan (rd.z, - rd.x))) {
    col = vec3 (0.4, 0.3, 0.3);
  } else {
    q = 0.005 * (ro.xz + 20. * tCur + ((200. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.2, 0.3, 0.3) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    col = vec3 (1., 0.8, 0.8) * mix (vec3 (0.6, 0.4, 0.4), clCol,
       0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col;
}

float PerBumpf (float p)
{
  return 0.5 * smoothstep (0., 0.5, abs (fract (p) - 0.5));
}

vec3 PerBumpv3 (vec3 p)
{
  return 0.5 * smoothstep (0., 0.5, abs (fract (p) - 0.5));
}

vec3 noiseDisp;
float fogAmp;

float BumpFbm3 (vec3 p)
{  // from "Energy Temple"
  vec3 q;
  float a, f;
  a = 1.;
  f = 0.;
  q = p;
  for (int j = 0; j < 4; j ++) {
    p += noiseDisp;
    p += PerBumpv3 (q + PerBumpv3 (q).yzx);
    p *= 1.5;
    f += a * (PerBumpf (p.z + PerBumpf (p.x + PerBumpf (p.y))));
    q = 2. * q + 0.5;
    a *= 0.75;
  }
  return f;
}

float FogDens (vec3 p)
{
  return fogAmp * BumpFbm3 (0.02 * p);
}

vec3 FogCol (vec3 col, vec3 ro, vec3 rd, float dHit)
{  // updated from "Sailing Home"
  float s, ds, f, fn;
  noiseDisp = 0.1 * vec3 (-1., 0., 1.) * tCur + 0.02 * sin (vec3 (0.3, 0.2, 0.3) * pi * tCur);
  s = 5.;
  ds = 5.;
  fn = FogDens (ro + s * rd);
  for (int j = VAR_ZERO; j < 30; j ++) {
    s += ds;
    f = fn;
    fn = FogDens (ro + (s + 0.5 * ds * Hashfv3 (16. * rd)) * rd);
    col = mix (col, vec3 (0.95, 0.8, 0.5) * (1. - clamp (f - fn, 0., 1.)),
       min (f * (1. - smoothstep (0.3 * dHit, dHit, s)), 1.));
    if (s > dHit) break;
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, roo, qHitG;
  vec2 vf, q;
  float dstObj, dstObjG, dstGrnd, dstTrObj, sh, f, a;
  int idObjG;
  fogAmp = 0.25 * SmoothBump (0.25, 0.75, 0.22, fract (0.03 * tCur));
  rEx = 8.;
  roo = ro;
  vf = vec2 (0.);
  dstObjG = ObjGRay (ro, rd);
  idObjG = idObj;
  qHitG = qHit;
  dstObj = ObjRay (ro, rd);
  dstGrnd = GrndRay (ro, rd);
  if (min (min (dstObj, dstObjG), dstGrnd) < dstFar) {
    if (dstObjG < min (dstObj, dstGrnd)) {
      dstObj = dstObjG;
      idObj = idObjG;
      qHit = qHitG;
      ro += dstObj * rd;
      gId = PixToHex (ro.xz);
      vn = ObjGNf (ro);
      a = atan (qHit.z, - qHit.x) / pi;
      if (idObj == 1) {
        f = smoothstep (-0.9, -0.7, qHit.y / treeHt);
        col4 = mix ((1. - 0.6 * obRand.x) * vec4 (0.6, 0.4, 0., 0.),
           vec4 (0., 0.4 + 0.6 * obRand.y, 0.1, 0.05), f) *
           (0.8 + 0.2 * SmoothBump (0.1, 0.9, 0.05, fract (8. * a)));
        vf = vec2 (16., (3. - 2.5 * f) * (1. - smoothstep (0.1, 0.3, dstObj / dstFar)));
      } else if (idObj == 2) {
        col4 = mix (vec4 (0.8, 0.1, 0., 0.3), vec4 (0.7, 0.7, 0., 0.3), step (0.6, obRand.y)) *
           (0.5 + 0.5 * SmoothBump (0.1, 0.9, 0.05, fract (4. * a)));
        vf = vec2 (32., 1.);
      }
    } else if (dstObj < dstGrnd) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      if (idObj == 3) col4 = vec4 (0.9, 0.9, 0.95, 0.2);
    } else {
      ro += dstGrnd * rd;
      q = Rot2D (ro.xz, 2. * pi * floor (5. * (atan (ro.z, - ro.x) / (2. * pi) + 0.5) + 0.5) / 5.);
      q.x -= dSep * rEx;
      if (length (q) > rEx) {
        vn = GrndNf (ro);
        col4 = GrndCol (ro, vn);
        vf = vec2 (0.5, 2. - smoothstep (0.7, 0.9, dstGrnd / (3. * grndScl)));
      } else {
        col4 = vec4 (0.4, 0.6, 0.3, 0.);
        vn = vec3 (0., 1., 0.);
        vf = vec2 (16., 0.2);
      }
    }
    sh = min (ObjSShadow (ro + 0.01 * vn, sunDir), ObjGSShadow (ro + 0.01 * vn, sunDir));
    sh = mix (sh, 1., smoothstep (0., 1., fogAmp / 0.25));
    if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
    col = col4.rgb * (0.2 + 0.1 * max (vn.y, 0.) + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
       col4.a * step (0.95, sh) * pow (max (dot (sunDir, reflect (rd, vn)), 0.), 32.);
  } else col = SkyBgCol (ro, rd);
  ro = roo;
  dstTrObj = TrObjRay (ro, rd);
  if (dstTrObj < min (dstObj, dstFar)) {
    ro += dstTrObj * rd;
    vn = TrObjNf (ro);
    col = mix (col * vec3 (1., 1., 0.7), SkyBgCol (ro, reflect (rd, vn)),
       0.2 + 0.7 * pow (1. - max (- dot (rd, vn), 0.), 3.));
  }
  col = FogCol (col, roo, rd, min (dstObj, dstGrnd));
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr, t;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  treeHt = 7.;
  dSep = 3.;
  az = 0.;
  el = -0.05 * pi;
  if (mPtr.z > 0.) {
    az -= 2. * pi * mPtr.x;
    el -= 0.5 * pi * mPtr.y;
  } else {
    t = mod (0.005 * tCur, 2.);
    az = 2.5 * pi * (0.5 - abs ((floor (16. * t) + smoothstep (0.8, 1., fract (16. * t))) / 16. - 1.));
  }
  el = clamp (el, -0.45 * pi, -0.02 * pi);
  vuMat = StdVuMat (el, az);
  grndScl = 50.;
  ro = vec3 (0., 0.05, -1.7 + 0.5 * abs (mod (az / pi + 1., 2.) - 1.)) * grndScl;
  ro = vuMat * ro;
  zmFac = 3.5;
  dstFar = 10. * grndScl;
  sunDir = normalize (vec3 (0., 1., 1.));
 #if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2e (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 6; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.96875);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.05, 0.); //vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

