        al *= 1.0-0.95*p.x;
        al *= demo;
        col = mix( col, vec3(0.85), al);
    }

    // draw helper curve
    if( n>0 )
    {
        float d = 1e20;
        for( int i=-2; i<=2; i++ )
        {
            float x0 = p.x + px*(float(i+0));
            float x1 = p.x + px*(float(i+1));
            float y0 = 0.25*helper( x0, n );
            float y1 = 0.25*helper( x1, n );
            d = min( d, sdLine(p,vec2(x0,y0),vec2(x1,y1) ) );
        }
        float al = 1.0-smoothstep(0.0,2.0*px,d);
        al *= demo;
        col = mix( col, vec3(0.5), al );
    }

    // draw Mega Polynomial
    {
        vec4 min_dis_col = vec4(1e20,0.0,0.0,0.0);
        for( int i=-2; i<=2; i++ )
        {
            int e = 0;
            int f = 0;
            float x0 = p.x + px*(float(i+0));
            float x1 = p.x + px*(float(i+1));
            float y0 = 0.25*mp( x0, e );
            float y1 = 0.25*mp( x1, f );
            float d = sdLine(p,vec2(x0,y0),vec2(x1,y1) );

            if( e==n ) d -= 4.0*px*demo; // inflate current segment

            if( d < min_dis_col.x )
            {
                vec3 tmp = mix( vec3(1.0), 0.5+0.5*cos(float(e)+vec3(0,2,4)), demo );
                min_dis_col = vec4(d,tmp);
            }
        }
        col = mix( col, min_dis_col.yzw, 1.0-smoothstep(1.0*px,3.0*px,min_dis_col.x) );
    }

    fragColor = vec4( col, 1.0 );
}

// 'Dune Ornithopter' dean_the_coder (Twitter: @deanthecoder)
// https://www.shadertoy.com/view/NltBRs (YouTube: https://youtu.be/01X29MpidM8)
//
// Processed by 'GLSL Shader Shrinker'
// (https://github.com/deanthecoder/GLSLShaderShrinker)
//
// I fancied modelling the Dune ornithopter. :)
// Apologies if performance is a problem - This one is a bit GPU-hungry.
//
// Tricks to aid performance:
//   - Precalculate function results, rotation matrices, and simplify calculations
//     when possible (see GLSL Shader Shrinker).
//   - The 'copter SDF is not calculated when the ray is too far away.
//   - Shadows not calculated if surface normal points away from the sun.
//   - Only the ground and sky are reflected.
//
// Thanks to Evvvvil, Flopine, Nusan, BigWings, Iq, Shane,
// totetmatt, Blackle, Dave Hoskins, byt3_m3chanic, tater,
// and a bunch of others for sharing their time and knowledge!
