#define R	iResolution
#define Z0	min(iTime, 0.)
#define I0	min(iFrame, 0)
#define sat(x)	clamp(x, 0., 1.)
#define S(a, b, c)	smoothstep(a, b, c)
#define S01(a)	S(0., 1., a)

/// Global time.
float gt, WING_SPEED, fade = 1.;
struct Hit {
	float d;
	int id;
	vec3 p;
};

void U(inout Hit h, float d, int id, vec3 p) { if (d < h.d) h = Hit(d, id, p); }

void UH(inout Hit h, Hit h2) { if (h2.d < h.d) h = h2; }

float min2(vec2 v) { return min(v.x, v.y); }

float max2(vec2 v) { return max(v.x, v.y); }

float max3(vec3 v) { return max(v.x, max(v.y, v.z)); }

float sum2(vec2 v) { return dot(v, vec2(1)); }

float mul3(vec3 v) { return v.x * v.y * v.z; }

float h31(vec3 p3) {
	p3 = fract(p3 * .1031);
	p3 += dot(p3, p3.yzx + 333.3456);
	return fract(sum2(p3.xy) * p3.z);
}

float h21(vec2 p) { return h31(p.xyx); }

float n31(vec3 p) {
	const vec3 s = vec3(7, 157, 113);

	// Thanks Shane - https://www.shadertoy.com/view/lstGRB
	vec3 ip = floor(p);
	p = fract(p);
	p = p * p * (3. - 2. * p);
	vec4 h = vec4(0, s.yz, sum2(s.yz)) + dot(ip, s);
	h = mix(fract(sin(h) * 43758.545), fract(sin(h + s.x) * 43758.545), p.x);
	h.xy = mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

// Two n31 results from two scales.
vec2 n331(vec3 p) {
	return vec2(n31(p * 20.), n31(p * 38.));
}

float n21(vec2 p) { return n31(vec3(p, 1)); }

float smin(float a, float b) {
	float h = sat(.5 + .5 * (b - a) / -.05);
	return mix(b, a, h) + .05 * h * (1. - h);
}

float remap(float f, float in1, float in2) { return sat((f - in1) / (in2 - in1)); }

// Dip below 0, overshoot 1, back down to 1.
float backInOut(float x) {
	float f = x < .5 ? 2. * x : 1. - (2. * x - 1.),
	      g = pow(f, 3.) - f * sin(f * 3.141);
	return x < .5 ? .5 * g : .5 * (1. - g) + .5;
}

// Ray/plane intersection.
bool intPlane(vec3 p0, vec3 n, vec3 ro, vec3 rd, out float t) {
	float denom = dot(n, rd);
	t = dot(p0 - ro, n) / denom;
	return t >= 0. && abs(denom) > 1e-4;
}

mat2 rot(float a) {
	float c = cos(a),
	      s = sin(a);
	return mat2(c, s, -s, c);
}

float box(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return length(max(q, 0.)) + min(max3(q), 0.);
}

float cap(vec3 p, float h, float r) {
	p.x -= clamp(p.x, 0., h);
	return length(p) - r;
}

float honk(inout vec3 p, mat2 rot, vec2 r) {
	p.xy *= rot;
	float d = cap(p, r.x, r.y);
	p.x -= r.x;
	return d;
}

float oct3D(vec3 p, float r, float h) {
	const vec3 k = vec3(-.9239, .3827, .4142);
	p = abs(p);
	p.xy -= 2. * min(dot(k.xy, p.xy), 0.) * k.xy;
	p.xy -= 2. * min(dot(vec2(-k.x, k.y), p.xy), 0.) * vec2(-k.x, k.y);
	p.xy -= vec2(clamp(p.x, -k.z * r, k.z * r), r);
	vec2 d = vec2(length(p.xy) * sign(p.y), p.z - h);
	return min(max2(d), 0.) + length(max(d, 0.));
}

vec3 rayDir(vec3 ro, vec2 uv) {
	vec3 f = normalize(-ro),
	     r = normalize(cross(vec3(0, 1, 0), f));
	return normalize(f + r * uv.x + cross(f, r) * uv.y);
}

vec3 sky(vec3 rd) { return mix(mix(vec3(.74, .61, .52), vec3(.85, .81, .74), S(-.2, 0., rd.y)), vec3(1.54, 1.28, 1.23), S(.4, 1., dot(rd, vec3(-.33, .67, .67)))); }

// Shameless self-promotion. :)
float dtc(vec2 p, float fl) {
	p += vec2(.8, .9);
	p.y += p.x;
	p.x *= fl;
	p *= vec2(2.5, 3);
	if (abs(p.x) > .6 || abs(p.y) > .5) return 0.;
	if (step(min2(abs(p - vec2(0, .2))), .08) * step(p.y, .3) * step(abs(p.x), .4) > 0.) return 1.4;
	float f,
	      dc = step(.5, p.x);
	p.x = abs(p.x) - .46;
	f = dot(p, p);
	dc += step(f, .25) * step(.16, f);
	return dc > 0. ? .8 : 0.;
}

vec2 dockFeet;
Hit wing(vec3 p, float ti, float isTop, float isBack) {
	p.y += .1 * (isTop * 2. - 1.) * (1. - isBack);
	p.xz *= rot(isBack * .1);
	if (isTop > 0.) {
		p.xy *= mat2(.995, .09983, -.09983, .995);
		ti += .2;
	}

	// Wing dock and spin.
	p.xz *= rot(mix(1.25, 1.4, isBack) * dockFeet.x);
	p.yz *= rot(dockFeet.x * 5.2);
	float a, f, d, thk,
	      l = length(p);
	Hit h = Hit(max(l - .35, p.x - .1), 3, p); // Ball socket. // Wing time.
	a = sin(3.141 * isBack + ti * WING_SPEED * 70. - abs(p.x) / 34.78261); // Pivot wing, and bend ends.
	f = max(sin(p.x * 70.), 0.) * .01; // Ridges.
	d = l - .3; // Ball joint.
	a *= S(12., 12.5, gt); // Fade in initial wing movement.
	d = min(d, honk(p, rot(a * .05), vec2(2, .16 - f))); // Wing rod.
	p.x -= 7.9;
	p.z -= .18;

	// Wing.
	thk = 1.01 - WING_SPEED;
	vec3 v = vec3(8, .04 * thk, .4);
	v.z -= .35 * sat(-6. - p.x) * sat(sign(p.z));
	d = min(d, box(p, v - (.03 - .05 * S(-.35, -.15, p.z)) * thk));
	U(h, d, 3, p);
	return h;
}

Hit wings(vec3 p, float ti) {
	Hit h;
	h.d = 1e7;
	p.x--;
	p.y -= .1;
	vec3 dyz = vec3(0, .8, .8);
	float b = 1.;
	for (float i = Z0; i < 2.; i++) {
		UH(h, wing(p - dyz * vec3(1, 1, -1), ti, b, 1. - b));
		UH(h, wing(p - dyz, ti, b, b));
		dyz = -dyz;
		b = 1. - b;
	}

	return h;
}

vec3 rot2(vec3 p, float r) {
	p.xz *= rot(1.57079);
	p.yz *= rot(r);
	return p;
}

// Copter pitch, height, distance.
vec3 cop;

vec3 copterPos(vec3 p) {
	p.yz *= rot(cop.x);
	return p;
}

// 'smooth' abs().
float sabs(float f) { return sqrt(f * f + 3e-4); }

Hit gnd(vec3 p) {
	p.y += cop.y;
	p.z -= cop.z;
	float h1,
	      lz = S(0., 90., length(p.xz));
	p.y += 1.77;
	vec3 pp = p;
	p.x += sin(p.z * .02 + 1.4 * n21(p.zx * .05)) * 15.;

	// Dunes.
	h1 = (1. - sabs(sin(p.x * .03))) * 12.;

	// Sand bumps.
	return Hit((p.y - mix(max(-.7, sin(p.x * 10. + sin(p.z + p.y))) * .02 * (1. - lz), h1, lz * lz)) * .9, 4, pp);
}

Hit map(vec3 p) {
	p = p.zyx;
	Hit h = gnd(p);

	// Optimization - Not close enough to the 'copter to bother including it.
	if (abs(p.z) > 52.) return h;

	p = copterPos(p);
	p.x = abs(p.x);
	vec3 q,
	     op = p;

	// Legs.
	p += vec3(-.3, .8, .8);
    mat2 m = mat2(.87758, .47943, -.47943, .87758);
	p.xy *= m;
	float f,
	      d = honk(p.zyx, rot(-dockFeet.y * .5), vec2(1, .1));
	d = min(d, honk(p.zyx, rot(3.4 + dockFeet.y * .8), vec2(1, .1)));
	p.yz *= rot(.25 + .31 * S01(dockFeet.y));
	p.xy *= m;
	d = min(d, box(p + vec3(0, -.05 * S(.2, .4, abs(p.z)), 0), vec3(.14, .05, .4)));
	p.z -= .3;
	p.y += (S(1., 1.3, abs(p.z)) - 1.) * .05;
	d = min(d, box(p, vec3(.2, .03, 1.2)) - .02);
	U(h, d, 3, p);
	p = op;

	// Tail.
	d = oct3D(p.xzy - vec3(0, 14.8, .4), 1., .3 - .2 * p.x);

	// Fuselage.
	p.z += 4.2;
	f = S(5., 10., p.z);
	p.y = abs(p.y - .1 - .3 * f) + .4;
	d = min(d, oct3D(p - vec3(0, 0, 10), 1.3 - f * .6, 10.));

	// Cockpit.
	p = op;
	p.y -= 4.4;
	p.yz *= mat2(.97367, -.22798, .22798, .97367);
	p.z += 4.;
	d = max(d, p.x - p.z * .4 - .6);
	d = max(d, -p.z - 1.18);
	q = p;
	q.y += .3 * abs(sat(p.x - .28 - .14 * p.z));

	// Windows.
	f = S(.06, 0., abs(q.z) - .9) * S(.52, .46, q.x - q.z * .32);
	f *= step(-3., q.y);

	// Nose top slope.
	d = max(d, f * .015 - oct3D(q.zyx, 3., 2.));

	// Nose window side cut-outs.
	p += vec3(-3.02, 3.5, 1.28);
	p.y -= max(0., p.z - 1.5) * S(.2, 0., p.y);
	p.yz *= mat2(.99755, .06994, -.06994, .99755);
	p.xz *= mat2(.90045, .43497, -.43497, .90045);
	d = smin(d, -oct3D(p.xzy, 3., .65) - .2);

	// Glass.
	f += step(-.4, p.y) * mul3(step(p, vec3(p.z * 1.02 - 3.9, .43, 2.8)));
	U(h, d - .05, 3 - 2 * int(step(1., f)), p);

	// Wings.
	UH(h, wings(op, gt));
	return h;
}

vec3 N(vec3 p, float t) {
	float h = t * .1;
	vec3 n = vec3(0);
	for (int i = I0; i < 4; i++) {
		vec3 e = .005773 * (2. * vec3(((i + 3) >> 1) & 1, (i >> 1) & 1, i & 1) - 1.);
		n += e * map(p + e * h).d;
	}

	return normalize(n);
}

float shadow(vec3 p, vec3 ld, vec3 n) {
	// Quick abort if light is behind the normal.
	if (dot(ld, n) < -.1) return 0.;
	float d,
	      s = 1.,
	      t = .01;
	for (float i = Z0; i < 47.; i++) {
		d = map(t * ld + p).d;
		s = min(s, 25. * d / t);
		t += max(.05, d);
		if (70. - t < .5 || s < .01) break;
	}

	return S01(s);
}

// Quick 2-level ambient occlusion.
float ao(vec3 p, vec3 n, vec2 h, float wingDock) {
	float od = dockFeet.x;
	dockFeet.x = wingDock;
	vec2 ao;
	for (int i = I0; i < 2; i++)
		ao[i] = map(h[i] * n + p).d;

	dockFeet.x = od;
	return sat(min2(ao / h));
}

float fog(float d) { return S(0., -80., d - 4e2); }

vec3 lights(vec3 p, vec3 rd, vec3 n, Hit h) {
	const vec3 ld = vec3(-.33333, .66667, .66667);
	float fre,
	      spe = 10.,
	      shine = 1.;
	vec3 c, l, sc;
	if (h.id == 4) {
		// Sand.
		c = vec3(.45, .35, .23);
		spe = .1;
		vec2 uv = h.p.xz * 1e2;
		shine = .05 + .1 * S(h21(floor(uv)), 0., length(fract(uv) - .5));
		float dust = S(1., 0., abs(gt - 21.) / 7.);
		if (dust > 0.) {
			float l = length(p.xz);
			dust *= S(15., 0., abs(l - 9.));
			dust *= n21(vec2(atan(p.x, p.z), l - gt * 20.) * vec2(12, 2));
			c = mix(c, vec3(.63, .49, .322), dust);
		}
	}
	else if (h.id == 3) {
		// 'copter.
		vec2 ns = n331(h.p);
		shine = 1.2 - sum2(ns) * .3;
		shine += dtc(h.p.xy, sign(p.z));
		c = vec3(.0185, .0165, .0125) * shine;
		float sand = S(.9, 0., ao(p, n, vec2(.2, 1.3), 0.) - ns.x * .45);
		sand *= .5 + .5 * ns[1];
		c = mix(c, vec3(.45, .35, .23), sand * .16);
	}
	else {
		// Window.
		c = vec3(.01);
		shine = 10.;
		spe = 20.;
	}

	l = sat(vec3(dot(ld, n),  // Key light.
                 sat(-n.y),  // Reverse light.
                 sat(n.y) // Sky light.
	       ));
	l.xy = .1 + .9 * l.xy; // Diffuse.
	l.yz *= .1 + .9 * ao(p, n, vec2(.2, 1), dockFeet.x); // Ambient occlusion.
	l *= vec3(1.1, .25, .3); // Light contributions (key, reverse, sky).
	l.x += pow(sat(dot(normalize(ld - rd), n)), spe) * shine; // Specular (Blinn-Phong)
	l.x *= .01 + .99 * shadow(p, ld, n); // Shadow.
	fre = S(.7, 1., 1. + dot(rd, n)) * .05;
	sc = sky(rd);
	return mix((sum2(l.xy) * vec3(2.4, 2, 1.92) + l.z * sc) * c, sc, fre);
}

float addFade(float a) { return min(1., abs(gt - a)); }

vec3 scene(vec3 ro, vec3 rd) {
	fade = addFade(0.) * addFade(20.);

	// March the scene.
	vec3 n, col,
	     p = ro;
	float i,
	      d = 1.;
	Hit h;
	for (i = Z0; i < 150.; i++) {
		h = map(p);

		if (abs(h.d) < .001 * d || d > 4e2) break;
		d += h.d;
		p += h.d * rd;
	}

	n = N(p, d);
	col = mix(sky(rd), lights(p, rd, n, h), fog(d));

	// Wing blur.
	float blurVis = S(.4, .7, WING_SPEED);
	if (blurVis > 0.) {
		const mat2 m1 = mat2(.63268e-5, 1, -1, .63268e-5);
		float t,
		      ws = gt * WING_SPEED;
		mat2 m2 = rot(-cop.x);
		vec3 pn = vec3(0, 0, 1);
		pn.yz *= m2;
		pn.xz *= m1;
		for (float dz = -1.; dz <= 1.; dz += 2.) {
			vec3 copP = vec3(0, 0, .8 * dz);
			copP.yz *= m2;
			copP.xz *= m1;
			for (float dy = -1.; dy <= 1.; dy += 2.) {
				if (intPlane(copP, pn, ro, rd, t) && t < d) {
					p = rot2(rd * t + ro, cop.x);
					p.x = abs(p.x);
					p.xy *= rot(.1 * sat(-dy));
					p.y += .8 * dy + sat(-dy) * .3;
					float tint = sin(p.y * 120. / p.x + ws * 10.);
					tint *= sin(p.y * 80. / p.x - ws * 20.);
					tint = sat(tint * .5 + .9);
					tint *= S(1.5, 2., p.x);
					tint *= S(6., -5., p.x - 16.);
					tint *= S(p.x * .15, 0., abs(p.y));
					col = mix(col, vec3(.02, .03, .01), tint * .4 * blurVis);
				}
			}
		}
	}

	if (h.id < 2) {
		// We hit a reflective surface, so march reflection.
		rd = reflect(rd, n);
		p += n * .01;
		d = .01;
		for (i = Z0; i < 32.; i++) {
			h = gnd(p.zyx);
			if (abs(h.d) < .0015 * d || d > 10.) break;
			d += h.d;
			p += h.d * rd;
		}

		// Add a hint of the reflected color.
		vec3 sc = sky(rd);
		col += .1 * (d > 10. ? sc : mix(sc, lights(p, rd, N(p, d), h), fog(d)));
	}

    // Gamma.
	return pow(max(vec3(0), col), vec3(.4545));
}

#define rgba(col)	vec4(col * fade, 0)

void mainImage(out vec4 fragColor, vec2 fc) {
	gt = mod(iTime, 60.);
	vec2 uv = (fc - .5 * R.xy) / R.y;

	// Camera.
	float fly, x,
	      camDrift = remap(gt, 30., 34.);
	vec3 ro, col,
	     cam = mix(vec3(0, .30, .125), vec3(.44, .58, .32), S01(remap(gt, 0., 10.)));
	cam = mix(mix(mix(mix(cam, vec3(.37, .78, .44), S01(remap(gt, 9., 20.))), vec3(.46, .5, .19), S01(remap(gt, 20., 20.01))), vec3(.35, .53, .3), S01(remap(gt, 20.01, 25.))), vec3(.8, .75, 1.25), S01(remap(gt, 31., 33.)));
	WING_SPEED = pow(ceil(pow(remap(gt, 12., 18.), 2.) * 4.) / 4., 2.);

	// Movement.
	fly = remap(gt, 28., 30.);
	x = max(0., gt - 29.) * fly;
	cop.z = x * (1. - exp(-.1 * x)) * 250.;
	cop.y = 8. * sin(x * 1.5) * camDrift;
	cop.y += 8. + fly * 22.;
	cop.y *= S01(remap(gt, 24., 27.));
	cop.x = (cop.x / -360. - backInOut(fly)) * .2;
	dockFeet = vec2(S(9., 5., gt), remap(gt, 29., 25.));

	// Camera drift.
	cam += vec3(sin(gt * .4) * .05 - .05 - sin(gt * .1), sin(gt * .6) * .05, sin(gt * .5) * .8) * camDrift;

	// Scene.
	ro = vec3(0, 0, -35. * cam.z);
	ro.yz *= rot(1. - cam.y * 2.);
	ro.xz *= rot(cam.x * -6. - 3.);
	col = scene(ro, rayDir(ro, uv));

	// Vignette.
	col *= 1. - .3 * dot(uv, uv);

	// Grain.
	col += (h21(fc) - .5) / 32.;

	fragColor = rgba(col);
}

