#define ANTIALIASING

//vec3 camPosition;
vec3 cameraForward;

struct RayHit
{
    vec3 ro;
    vec3 rd;

    vec3 pos;
    float dist;

    vec3 normal;
    vec3 reflDir;
    vec3 refractDir;
    int steps;
    bool hasHit;
    vec3 instanceID;
    float closestDist;

    float totalDist;
};

vec3 GetObjectColor(vec3 instanceID)
{
    float hue = 37. * instanceID.x + 43. * instanceID.y + 29. * instanceID.z;
    return HSVtoColor(hue, 1., 1.);
}

vec3 GetObjectColor(RayHit hit)
{
    return GetObjectColor(hit.instanceID);
}


vec3 GetInstanceID(vec3 p)
{
    return floor((p + STEPS/2.) / STEPS);
}

vec3 DistortTimeAndSpace(vec3 p)
{
    p.y += 5.5 * sin(p.z * .15); 
    p.x += 5.5 * cos(p.z * .15);

    mat2 r2 = Rot(p.z * .0);
    p.xy *= r2;

    return p;
}

float GetDist(vec3 p)
{
    p = DistortTimeAndSpace(p);
    vec3 ids = GetInstanceID(p);

    p = opRep(p, vec3(STEPS));

    //float d = sdSphere(p, 0.15 + 0.05 * sin(ids.z * .7 + ids.x * 0.5));
    float d = sdSphere(p, 0.1);

    return d;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = GetDist(p) - 
        vec3(GetDist(p-e.xyy), GetDist(p-e.yxy),GetDist(p-e.yyx));

    return normalize(n);
}


RayHit CalculateRayHit(vec3 ro, vec3 rd, float dist, bool hasHit, int steps, float closestDist)
{
    RayHit hit;
    hit.ro = ro;
    hit.rd = rd;
    hit.dist = dist;
    hit.pos = ro + rd * dist;
    hit.normal = GetNormal(hit.pos);
    hit.reflDir = reflect(rd, hit.normal);
    hit.refractDir = refract(rd, hit.normal, IOR);
    hit.steps = steps;
    hit.closestDist = closestDist;

    hit.hasHit = hasHit;
    hit.instanceID = GetInstanceID(DistortTimeAndSpace(hit.pos));
    return hit;
}



RayHit RayMarch(vec3 ro, vec3 rd) {
	float dist = 0.;
    bool hasHit = false;

    int steps = 0;
    float closestDist = MAX_DIST;
    for(int i = 0; i < MAX_STEPS; i++)
    {
    	vec3 p = ro + rd * dist;
        float dS = GetDist(p);
        dist += dS;

        closestDist = min(closestDist, dS);

        steps = i;

        if(dist > MAX_DIST) break;

        if(abs(dS) < SURF_DIST)
        {
            hasHit = true;
            break;
        }
    }

    return CalculateRayHit(ro, rd, dist, hasHit, steps, closestDist);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) 
{ 
    vec3 f = normalize(l-p);
    vec3 r = normalize(cross(vec3(0,1,0), f));
    vec3 u = cross(f,r);
    vec3 c = f*z;
    vec3 i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

vec3 GetColor(RayHit hit)
{
    vec3 col;
    vec3 highLight;

    if(hit.hasHit) 
    {
        float fresnel = saturate(dot(-hit.rd, hit.normal));
        fresnel = 1. - pow(fresnel, 1.);

        col = GetObjectColor(hit);

        col += fresnel;

        vec3 lightDir = cameraForward;

        float diffuse = 2. * saturate(dot(hit.normal, lightDir));

        float specularStrength = 1.5;
        float spec = pow(max(dot(hit.rd, hit.reflDir), 0.0), 32.);
        vec3 specular = specularStrength * spec * vec3(1);  

        col *= diffuse + specular;
        col *= saturate(0.5 - saturate(hit.totalDist / (MAX_DIST * 0.3)));
        //col = pow(col, 5.);
    }


    //highLight = vec3(1.0) * saturate(hit.closestDist * 1.);


    float highlightRatio = saturate(float(hit.steps) / 150.);
    highlightRatio = highlightRatio * highlightRatio;
    vec3 fog = GetCol(109, 143, 163);
    //vec3 fog =  vec3(0.0);

    return mix(col, fog, highlightRatio) + highLight; 
}

void PerspectiveCam(vec3 camPos, vec4 camRot, vec2 uv, float fov, float aspectRatio, out vec3 ro, out vec3 rd)
{

    //float imageAspectRatio = resolution.x / resolution.y;  //assuming width > height
    uv.x = uv.x * tan(fov / 2. * PI / 180.) * aspectRatio;
    uv.y = uv.y * tan(fov / 2. * PI / 180.);

    //Add FOV
    vec3 forward = normalize(vec3(uv.x, uv.y, 1.));
    ro = camPos;
    rd = qmul(camRot, forward);
}

void OrthographicCam(vec3 camPos, vec4 camRot, vec2 uv, float size, float aspectRatio, out vec3 ro, out vec3 rd)
{
    //float imageAspectRatio = resolution.x / resolution.y;  //assuming width > height
    uv.x = uv.x * aspectRatio;
    uv.y = uv.y;

    //Add FOV
    //float3 forward = normalize(float3(uv.x, uv.y, 1.0));
    ro = camPos + (uv.x * qmul(camRot, vec3(1, 0, 0)) * size) + (uv.y * qmul(camRot, vec3(0, 1, 0)) * size);
    rd = qmul(camRot, vec3(0, 0, 1));
}



vec3 GetColor(vec3 camPos, vec4 camRot, vec2 uv)
{
    //vec3 ro1, rd1;
    //vec3 ro2, rd2;

    float aspectRatio = iResolution.x / iResolution.y;
    //PerspectiveCam(camPos, camRot, uv, aspectRatio, ro1, rd1);
    //OrthographicCam(camPos, camRot, uv, 5., aspectRatio, ro2, rd2);

    //vec3 ro = mix(ro1, ro2, 0.0);
   // vec3 rd = mix(rd1, rd2, 0.0);
    vec3 ro, rd;
    const float fov = 40.;
    PerspectiveCam(camPos, camRot, uv, fov, aspectRatio, ro, rd);


    //vec3 fpos = DistortTimeAndSpace(vec3(0.0, 0.0, 0.75 * iTime));
    //vec3 ro = vec3(-fpos.x - STEPS/2., -fpos.y - STEPS/2., fpos.z);
    //camPosition = ro;

    //vec3 rd = normalize(vec3(uv.x, uv.y, 1.));

    const int reflections = 5;
    int lastReflectionId = 0;
    vec3 hitColors[reflections];

    float totalDist = 0.;
    RayHit hit;
    for(int i = 0; i < reflections; i++)
    {
        hit = RayMarch(ro, rd);

        if(hit.hasHit)
        {
            totalDist += hit.dist;
            hit.totalDist = totalDist;
            hitColors[i] = GetColor(hit);

            lastReflectionId = i;
            ro = hit.pos + hit.normal * (SURF_DIST + 0.01);
            rd = hit.reflDir;
        }
        else
        {

            break;
        }
    }

    vec3 col = hitColors[lastReflectionId];
    for (int i = lastReflectionId - 1; i >= 0; i--)
    {
        col = mix(hitColors[i], col, 0.2);
    }
    return col;
}

vec3 FixPos(vec3 pos)
{
    pos = DistortTimeAndSpace(pos);
    return vec3(-pos.x + STEPS/2., -pos.y + STEPS/2. , pos.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 uv = (fragCoord.xy / iResolution.xy) - vec2(0.5);


    float speed = 1.5;
    //float speed = 1.5;

   // vec3 fpos = DistortTimeAndSpace(vec3(0.0, 0.0,speed * iTime));

    vec3 fposPrev1 = FixPos(vec3(0.0, 0.0, speed * iTime + 1.5));
    vec3 fposPrev2 = FixPos(vec3(0.0, 0.0, speed * iTime + 1.4));
    cameraForward = normalize(fposPrev1 - fposPrev2);

    //vec3 camPos = vec3(-fpos.x + STEPS/2., -fpos.y + STEPS/2. , fpos.z);
    vec3 camPos = FixPos(vec3(0.0, 0.0, speed * iTime));

    vec4 camRot = qLookAt(cameraForward, vecY(1.0));


    vec3 col;
    #ifdef ANTIALIASING
        vec3 colorSum = vec3(0.0);        
        //vec2 nextUv = ((fragCoord+vec2(1.0))-0.5*iResolution.xy)/iResolution.y;
        vec2 nextUv = ((fragCoord.xy+vec2(1.0)) / iResolution.xy) - vec2(0.5);


        colorSum += GetColor(camPos, camRot, uv);
        colorSum += GetColor(camPos, camRot, mix(uv, vec2(uv.x, nextUv.y), 0.5));
        colorSum += GetColor(camPos, camRot, mix(uv, vec2(nextUv.x, uv.y), 0.5));
        colorSum += GetColor(camPos, camRot, mix(uv, nextUv, 0.5));
        colorSum /= 4.0;

        col = colorSum;  
    #else
        col = GetColor(camPos, camRot, uv);
    #endif

    fragColor = vec4(col,1.0);
}

