vec3 HSVtoColor(float hue, float saturation, float value)
{
    float hi = mod(floor(hue / 60.), 6.);
    float f = hue / 60. - floor(hue / 60.);

    value = value;
    float v = value;
    float p = value * (1. - saturation);
    float q = value * (1. - f * saturation);
    float t = value * (1. - (1. - f) * saturation);

    if (hi == 0.)
        return vec3(v, t, p);
    else if (hi == 1.)
        return vec3(q, v, p);
    else if (hi == 2.)
        return vec3(p, v, t);
    else if (hi == 3.)
        return vec3(p, q, v);
    else if (hi == 4.)
        return vec3(t, p, v);
    else
        return vec3(v, p, q);
}

