// golfed variant of https://shadertoy.com/view/stcBRj

#define L     length          
#define r(a)  mat2(cos(a+vec4(0,q,-q,0)))              // rotation
#define R(a)  U = L(I)*4./R.y * cos( mod( atan(I.y,I.x) +  a/2. , a ) -  a/2. + vec2(0,q) ) // rotational symmetry(a)
#define s(v)  smoothstep(-12./R.y, 0., v )             // AA draw

// --- shuttle tile( center, angle, diagonal sign ) = equilateral tri + square + equilateral tri 
#define S(b,w)                                                                         \
    V  = abs( Q = U*r(b) - P*r(a) );                                                   \
    d  = min(.5 - V.y, .683 - .5*V.x-.866*V.y );                                       \
    O = max(O, s(d)                                             /* mask */             \
             *( 1.-s( min( L(V-.5), L(V-vec2(1.366,0)) ) -.33 ) /* dots */             \
                  +s( min( L(Q-vec2(1,w 1)), L(Q+vec2(1,w 1)) ) -1.3 ) *step(V.x,.5) ) ) /* diagonal bar */

void mainImage( out vec4 O, vec2 u ) {
    O-=O; 
    float d,a, q = 1.57;
    vec2  R = iResolution.xy, V, D,T, Q, I =  u+u - R,
