float circleShape(float radius, vec2 position) {
	float value = distance(position, vec2(0.5));
	return 1. - step(radius, value);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float circleWidth = 0.2;
    float circle = circleShape(circleWidth, uv);
    vec3 circleCol = vec3(circle);
    vec3 color;
    if (circleCol == vec3(0)) {
        color = vec3(0);
    } else {
        color = 0.5 + 0.5 * sin(iTime * circleCol + vec3(0,2,4));
    }

    // Output to screen
    fragColor = vec4(color, 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv =  (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);

    float inverseSpeed = 15.;
    float LOD = 50.0;
    for(float i = 1.0; i < LOD; i++){
        uv.x += 0.6 / i * cos(i * 2.5* uv.y + iTime/inverseSpeed);
        uv.y += 0.6 / i * cos(i * 1.5 * uv.x + iTime/inverseSpeed);
    }

    float intensity = .8;

    fragColor = vec4(vec3(intensity)/abs(sin(iTime/inverseSpeed-uv.y-uv.x)),1.0);
}

// http://www.pouet.net/prod.php?which=57245
// If you intend to reuse this shader, please add credits to 'Danilo Guanabara'

// improving readability + adding configurable params to Danilo's shader

#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    /* visual params */

    float rippleDirection = -1.; // negative inwards, positive outwards
    float rippleRate = 3.0; // rate that ripples move in/out
    float numRipples = 20.; // num ripples

    float overallDensityRate = 1.0;  // rate of oscillation of overall ripple density. sin within sin
    float densityCap = 1.0;  // cap density ie num of coexisting ripples

	vec3 c;
	float l, t=iTime;
    vec2 p = fragCoord.xy/r;
    p-=.5; // remap uv from [0,1] --> [-0.5, 0.5]. center pixel now (0,0)
    p.x *= r.x / r.y;  // scale x by ratio of screen width to height. 
    // now p.x. and p.y range are proportional to screen resolution.
    // e.g. if screen is 200px wide, 100px tall, p.x range from -1,1.
    l=length(p); // distance from center of screen

	for(int i=0;i<3;i++) {
        vec2 uv = fragCoord.xy/r;

        t += .07;  // change time for each color channel. RGB diffraction effect

        uv += // why are we using 0-1 UV here when this is a radially symmetric shader??
            p / 
            l * densityCap * (sin(overallDensityRate * t)+1.) * // sin of time, shifted +1 to [0,2] range. independent for pixels, applies to entire screen
            abs(sin(rippleDirection * rippleRate * t + l*numRipples)); // sin based on distance from center pixel, shifted proportionally to distance
            // means freq of this sin is fixed at t coefficient, but repeats in ripples every 1/L*k units of radial distance

        c[i]= .01 / length(fract(uv) - .5);  // why divide from .01??
	}
	fragColor=vec4(c / l, 1.0);
}

