    bool showRed = true;
    bool showGreen = true;
    bool showBlue = true;

    float r = clamp( precisionAmount * round( uv.x / precisionAmount ), 0., 1. ) - float(!showRed);
    float g = clamp( precisionAmount * round( uv.x / precisionAmount ), 0., 1. ) - float(!showGreen);
