//Based on a chat on stackexchange.
//https://gamedev.stackexchange.com/questions/23743/whats-the-most-efficient-way-to-find-barycentric-coordinates
vec3 barycentricCoords(vec2 uv, tri3 tri){
    vec2 v1 = tri.v2.xy - tri.v1.xy;
    vec2 v2 = tri.v3.xy - tri.v1.xy;
    vec2 v3 = uv - tri.v1.xy;

    float d1 = dot(v1, v1);
    float d2 = dot(v1, v2);
    float d3 = dot(v2, v2);
    float d4 = dot(v3, v1);
    float d5 = dot(v3, v2);
    float denomInv = 1.0/(d1*d3 - d2*d2);

    float v = (d3*d4 - d2*d5)*denomInv;
    float w = (d1*d5 - d2*d4)*denomInv;
    float u = 1.0 - v - w;

    return vec3(u, v, w);
}

bool displayTriangle(vec2 uv, tri2 tri, in float clrInt, out vec3 outClr){
    vec3 b = barycentricCoords(uv, tri3(vec3(tri.v1.xy, 0), vec3(tri.v2.xy, 0), vec3(tri.v3.xy, 0), tri.inClr));
    if(b.x >= 0.0 && b.y >= 0.0 && b.x + b.y <= 1.0){
        outClr = tri.inClr*clrInt;
        return true;
    }
    return false;
}

