    zRotation[1] = vec4(-sin(rot.z*degreesToRadians), cos(rot.z*degreesToRadians), 0, 0);
    zRotation[2] = vec4(0, 0, 1, 0);
    zRotation[3] = vec4(0, 0, 0, 1);

