    vec4 v1 = vec4(tri.v1, 1);
    vec4 v2 = vec4(tri.v2, 1);
    vec4 v3 = vec4(tri.v3, 1);

    v1.xyz *= scale;
    v2.xyz *= scale;
    v3.xyz *= scale;

    v1 *= rotation;
    v2 *= rotation;
    v3 *= rotation;

    v1.xyz += trans;
    v2.xyz += trans;
    v3.xyz += trans;

    v1.xyz -= cam.pos;
    v2.xyz -= cam.pos;
    v3.xyz -= cam.pos;

    v1 *= camRotation;
    v2 *= camRotation;
    v3 *= camRotation;

    v1 *= projection;
    v2 *= projection;
    v3 *= projection;

    vec3 outClr;

    normal = normalize(cross(v3.xyz - v1.xyz, v2.xyz - v1.xyz));

    camDir = -v1.xyz;

    v1.xy /= v1.w;
    v2.xy /= v2.w;
    v3.xy /= v3.w;

    tri2 outTri = tri2((v1.xy + 1.0)*(iResolution.xy/2.0), (v2.xy + 1.0)*(iResolution.xy/2.0), (v3.xy + 1.0)*(iResolution.xy/2.0), vec3(1));

