    float z = (1.0/v1.z)*barycentrics.x + (1.0/v2.z)*barycentrics.y + (1.0/v3.z)*barycentrics.z;

    depth = 1.0/z;

    return displayTriangle(uv, outTri, 1.0, outClr);
}

