  d2 = smin(d2, length(p1-vec3(0., 1., 0.)), 1.);
  flameC += .3/(1.+d2*d2);
  fin;
}
vec2 m(vec3 p){
  p.z = abs(p.z)-10.+TIC*10.; // 2.6
  p.xy *= rot(cos(t*.225+p.z*.087345));
  vec2 e = body(p);
  vec2 e2 = flame(p);
  d = e.x < e2.x ? e.x : e2.x;
  fin;
}
vec3 nm(vec3 p){
  vec2 e = vec2(0.81,0.);
  return normalize(m(p).x-vec3(m(p-e.xyy).x, m(p-e.yxy).x, m(p-e.yyx).x));
}
void ray(inout vec3 p, inout vec3 r){
  const float MM = 300.;
  float di = 0., dd = 0., id = -1.;
  for(float i = 0.; i++ < 100.;){
    vec2 e = m(p);
    di = e.x;
    id = e.y;
    if(di < .00001){
      break;
    }
    //else if(dd > MM){
      //p = vec3(MM);
      //di = MM;
      //break;
    //}
    dd += di;
    p+=di*r;
  }
}
