  vec3 s = vec3(0.01,-15.01,-96.);
  s.zy *= rot(pi/2.);
  vec3 p = s;
  vec3 tg = vec3(0.01,10.01, 0.01);
  vec3 cz = normalize(tg-s);
  vec3 cx = normalize(cross(cz,vec3(0., -1., 0.)));
