#define PI 3.14159265
#define RAD 6.2831853

float antiAlias;

vec2 rotAround(vec2 p, vec2 o, float ang);

vec4 circle(vec2 uv, vec2 origin, float size, vec3 col) {
    return vec4(col, smoothstep(size + antiAlias, size - antiAlias, length(uv - origin)));
}

vec4 eye(vec2 uv, vec2 origin, float size, float ang) {
    vec4 eyeBall = circle(uv, origin, size, vec3(1.));
    vec2 pupilO = vec2(origin.x + size/2., origin.y);
    pupilO = rotAround(origin, pupilO, ang);
    vec4 pupil = circle(uv, pupilO, size/2., vec3(0.));

    return mix(eyeBall, pupil, pupil.w);
}


mat2 rot2D(float ang) {
    return mat2(cos(ang), -sin(ang), sin(ang), cos(ang));
}

// p: reference point to rotation from
// o: point that will be rotated
// ang: angle in radians
vec2 rotAround(vec2 p, vec2 o, float ang) {
    vec2 v = (o - p);

    return (rot2D(ang) * v) + p;
}

// returns the angle of a vector in radians
float angle(vec2 v) {    
    return -atan(v.y/v.x);
}

vec3 rgb(int r, int g, int b) {
    return vec3(float(r)/255., float(g)/255., float(b)/255.);
}

// https://www.wolframalpha.com/input?i=f%28x%29%3D0.5%2B%28cos%28%28%28x%2B0.5%2F1%29*20*1%29%2F%28PI%29%29%2F2.%29
float cycle(float perSecond) {
    float d = fract((iTime + iTimeDelta) * perSecond);
    return 2. * ( d>.5 ? 1.-d : d );
}


// returns -size to size cycle
float rangeCycle(float size, float perSec) {
    return mod(iTime*perSec - size, size) - .5*size;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    antiAlias = 1.5/iResolution.y;

    vec3 col = vec3(0.);

    float size = floor(50. + (200. * cycle(0.1)));
    float thick = 0.15 + (0.25 * cycle(0.2));

    vec2 o = vec2(-1.3, 0.);
    vec3 initCol = rgb(186, 98, 245);
    vec3 destCol = rgb(228, 237, 62);

    // what is the step size when going from source color to destinate color?
    vec3 incCol = (destCol-initCol) / size;
    // used for the head
    vec2 lastO;

    for (float i = 1.; i <= size; i+= 1.) {
       lastO = vec2(o);
       o.x += 0.01;
       o.y = cos(o.x + iTime*5.12) / (0.5/thick);
       initCol += incCol;

       vec4 circleColor = circle(uv, o, thick, initCol);
       col = mix(col, circleColor.xyz, circleColor.w);

       // last part, time for the eyes c.c
       if (i == size) {
           float eyeSize = thick/4.;
           vec2 eyeVec = vec2(eyeSize, eyeSize * 2.);

           // discover the angle between the head and the body
           vec2 angVec = o - lastO;
           float ang = angle(angVec);

           // discover the left eye origin
           vec2 lEyeO = o + eyeVec;
           // align the eye with the body
           lEyeO = rotAround(o, lEyeO, ang);

           // draw the eye
           vec4 lEye = eye(uv, lEyeO, eyeSize, ang);
           col = mix(col, lEye.xyz, lEye.w);

           // same thing as above but for right eye
           // note that ang is the same, since I want the two eyes to look at the same place
           eyeVec.y = -eyeVec.y;
           vec2 rEyeO = o + eyeVec;
           rEyeO = rotAround(o, rEyeO, ang);
           vec4 rEye = eye(uv, rEyeO, eyeSize, ang);

           col = mix(col, rEye.xyz, rEye.w);
       }
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

// ---------------------------------------------------------------------------------------
//	Created by fenix in 2022
//	License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
//  Honestly the rendering style was originally an accident, but I thought it was cool
//  enough to keep. I thought I was working on a shader using the "bubble" rendering
//  technique (press P to see this) but I turned it off because it seemed to just distract
//  from the whirling pattern.
//
//  All this is, is a regular voronoi renderer, but instead of rendering the closest
//  particle to each point, I render the 4th closest particle.
//
//  I'm fairly happy with the simulation itself. Developing the bubble shaders forced me
//  to improve the neighbor search, which probably helped. You can stop the rotation by
//  grabbing with the mouse, and the particles keep most of their volume, and don't move
//  too erratically.
//
//  In case it's not obvious, I should mention that you can "throw" the mixer with the mouse
//  to impart some velocity (which will eventually damp out to the default clockwise spin).
//
//  Buffer A computes the particle positions and neighbors
//  Buffer B does a voronoi search to help out building neighborhoods
//
// ---------------------------------------------------------------------------------------


void renderParticle(in fxParticle p, in vec2 fragCoord, inout vec4 fragColor)
{   
    vec2 closest;
    p.pos.x *= iResolution.y / iResolution.x;
    float dist = sqrt(linePointDist2(p.pos, p.pos - p.vel, fragCoord, iResolution, closest));
    int maxParticles = min(int(iResolution.x * iResolution.y) / NUM_PARTICLE_DATA_TYPES, MAX_PARTICLES);

    float wd =  2./(min(iResolution.x, iResolution.y));

    fragColor.xyz = max(fragColor.xyz, (mix(vec3(1), fragColor.xyz, smoothstep(0.0, wd, abs(dist -  min(PARTICLE_REPEL_SIZE*2., p.size))))));

    // Render neighbor lines
    if (keyDown(KEY_SHIFT))
    {
        for(int i = 0; i < 4; i++){
            ivec4 neighbors = p.neighbors[i];
            for (int j = 0; j < 4; ++j)
            {
                int cid = neighbors[j];
                if(cid==-1 || cid >= maxParticles || cid == 0) continue;

                vec2 otherPos = fxGetParticleData(cid, POS_VEL).xy;
                otherPos.x *= iResolution.y / iResolution.x;

                float distToLin = linePointDist2(p.pos, p.pos + 0.5 * (otherPos - p.pos), fragCoord, iResolution, closest);
                fragColor.xyz += iResolution.x* max(0.0, 0.001 - sqrt(distToLin)) / (0.0004);
            }
        }
    }
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{    
    // Simple vignette effect by Ippokratis
    // https://www.shadertoy.com/view/lsKSWR
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv *=  1.0 - uv.yx;   //vec2(1.0)- uv.yx; -> 1.-u.yx; Thanks FabriceNeyret 
    float vig = uv.x*uv.y * 15.0; // multiply with sth for intensity
    vig = pow(vig, 0.25); // change pow for modifying the extend of the  vignette
    fragColor = vec4(vig*.1, vig*.1, vig, 1);

  	vec2 p = fragCoord/iResolution.xy;

    //get the id's of the 4 particles that (should be) closest.
    //the 4 ids are stored in .x, .y, .z, .w
    ivec4 ids = fxGetClosest(ivec2(fragCoord));

    // The complex color pattern comes from computing a traditional voronoi, but then instead of using
    // the closest particle we choose the fourth closest particle. Change to zero for traditional
    // voronoi, 1 or 2 for other abstract patterns
    const int WHICH_NEIGHBOR = 3;
    int id = ids[WHICH_NEIGHBOR];
    fxParticle particle = fxGetParticle(id);

    fragColor.xyz = particle.color;

    if (keyDown(KEY_P))
    {
        //draw the particles
        for(int i = 0; i < 4; i++)
        {
            //get the particles position
            int id = ids[i];
            fxParticle particle = fxGetParticle(id);

            renderParticle(particle, p, fragColor);
        }
    }

    fxState state = fxGetState();

    p.x /= iResolution.y / iResolution.x;
    fragColor = mix(fragColor, vec4(vig), vec4(clamp(-distanceFromWalls(p, iResolution, state.rotation)*iResolution.y, 0.0, 1.0)));
}

