// In the end there is just a moving sphere in a mirror box...


#define MAX_STEPS 200
#define MAX_DIS 400.
#define MIN_DIS 10.

#define SURF_DIS .01
#define SURF_MUL 100.
#define SURF_EXP 2.

#define EYE_DIS 1.0
#define FIL_COR 1.05

#define iTime iTime*.6

mat2 Rot(float a)  
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float Dist(vec3 p) 
{
    //cam rotation
    float q = min(iTime/200., 5.);
    float c = 0.2 / (1.+q/3.);
    p.yx *= Rot(.7854*smoothstep(0.,c+c,-cos(iTime/(23.+q))));
    p.zx *= Rot(.7854*smoothstep(-c,c+c,-cos(iTime/(11.+q))));
    p.zy *= Rot(.7854*smoothstep(c,c+c,-cos(iTime/(45.+q))));

    //distance frequency
    float f = 1./(400.+sin(iTime/13.)*200.+sin(iTime/19.)*100.);
    float dis = length(p)*f;

    //mirrorbox
    vec3 size = vec3(10.);
    p = mod(abs(-mod(p,4.*size)+2.*size),4.*size);

    //sphere
    float d = length(p - sin(iTime*.5+dis*6.28)*size.x-size) - 
              10.*(sin(iTime*1.+dis*6.28)*.5+.8)*pow(dis/f/MAX_DIS, 1.2) *
              (1.+smoothstep(0.9,1.,sin(iTime/2.+dis/f/200.))); 

    return d;
}

float RTM(vec3 ro, vec3 rd) 
{
    float sum = 0.;
	float s = 1.;
    float d = MIN_DIS;
    const float a = 1. / float(MAX_STEPS); 

    for(int i = 0; i < MAX_STEPS; i++) 
    {    
        float sd = (SURF_DIS * (pow(d/MAX_DIS, SURF_EXP)*SURF_MUL+1.));
        if (s < sd || d > MAX_DIS) break;

        s = Dist(ro + rd*d);
        s = max(abs(s), 2.*sd);
        d += s * .8;
        sum += a;
    }

    return smoothstep(0., 1., sum * (1.-exp(-d*d)));
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z)
{
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y;

    //interaction
    vec2 m = iMouse.xy / iResolution.xy;
    if (length(m) == 0.) m = vec2(.5);
    m.x = clamp(m.x, .1, .9);
    m.y = clamp(m.y, .25, .75);

    //set cam
    vec3 ro = vec3( 0., 0., -15.);
    ro.yz *= Rot(-m.y*3.14159 + 1.5708);
    ro.xz *= Rot(-m.x*6.28318);
    vec3 rd = R(uv, ro, vec3(0., 0., 0.), .7 + 0.4*smoothstep(.6,.9,sin(iTime/16.)));
    ro += normalize(cross(rd, vec3(0., -1., 0.))) * EYE_DIS * -.5;

    //left eye
    float colL = RTM(ro, rd);

    //eye distance
    ro += normalize(cross(rd, vec3(0., -1., 0.))) * EYE_DIS;

    //right eye
    float colR = RTM(ro, rd);

    //filter correction
    colL *= FIL_COR; 
    colR /= FIL_COR;

    //contrast
    vec3 colS = smoothstep(vec3(0.), vec3(1.), vec3(colL, colR, colR));

    //Vignette
    colS *= smoothstep(2.,-2./5., dot(uv,uv)); 

    fragColor = vec4(colS, 1.);
}

// Based off https://jameshfisher.com/2020/08/11/production-ready-green-screen-in-the-browser/

// From https://github.com/libretro/glsl-shaders/blob/master/nnedi3/shaders/rgb-to-yuv.glsl
vec2 RGBtoUV(vec3 rgb) {
  return vec2(
    rgb.r * -0.169 + rgb.g * -0.331 + rgb.b *  0.5    + 0.5,
    rgb.r *  0.5   + rgb.g * -0.419 + rgb.b * -0.081  + 0.5
  );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 texCoord = fragCoord.xy / iResolution.xy;

  vec4 rgba = texture(iChannel0, texCoord);
  float chromaDist = distance(RGBtoUV(texture(iChannel0, texCoord).rgb), RGBtoUV(vec3(0.0, 1.0, 0.0)));

  // For similarity
  float baseMask = chromaDist - 0.46;
  // For smoothness
  float fullMask = pow(clamp(baseMask / 0.08, 0., 1.), 1.5);
  rgba.a = fullMask;

  // For spill
  float spillVal = pow(clamp(baseMask / 0.1, 0., 1.), 1.5);
  float desat = clamp(rgba.r * 0.2126 + rgba.g * 0.7152 + rgba.b * 0.0722, 0., 1.);
  rgba.rgb = mix(vec3(desat, desat, desat), rgba.rgb, spillVal);
  fragColor = rgba;
}

