        if( int(hit.y)==100 || int(hit.y)==41 )
        { multc=0.650+0.2*sin(t+cos(t)); }


        result=  lightingv3(nor, p,light_pos1, rd,ro,colobj,hit.x)*light_color1*multc;
        result+= lightingv3(nor, p,light_pos2,rd, ro,colobj,hit.x)*light_color2*multc;
        col= result/2.0;
