void mainImage( out vec4 O, vec2 U )
{
    int T = int(iTime)%3;
    float w, t=0., d2,  
         dof = 38.*( iMouse.z > 0. ? iMouse.x : U.x ) / R.x, // depth of field  current radius
         dens = 8.,                                          // sampling density
         N = clamp( dens*dof*dof , 8., 1e4 );                // sampling ~ disk area
    O-=O;
    for( float i=0.; i < N; i++ ) {
        vec2 dP = vec2(cos(i*2.4),sin(i*2.4)) * sqrt(i/N);   // Vogel disc sampling ( 2.39996 )
        vec4 C = texture(iChannel0,(U+dof * dP) / R );       // Bokeh sampling of the image
        d2 = dot(dP,dP);
        t += w =   T==0 ? 1.                                 // disc profile
                 : T==1 ? exp( -.5*d2 *6.)                   // Gaussian profile ( cut at 2.5sigma )
                 :        step( abs( d2 - .5 ) , .1 );       // ring
        O += w * C;
    }

    O = pow( O/t, vec4(1./2.2));                             // to sRGB
}

