vec3 worleyTiles(in vec2 uv, in vec3 col) {
    vec2 R = iResolution.xy;
    vec2 px = 1./R,
         pxc = 2./R.yy;          // [-1,1] vertically

    uv.x += iTime*0.000001;
    uv.y += sin(iTime*1.1+uv.x)*0.0005;

    float worls = 0.;
    for (int i=0;i<8;i++) {
        float worl = worley(uv*(4.+sin(iTime*0.1)*3.)+iTime*0.1,1.5+sin(iTime*0.5)-float(i)/8.,true).x;
        float worlstep = float(i)*0.15;
        worls += smoothstep(-0.01,0.01,worl-worlstep);
    }
    worls = mod(worls,2.);
    col *= worls;

    vec3 lp = texture(iChannel0,uv).rgb;
    col = mix(col,lp, 0.96);

    vec3 lpn = texture(iChannel0,uv+vec2(0.,pxc.y)).rgb;
    vec3 lps = texture(iChannel0,uv-vec2(0.,pxc.y)).rgb;
    vec3 lpe = texture(iChannel0,uv+vec2(pxc.x,0.)).rgb;
    vec3 lpw = texture(iChannel0,uv-vec2(pxc.x,0.)).rgb;

    vec3 newcol = vec3(lpn.r,lpe.g,lps.b);

    float mixer = mix(lps.r,lps.b,sin(iTime)*0.5+0.5);
    col = mix(col,newcol,mixer);

    // color adjustments
    vec3 hsv = rgb2hsv(col);
