    float shape = 100.;
    float t = timeline;
    float w = fract(t/delay);

    // shape parameters
    float a = 1.;
    float n = 196.+t*.2;
    float r = 1.;
    float f = 1.7;
    float b = 1.;
    float s = .05;
    const float count = 8.;

    for (float i = 0.; i < count; ++i)
    {
        p.xy *= rot(n/a);
        p.yz *= rot(n*a);
        p.x = abs(p.x)-r*a;
        shape = length(p)-s*a;
        mat = mix(i, mat, smoo(dist, shape, b*a));
        dist = smin(dist, shape, b*a);
        a /= f;
    }

    return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
