  time /= tempo;
  float s1 =
      4.,
  m4 = fmax1(time+floor(time*s1),time,2.),
  s2 = 8./(1.+m4),
  t=
      time/(1.+m4)
  ,
  m1 =
      fmax1(t*s1,t,s2)
