{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.y/iResolution.x;
    uv += iTime*0.05;

    float h = fbm(uv*10.0, 2.0, 1.0);
    //float h = perlinNoise(uv * 10.0, 2.0);

    fragColor = vec4(vec3(h),1.0);
}

