float sdSphere(vec3 p,float s)
{
    return length(p)-s;
}

float sdCapsule(vec3 p,vec3 pos1,vec3 pos2,float r)
{
    vec3 ap=p-pos1,ab=pos2-pos1;
    float pro=clamp(dot(ap,ab)/dot(ab,ab),0.,1.);
    return length(ab*pro-ap)-r;
}

// Ref:"Foundation ActionScript 3.0 Animation" P.367
// Ref: https://www.alanzucconi.com/2020/09/14/inverse-kinematics-in-3d/
vec2 ik(vec2 A, vec2 C, float a, float b, bool isInverted){
    C -= A;
    float aD = atan(C.y, C.x);
    float c = min(a+b, length(C));
    float aB = acos((b*b-a*a-c*c)/(-2.*a*c));
    float dir = (isInverted) ? -1. : 1.;
    float aDB = -(aD-aB*dir)+PI*.5;
    return vec2(sin(aDB),cos(aDB))*a+A;
}

vec3 ik(inout vec3 A, inout vec3 C, float a, float b, bool isInverted){
    vec3 ax = vec3(0,1,0); 
    float aC = atan(C.x,C.z);
    A.xz=rot(A.xz,-aC);
    C.xz=rot(C.xz,-aC);

    vec3 B = vec3(0, ik(A.yz, C.yz, a, b, false));

    A.xz=rot(A.xz,aC);
    B.xz=rot(B.xz,aC);
    C.xz=rot(C.xz,aC);
    return B;
}

float map(vec3 p){
    p.y+=.35;
    float t = iTime*3.;
    float a = 0.4;
	float b = 0.5;
	vec3 A = vec3(0,.2,0);

    vec3 offC = vec3(sin(t*.5), 0, cos(t*.5))*.5;
    float radC = .3;
	vec3 C = vec3(sin(t)*radC, sin(t*.3)*.2+.3, cos(t)*radC);
    C+=offC;

    vec3 B = ik(A, C, a, b, false);

    float d = 3e38;
    d=min(d, sdCapsule(p,vec3(0),A,.005));
    d=min(d, sdCapsule(p,A,B,.005));
    d=min(d, sdCapsule(p,B,C,.005));
    d=min(d, length(p-A)-.025);
    d=min(d, length(p-B)-.025);
    d=min(d, length(p-C)-.025);
    d=min(d, max(abs(length(p.xz-offC.xz)-radC)-.01, abs(p.y-C.y)-.01));
    d=min(d, max(length(p.xz)-.0125, abs(p.y-.05)-.05));
    d=min(d, max(length(p.xz)-.5, abs(p.y+.0125)-.0125));
    return d;
}

