// Graffiti 3D
//
// is it what we call stochastic?
// when we agree onto something random?
//
// trying to render an extruded cube on a curve
// in a least worst way possible
//
// previous iterations were Cyborg Signature and Fraggiti:
// https://www.shadertoy.com/view/7lcfz7
// https://www.shadertoy.com/view/NldfR4

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // data unpacking
    vec4 data = texture(iChannel0, uv);
    float shade = data.r;
    float timeline = data.g;
    float glow = data.b;
    float total = data.a;

    // background
    vec4 color = vec4(1) * smoothstep(2., -2., length(uv-.5));

    if (total > .001 && total < max_dist)
    {
        // coloring
        float tint = shade*2.+total*5.+timeline*1.;
        vec3 palette = .5+.5*cos(vec3(1,2,3)*5.+tint);
        color = vec4(palette,1);
        color *= shade;
        color += .5*pow(shade, 10.);
    }
    else
    {
        // outline and shadow
        color *= smoothstep(5.,0.,glow);
        color *= .8+.2*smoothstep(.8,0.,glow);
    }

    fragColor = color;
}

